style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com")
{

  .content--home, .header-wrap, .content-wrap
  {
    background-color: #000 !important;
    background-image: url(data:image/jpeg;base64,) !important;
    background-attachment: fixed !important;
    background-position: right !important;
    background-repeat: no-repeat !important;
    background-size: 100% 60% !important;
  }

  .result
  {
    color: #FFFFFF !important;
    background: #000000 !important;
    border: 1px #23B6DB solid !important;
  }

  .site-wrapper,  .page-index,
#zci-about, #zero_click_wrapper div,
.acp-wrap
  {
    background: #000000 !important;
  }

  .header-wrap
  {
    background: transparent !important;
  }

  .nav-menu__list, .zci__metabar-wrap,
#zci-videos, .header, #header,
.has-active-zci .header:after,
.has-active-zci .header-wrap:before, .has-active-zci .header-wrap:after
  {
    background-color: #000000 !important;
  }

  .search--home, .search--adv
  {
    background: #000000 !important;
    border: 1px #23B6DB solid !important;
  }

  .btn, .msg, .msg--box, .msg--result
  {
    color: #23B6DB !important;
    background: #000000 !important;
    border: 1px #23B6DB solid !important;
  }

  .btn:hover
  {
    color: #000000 !important;
    background: #23B6DB !important;
    border: 1px #23B6DB solid !important;
  }

  .search__autocomplete, .acp,
#did_you_mean
  {
    color: #FFFFFF !important;
    background: #000000 !important;
    border: 1px #23B6DB solid !important;
  }

  b
  {
    color: #23B6DB !important;
  }

  a, strong, .result__snippet,
#search_form_input_homepage,
.search__input--adv, .info,
.zci__content--info, .zci__body,
.nav-menu__heading span, 
.zci__metabar__primary-text,
.tile__content, .tile__title,
.results--message__safe-search
  {
    color: #FFFFFF !important;
  }
}
