style.css:
@-moz-document domain("aminoapps.com") {
:root
{
    --deadly-black: #202020;
    --nav-black: #1d1d1d;
    --post-black: #232323;
    --light-purple: #7F7AEB;
    --deadly-white: #fbfdd8;
}
body {
    --community-color: var(--light-purple);
    background-color: var(--deadly-black);
    background: var(--deadly-black);
}
             
/* общий фон */
.global-body {
    background-color: var(--deadly-black);
}
.home-section-nav .overflowing-x+.overflow-hint {
    background: transparent;
}
.global-chat-community-bg {
    background: transparent;
}

a,a:active,a:hover,button {
    color: var(--light-purple);
}

/* навигационная панель */
.home-section-nav .home-fixed-container {
    background: var(--nav-black);
}
.home-section-nav ul>.section-item.community-color {
    background: var(--nav-black);
}
.home-section-nav .overflowing-x+.overflow-hint .amino-icon.community-color {
    background: var(--nav-black);
}
.home-section-nav ul>.section-item {
    box-shadow: 0 0px 0px transparent;
}
.home-section-nav .overflowing-x+.overflow-hint>.amino-icon {
    box-shadow: 0 0px 0px transparent;
}
.home-section-nav .home-nav-home>i.amino-icon {
    box-shadow: 0 0px 0px transparent;
    background: var(--nav-black);
}
.community-color, .community-color:hover, .community-color:visited, .community-color:link, .community-color:active {
    color: var(--light-purple);
}


.global-dropdown .dropdown-head, .global-identity .global-account-info, .global-dropdown .amino-card, .global-dropdown .aminos-list, .global-dropdown-anchor.open .global-dropdown-container {
    background: var(--post-black);
}
.global-dropdown .amino-card:hover {
    background: var(--nav-black);
}
.global-identity .explore-btn {
    background-color: var(--light-purple);
    box-shadow: 0 4px 0 var(--light-purple);
}


/* страницы */
.main-explore .section .community {
    background: var(--post-black);
}
body.site-body {
    background: var(--deadly-black);
}
.main-explore .section {
    color: var(--deadly-white);
}



/* посты */
.post-list>.list-item {
    background-color: var(--post-black);
    border-bottom: 1px solid var(--post-black);
}
.main-page article.post.main-post {
    background-color: var(--post-black);
}
section.post-list.new-post-list .post-quiz .quiz-start {
	color: var(--light-purple);
	border-color: var(--light-purple);
    border-width: 2px;
}
section.post-list.new-post-list .post-quiz .quiz-start:hover {
    background: rgba(127,122,235,.2);
}
p,.post-list section.desc>.content {
    color: var(--deadly-white);
}
.community-sidebar .sidebar-card { 
    background-color: var(--post-black);
}
.recommendation-sidebar section.author-aside, .recommendation-sidebar section.gallery, .recommendation-sidebar section.leader-pick, .recommendation-sidebar section.popular {
    background-color: var(--post-black);
}
.main-page section.recommend article.post.recommend-item {
    background-color: var(--post-black);
}



.community-sidebar .compose-entry-wrapper {
    background-color: var(--post-black);
}
.main-page section.share .item .amino-icon {
    color: var(--deadly-white);
}
.main-page section.blog-like>.vote-counter {
    background-color: var(--post-black);
    box-shadow: 0 0px 0px transparent;
}
.main-page .avatar, .main-page section.recommend article.post.recommend-item .avatar, .recommendation-sidebar section.author-aside .avatar, section.comment-list .avatar,
.modal .call2action-popup .motivation .avatar, .live-member-list .avatars img.avatar{
    border-radius:20%;
    border: 0px solid transparent;
    box-shadow: 0 0 0px hsla(0,0%,100%,.5);
}
.comment-input-bar textarea[name=content] {
    border: 0px solid transparent;
    background-color: var(--deadly-black);
    color: var(--deadly-white);
    border-radius: 8px;
}
.comment-input-bar textarea[name=content]:focus {
    box-shadow: inset 0 0 0px transparent;
    background-color: var(--nav-black);
}
.main-page section.blog-like .more-votes, .main-page section.blog-like .vote-icon {
    background-color: var(--deadly-black);
    border: 0px solid;
}
.main-page section.blog-like>.vote-counter:hover {
    background-color: var(--nav-black);
    border: 0px solid;
}
section.comment-list article.comment [data-vce=toggle-content] .height-toggle {
    background: linear-gradient(0deg,var(--post-black),var(--post-black), 60%,hsla(0,0%,100%,0));
}
.global-bg {
    background: var(--deadly-black);
}


/* постописалка */
.compose-community-bg {
    background-color: var(--deadly-black);
}
.composer[data-v-26a804cf] {
    background: var(--post-black);
}
.editor-container>.title-input[data-v-4c3a1982] {
    background: transparent;
}
.post-preview.no-background {
    background: var(--post-black);
    color: var(--deadly-white);
}
.attachment-menu>.attachment-list[data-v-33e6a769] {
    background: var(--deadly-black);
}
.attachment-menu[data-v-33e6a769] {
    background: var(--nav-black);
    color: var(--deadly-white);
}
.post-setting[data-v-e8eaf0fa] {
    background: var(--deadly-black);
}
.background-picker>.image-picker-button[data-v-2cc4c412], .cover-picker>.image-picker-button[data-v-fdd39a5e], .category-button>.add-category[data-v-7f52f7eb] {
    background: var(--post-black);
}
.cover-picker>.image-picker-button[data-v-fdd39a5e]:hover, .background-picker>.image-picker-button[data-v-2cc4c412]:hover, .category-button>.add-category[data-v-7f52f7eb]:hover {
    background: var(--nav-black);
}
.category-button>.category-menu[data-v-7f52f7eb], .category-button .section-header[data-v-7f52f7eb] {
    background: var(--post-black);
}
.community-hover-bg {
    background: var(--post-black);
}


/* чат */

::-webkit-scrollbar-thumb{
background:#23262b;
transition:1s
}
::-webkit-scrollbar-thumb:hover{
background:#495261;
transition:1s
}
.modal .popup.dialog {
    background: var(--deadly-black);
}
.modal .popup .message-box-popup .input, .modal .popup .message-box-popup .actions .btn-cancel:not(:hover), .chat-window-container .loading-overlay {
    background: var(--deadly-black);
}
.modal .call2action-popup .btn.btn-get-app {
    background-color: var(--post-black);
	box-shadow: inset 0 -3px var(--nav-black), 0 2px var(--nav-black);
}
.modal .call2action-popup .motivation>.bubble {
    background-color: var(--post-black);
    background: var(--post-black);
}


.chat-window-container.minimized {
    background:var(--deadly-black);
    box-shadow: 0 0 10px rgba(0,0,0,.4), 0 10px 15px -10px rgba(0,0,0,.3), 0 10px 40px 8px rgba(0,0,0,.2), 0 20px 40px 3px rgba(0,0,0,.3);
}
.chat-container {
    background:var(--deadly-black);
}

.chat-input-area {
    border-color: transparent;
}
.unavailable-chat {
    background:var(--deadly-black);
    color:#ff7878
}
.thread-list .pretty-date {
    color:#c8cfdb
}
.thread-list .chatroom.active, .thread-list .chatroom:hover {
    background:var(--deadly-black);
}
.chat-input-area .send-icons>.icon>svg:hover {
    fill:var(--deadly-black);
}
.chat-input-area .send-icons>.icon>svg {
    fill:var(--deadly-black);
}
.chat-input-area .send-icons>.icon>svg:hover {
    fill:var(--deadly-black);
}
.send-button.can-send svg {
    fill:var(--deadly-black);
}
.send-button svg {
    fill:#c8cfdb
}
.thread-list {
    border-color:#23262b
}
.user-message .author-name {
    color:#c8cfdb
}
.user-message.from-me .text-msg {
    background:var(--light-purple);
    color:#495261;
}
.info-message .linkblue {
    color:#b3ccff
}
.info-message {
    color:#c8cfdb;
    background:inherit
}
.timestamp {
    color:#c8cfdb;
}
.user-message .text-msg {
    background: #495261;
    color: #c8cfdb;
}
.message-list {
    background:inherit
}
.chat-input-area .text-input {
    background:var(--deadly-black);
    color:#c8cfdb
}
.cover-img .avatar {
    border-color:#b3ccff
}
}
