style.css:
@-moz-document url-prefix("https://www.upwork.com/"), regexp("https?://www\\\\.upwork\\\\.com(?!/reports/).*'") {
/* ==UserStyle==
@name         Upwork - Dark Nightlancer. Black Theme Style.
@description  Upwork Dark Nightlance - A BETA skin for Upwork, used to help with your eyes and those hours reading looking or creating jobs on the platform.
@version      1.0.4 (BETA)
@author       David R. Mier
@homepageURL  https://www.behance.net/DrMier
@supportURL   Please write on the comments sections.
@license      Attribution-NonCommercial-NoDerivatives 4.0 International
==/UserStyle== */
/* Citation Text Color */
:root{
    
    --Upwork-OriginalLogoGreen: #6fda44;
    --Upwork-DarkThemeGreenValley: #00c403;
    --Upwork-DarkThemeHighlightGreen:#84ff00;
    --Upwork-DarkThemeOrangeHype: #ffd000;
    --Upwork-DarkThemeBlackSuper: #000000;
    
    --Upwork-DarkThemeBGColorMenuHeader: #222222;
    --Upwork-DarkThemeBGColorBody: #171717;
    --Upwork-DarkThemeBGColorCardHeader: #1d1d1d;
    
    --Upwork-rash-SkillTagBackground: #3c3c3c;
    --Upwork-rash-SkillTagText: #7b7b7b;

     --Upwork-DarkThemeJobTitleColorTextNormal: #e0e0e0;
    --Upwork-DarkThemeBrightColorText: #f5f5f5;
    --Upwork-DarkThemeNormalColorText: #bbb;
    --Upwork-DarkThemeMutedColorText: #828282;
    --Upwork-DarkThemeHighlightColorText: #84ff00;
    
    --Upwork-DarkThemeButtonBGColor: #232323;
    --Upwork-DarkThemeButtonBGColorHover: #313030;
    --Upwork-DarkThemeButtonBGColorDisabled: #717171;
    --Upwork-DarkThemeButtonBGColorDisabledBorder: #515151;
    
    
    --Upwork-DarkThemeSeparatorColor:#2d332e;
    --Upwork-AircardHeaderSeparatorColor: #2d332e;
    --Upwork-rash-SeparatorLightColor: #2d332e;
    
    
    --Upwork-rash-FeaturedJobDarkColor: #0F376C;
    --Upwork-rash-FeaturedJobLightColor: #14498F;
    --Upwork-rash-FeaturedJobTextColor: #d8e9ff;
    --Upwork-rash-JobInterestingDarkColor: #8000ff;
    --Upwork-rash-JobInterestingLightColor: #b972ff;
    --Upwork-rash-JobInterestingTextColor: #f2f8ff;
    
    --Upwork-rash-TabsActiveHighlightTopColor: #6ab90b;
    --Upwork-rash-TabsActiveTextColor: #6fda44;
    
    
    --Upwork-rash-DropdownSuggestionsHeaderColor: #040404;
    --Upwork-rash-DropdownOptionsButtonHeaderColor: #202020;
    --Upwork-rash-DropdownTextColor: #e0e0e0;

    

        
    
}




/* :::::::::::::::::::: */
/* :: HEADER SECTION :: */
/* :::::::::::::::::::: */

/* Header Master Background Color */
.navbar, .nav-v2 {
    background-color: #222222;
    min-height: 55px;
    -webkit-box-shadow: 0 1px 6px rgba(255,255,255,0.05);
    box-shadow: 0 1px 6px rgba(255,255,255,0.05);
}

/* Body Main Background Color */
#layout {
    -webkit-box-flex: 1;
    -ms-flex: 1 0 auto;
    flex: 1 0 auto;
    background-color: var(--Upwork-DarkThemeBGColorBody);
}

/* Header Menu Section Notification Bell Color Dot */
.dropdown-toggle .badge-circle {
    background-color: var(--Upwork-DarkThemeOrangeHype);
}


/* ::: FONT MAIN COLOR CONTROL ::: */ 
/* Main Text Color */
body {
    color: var(--Upwork-DarkThemeNormalColorText);
}
/* Secondary or Muted Text Color */
.text-muted {
    color: var(--Upwork-DarkThemeMutedColorText) !important;
}



/* ::: HYPERLINKS (a values) MAIN CONTROL ::: */

/* Hyperlink Normal */
a {
    color: #3fb700;
    text-decoration: none;
}
/* Hyperlink Hover */
a:hover, a:focus, a:active {
    color: var(--Upwork-DarkThemeHighlightGreen);
    text-decoration: none;
}






/* Air Cards Main Header Background Color  */
.air-card header, .air-card footer {
    background-color: #1d1d1d;
}
/* Air Cards Main Header Separator Color  */
.air-card header, up-c-card header {
    border-bottom: 1px solid var(--Upwork-AircardHeaderSeparatorColor);
}








/* Air Cards Main Body Color  */
.air-card, up-c-card {
    position: relative;
    background-color: #232323;
    margin: 30px -30px;
    padding: 30px;
        padding-top: 30px;
        padding-bottom: 30px;
    -webkit-box-shadow: 0 1px 6px rgba(57,73,76,0.35);
    box-shadow: 0 1px 6px rgba(63, 76, 57, .35);
}
section.air-card-hover:hover, section.air-card-hover:focus {
    -webkit-box-shadow: none;
    box-shadow: none;
    background-color: #1e1e1e;
    outline: none;
    cursor: pointer;
}

/* Air Cards Main Body Color after Visited */
section.air-card-visited {
    background-color: var(--Upwork-DarkThemeButtonBGColorHover);
}



/* Air Cards Main Separator Color  */
.air-card section, up-c-card section {
    border-bottom: 1px solid #383838;
    margin: 0 -30px;
        margin-top: 0px;
    padding: 20px 30px;
}


/* Air Cards Job Title Color  */
section.air-card-hover h3 a, section.air-card-hover h4 a, section.air-card-hover p a {
    color: var(--Upwork-DarkThemeJobTitleColorTextNormal);
}
/* Hovered */
section.air-card-hover:hover h4 a:hover{
    color: var(--Upwork-DarkThemeGreenValley);
    text-decoration: none;
}
/* When AirCard becomes hovered */
section.air-card-hover:hover h4 a, section.air-card-hover:hover p a {
    color: var(--Upwork-DarkThemeGreenValley);
  /*text-shadow: 0px 0px 3px #00c40340; GLOW NOT ACTIVE FOR PERFORMANCE FEAR */
}



/* Air Cards Button Color Control */
.btn-default, .btn-success, .btn-info, .btn-warning, .btn-danger, .btn-default.sc-up-c-dropdown {
    color: #37A000;
    background-color: #272727;
    border-color: #293623;
}

/* Air Cards Button Color Control HOVER*/
.btn-default.sc-up-c-dropdown:hover, .btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .btn-success:hover, .btn-success:focus, .btn-success:active, .btn-success.active, .btn-info:hover, .btn-info:focus, .btn-info:active, .btn-info.active, .btn-warning:hover, .btn-warning:focus, .btn-warning:active, .btn-warning.active, .btn-danger:hover, .btn-danger:focus, .btn-danger:active, .btn-danger.active {
    color: var(--Upwork-DarkThemeHighlightGreen);
    background-color: #313030;
    border-color: #2f2f2f;
}
/* /* Air Cards Button Color Control HOVER GLOW */
.btn-default.sc-up-c-dropdown:hover, .btn:hover, .btn:active {
    -webkit-transition: all .3s;
    transition: all .3s;
    outline: none;
    -webkit-box-shadow: 0 2px 10px 1px rgba(6, 104, 19, .4),0 1px 2px rgba(92, 198, 117,0.25);
    box-shadow: 0 2px 10px 1px rgba(6, 104, 19, .4),0 1px 2px rgba(92, 198, 117, .3);
}











/* Air Cards Content Dividers Color Control */
.air-card section.air-card-divider-sm::after, up-c-card section.air-card-divider-sm::after, .air-card [eo-collapse] .air-card-divider-sm::after, up-c-card [eo-collapse] .air-card-divider-sm::after {
    border-bottom: 1px solid #505050;
}







/* Air Cards Main Footer Color  */
.air-card footer {
    border-top: 1px solid var(--Upwork-AircardHeaderSeparatorColor);
}


/* Air Cards Ending Separator Border Color  */
.air-card [eo-collapse] section, up-c-card [eo-collapse] section {
    border-bottom: 1px solid var(--Upwork-AircardHeaderSeparatorColor);
}





/* ::::::::::::::::::::::::::::::::::: */
/* :: TAGS SKILLS KEYWORDS CONTROLS :: */
/* ::::::::::::::::::::::::::::::::::: */

/* Air Cards Job Tags Color Control */
.o-tag, .o-tag-tested, .o-tag-certified, .o-tag-skill, .tokenizer-wrapper > .tokenizer-token-list .tokenizer-token {
    background-color: var(--Upwork-rash-SkillTagBackground);
    color: var(--Upwork-rash-SkillTagText);
}



/* ::::::::::::::::::::::::::: */
/* :: TITLES COLOR CONTROLS :: */
/* ::::::::::::::::::::::::::: */

/* Title Main Color */
h1 {
    color: #83f000;
}

/* Main Title AirCards (Job Feed / Contract Name Messages / Name Color)*/
h2 {
    color: var(--Upwork-DarkThemeJobTitleColorTextNormal) !important;
}

/* Used in Profile Titles + */
h3 {
    color: #f5f5f5;
}

h4 {
    color: var(--Upwork-DarkThemeBrightColorText);
}

h5 {
    color: #0dd0d9;
}

h6 {
    color: #fd06ff;
}

/* Proposals Question Titles + Checkbox Text + Sort By */
label:not(.btn), .control-label {
    margin-bottom: 10px !important;
    color: var(--Upwork-OriginalLogoGreen);
    font-weight: 700;
    font-size: 13px;
    text-align: left !important;
}


/* ::::::::::::::::::::::::::::::::: */
/* :: Input Fields Error Messsage :: */
/* ::::::::::::::::::::::::::::::::: */

.form-error.form-message {
    color: var(--Upwork-DarkThemeHighlightColorText);
}




/* :::::::::::::::::::::::::::::::: */
/* :: Input Fields Main Controls :: */
/* :::::::::::::::::::::::::::::::: */

/* Input Field Background & Glow Control */
input.form-control, textarea.form-control, .select2-search input {
    background-color: #222;
    border-color: #484848;
}
/* Input FIX for "FIND WORK >Skills Search */
.input-group .form-control:last-child{
    background-color: #222;
}

/* Input Field Text Color */
input.form-control, textarea.form-control, .select2-search input {
    color: #aaa;
}


/* :: IMPROVED SEARCH WITH SUGESTIONS SECTION :: */

/* Search Bar Outline color */
.navbar-v2 input.form-control, .navbar-v2 input.form-control,
.nav-v2 .nav-form-control{
border-color: #4a4a4a;
}

/* Search Bar Text color */
.navbar-v2 input.form-control,
.nav-v2 .nav-form-control{
color: var(--Upwork-DarkThemeNormalColorText);
}


/* Improved Search Background */
.basic-search-form-v2 > .input-group-search,
.nav-v2 .nav-form-control{
    background-color: #222;
    border-color: #484848;
}



/* Improved Search Suggestions Header Control */
.dropdown-header {
    color: var(--Upwork-rash-DropdownTextColor);
    background-color: var(--Upwork-rash-DropdownSuggestionsHeaderColor);
}


/* :: INPUT FIELD BLOCKED CONTROL (PROFILE LINK) :: */
input.form-control[readonly], textarea.form-control[readonly], .select2-search input[readonly], input.form-control[disabled], textarea.form-control[disabled], .select2-search input[disabled] {
    cursor: not-allowed;
    border: 1px solid #515151;
    color: #999;
    background-color: #3C3C3C;
    -webkit-box-shadow: none;
    box-shadow: none;
}













    

/* :::::::::::::::::::::::::::: */
/* :: DROPDOWN MAIN CONTROLS :: */
/* :::::::::::::::::::::::::::: */

/* Dropdown Arrow Located in Search Bars - Button Background */
.input-group.input-group-search .dropdown-toggle.btn {
    background-color: var(--Upwork-DarkThemeButtonBGColor);
}
/* INDIVIDUAL CONTROL FOR DROPDOWN HEADER BUTTTON (NOT MIXED WITH SUGGESTIONS) */
header.dropdown-header {
    color: var(--Upwork-rash-DropdownTextColor);
    background-color: var(--Upwork-rash-DropdownOptionsButtonHeaderColor);
}

/* Dropdown Custom (used in Proposals for project length)*/
/* Dimmed / Unactive */
.sc-up-c-dropdown-h .up-c-dropdown.sc-up-c-dropdown .dropdown.sc-up-c-dropdown:not(.dropdown-secondary) .dropdown-toggle.sc-up-c-dropdown {
    color: var(--Upwork-DarkThemeNormalColorText);
}

/* Active */
.dropdown.open.sc-up-c-dropdown .dropdown-toggle.btn.sc-up-c-dropdown {
    background-color: var(--Upwork-DarkThemeBGColorBody);
    color: var(--Upwork-DarkThemeBrightColorText);
}



/* :: STANDARD DROPDOWN :: */
/* STD Dropdown Text Color */
.btn-group.dropdown .btn {
    color: var(--Upwork-OriginalLogoGreen);
    -webkit-box-shadow: 0 1px 6px rgba(57,73,76,0.35);
    box-shadow: 0 1px 6px rgba(57,73,76,0.35);
    max-width: 100%;
}
.btn-group.dropdown .btn:hover, .btn-group.dropdown .btn:focus, .btn-group.dropdown .btn:active {
    color: var(--Upwork-DarkThemeHighlightGreen);
    background-color: var(--Upwork-DarkThemeButtonBGColorHover);
    -webkit-box-shadow: 0 2px 10px 1px rgba(57,73,76,0.4),0 1px 2px rgba(57,73,76,0.25);
    box-shadow: 0 2px 10px 1px rgba(57,73,76,0.4),0 1px 2px rgba(57,73,76,0.25);
}









/* ::::::::::::::::::::::::::::::::: */
/* :: MENU MASTER CONTROL SECTION :: */
/* ::::::::::::::::::::::::::::::::: */

/* Dropdown Menu Bezel */
.dropdown-menu, .eo-dropdown-menu {
    background-color: #363636;
}

/* Option Selected */
.dropdown-menu .active:not(.btn) > a, .eo-dropdown-menu .active:not(.btn) > a, .dropdown-menu .active:not(.btn) > a:hover, .eo-dropdown-menu .active:not(.btn) > a:hover, .dropdown-menu .active:not(.btn) > a:focus, .eo-dropdown-menu .active:not(.btn) > a:focus {
    background-color: #272727;
    color: #6fda44;
}

/* Options unselected */
.dropdown-menu > li > a, .eo-dropdown-menu > li > a {
    background-color: #1d1d1d;
    color: #888;
}
.dropdown-menu > li > a:hover, .eo-dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus, .eo-dropdown-menu > li > a:focus, .dropdown-menu > li > a.focus, .eo-dropdown-menu > li > a.focus {
    background-color: #474646;
    color: #6fda44;
}

/* PROFILE DROPDOWN MENU |  Freelancer BG color corrector */
body.bpa-settings-page.bpa-growable-layout div.off-canvas-container div.off-canvas-content div#layout nav.navbar div.container div#skinny-nav.navbar-collapse.d-none.d-lg-flex ul.navbar-nav.navbar-sm.navbar-right li.dropdown.dropdown-secondary.dropdown-account.align-end.open ul.dropdown-menu.dropdown-menu-md li ul.list-unstyled.dropdown-list li.active a{
    background-color: #363636 !important;
    color: #6fda44;
}


/*
/* MASTER HEADER MENU - 2019 UPDATE */
/* 


/* Menu Dropdown Top BG Color */
.navbar-v2 .dropdown-menu, .nav-v2 .nav-dropdown-menu{
    background-color: var(--Upwork-DarkThemeButtonBGColorHover);
}
/* Menu Dropdown Options BG Color */
.navbar-v2 .dropdown-menu li a,
.nav-v2 .nav-dropdown-menu li{
    color: var(--Upwork-DarkThemeNormalColorText);
    background-color: var(--Upwork-DarkThemeButtonBGColorHover);
    border-left: 0px solid  #84ff00ff;
}
/* Menu Dropdown Options HOVER Colors */
.navbar-v2 .dropdown-menu li > a.focus, .navbar-v2 .dropdown-menu li > a:focus, .navbar-v2 .dropdown-menu li > a:hover,
.nav-v2 .nav-dropdown-menu li a:hover{
    background-color: var(--Upwork-DarkThemeBGColorMenuHeader);
    color: var(--Upwork-DarkThemeGreenValley);
    border-left: 4px solid  #84ff00ff;
}


/* :: Menu PROFILE Dropdown Section :: */

/* Menu PROFILE Visibility ONLINE button */
.navbar-v2 .btn-group .btn,
.nav-v2 .nav-status button{
    background-color: var(--Upwork-rash-SkillTagBackground);
    color: var(--Upwork-rash-SkillTagText);
}




/* :::::::::::::::::::::::::: */
/* :: BUTTONS MAIN CONTROL :: */
/* :::::::::::::::::::::::::: */


/* Button Unactive & text + Dropdown unhovered BG +  */
.btn-group > .btn, .btn-group > .btn-default, .btn-group > .btn-primary {
    color: #c2c2c2; /* color for online status in menu profile*/
    background-color: #232323;
}

/* Button Disable BG, BORDER and TEXT */
.btn-primary.disabled, .btn-primary[disabled], .btn-primary.disabled:active, .btn-primary[disabled]:active, .btn-primary.disabled:hover, .btn-primary[disabled]:hover {
    color: #2b2b2b !important;
    border-color: var(--Upwork-DarkThemeButtonBGColorDisabledBorder);
    background: var(--Upwork-DarkThemeButtonBGColorDisabled) !important;
}











/* ATTACHMENTS DROP BOX STYLE */
.eo-image-drop.drop-box {
    background: none repeat scroll 0% 0% #515151;
    color: #e0e0e0;
    border: 2px dashed #4DE000;
    min-height: 80px;
    padding: 10px 0 0 10px;
        padding-top: 10px;
        padding-bottom: 0px;
}








/* :::::::::::::::::::::::::::::: */
/* :: ALERTS      MAIN CONTROL :: */
/* :::::::::::::::::::::::::::::: */


/* Alert Background */
.alert-info, .alert-success, .alert-warning, .alert-danger, .alert-notification, .alert-inline {
    background-color: #1d1d1d;
    color: var(--Upwork-DarkThemeBrightColorText);
}
/* Alert Vertical Separator */
.alert-info::after, .alert-success::after, .alert-warning::after, .alert-danger::after, .alert-notification::after, .alert-inline::after {
    border-left: 1px solid #84ff00;

}



/* Alert Icon Star Color :: Already Submitted Proposal */
.alert-info .glyphicon:not(.air-icon-close), .alert-success .glyphicon:not(.air-icon-close), .alert-warning .glyphicon:not(.air-icon-close), .alert-danger .glyphicon:not(.air-icon-close), .alert-notification .glyphicon:not(.air-icon-close), .alert-inline .glyphicon:not(.air-icon-close) {
    color: #1d1d1d;
}
/* Alert Icon Star BACKGROUND Color :: Already Submitted Proposal */
.alert-info .glyphicon:not(.air-icon-close) {
    border-color: #656565;
    background-color: #84ff00;
}


/* Alert EXCLAMATION Color :: Already Submitted Proposal */
.alert-danger .glyphicon:not(.air-icon-close) {
    border-color: #D50000;
    background-color: #84ff00;
}



















/* :::::::::::::::::::::::::::::: */
/* :: SLIDER JOBS MAIN CONTROL :: */
/* :::::::::::::::::::::::::::::: */

/* Slider Main Screen Transparency */
[is-opened] .slider {
    background: #0009 !important;
}

.slider-header {
    display: var(--slider-header-display);
    background: #1a1a1a;
    border-bottom: 1px solid #37a000;
    -webkit-box-shadow: 990px -1px 0 #171616,990px 0 0 0 #e0e0e0;
    box-shadow: 990px -1px 0 #777,990px 0 0 0 #e0e0e0;
}

.slider-content {
    background: #272727 !important;
    -webkit-box-shadow: 990px 0 0 #272727,-2px 0 7px rgba(57,73,76,.45);
    box-shadow: 990px 0 0 #272727,-2px 0 7px rgba(57,73,76,.45);
}

/* Slider BG Level 2 */
.slider-body {
    padding: 30px;
    background: #272727 !important;
}



/* ::::::::::::::::::::::::: */
/* :: POPUPS MAIN CONTROL :: */
/* ::::::::::::::::::::::::: */


/* ::: TOOLTIP POPUP CONTROL ::: */
/* Tooltip BG and Text Control */
.tooltip-inner {
    color: #5baa37;
    background-color: #383838;
}
/* Tooltip Arrow Tip Color */ 
.tooltip-arrow {
    border: 6px solid #383838 !important;
        border-top-color: rgb(56, 56, 56);
        border-right-color: rgb(56, 56, 56);
}


/* ::::::::::::::::::::::::::::::::: */
/* :: ROUNDED POPUPS MAIN CONTROL :: */
/* ::::::::::::::::::::::::::::::::: */
/* ::: Help - Tag Description ::: */

/* Rounded Popup BG and Text Control */
.popover{
    background-color: #383838;
    border: 2px solid #2b2b2b;
}
/* Rounded Popup Close Button Color */
.popover .popover-inner > .close {
    color: var(--Upwork-DarkThemeHighlightGreen);
}
/* Rounded Popup Close Button Shadow Color */
.close {
	text-shadow: 0 1px 0 #454545;
}


/* :::::::::::::::::::::::::::::::::::: */
/* :: SQUARED WARNING POPUPS CONTROL :: */
/* :::::::::::::::::::::::::::::::::::: */
/* :: Link warning in Upwork Messages :: */
.up-disintermediation-inline-callout {
    position: relative;
    background-color: #000;
    padding: 15px;
    margin: 0 !important;
    border: 1px solid #3b3535 !important;
        border-top-width: 1px;
    border-top-width: 0 !important;
    color: pink;
}
 /* Close X button */
.up-disintermediation-inline-callout button.close {
    color: var(--Upwork-DarkThemeHighlightGreen);
}



























/* ::::::::::::::::::::::::::::::::::: */
/* :: TABLES  SECTION (REPORTS    ) :: */
/* ::::::::::::::::::::::::::::::::::: */


/* Table Header Separator */
.table > thead > tr > th {
    border-bottom: 1px solid var(--Upwork-AircardHeaderSeparatorColor);
}

/* Table Body Separator */
.table tbody > tr > td {
    border-bottom: 1px solid var(--Upwork-rash-SeparatorLightColor);
}
/* Table Body Separator START FIX */
.table tbody > tr > td:first-child::after {
    border-bottom: 1px solid var(--Upwork-rash-SeparatorLightColor);
}
/* Table Body Separator END FIX */
.table tbody > tr > td:last-child::after {
    border-bottom: 1px solid var(--Upwork-rash-SeparatorLightColor);
}




/* ::::::::::::::::::::::::::::::::::::::::: */
/* :: SEARCH RESULTS BY PAGE (PAGINATION) :: */
/* ::::::::::::::::::::::::::::::::::::::::: */

/* Non selected pages and next results navigation */
.pagination > li > a, .pagination > li > span {
    color: var(--Upwork-DarkThemeMutedColorText);
}
.pagination > li > a:hover, .pagination > li > a:focus, .pagination > li > span:hover, .pagination > li > span:focus {
    color: var(--Upwork-OriginaLogoGreen);
    text-decoration: none;
}


/* :::::::::::::::::::::::: */
/* :: SEPARATOR BY TABLE :: */
/* :::::::::::::::::::::::: */
hr {
    border-top: 1px solid var(--Upwork-rash-SeparatorLightColor);
}




/* :::::::::::::::::::::::: */
/* ::   FOOTER SECTION   :: */
/* :::::::::::::::::::::::: */

/* Background Color */
.footer-navbar-wrapper {
    background-color: var(--Upwork-DarkThemeBGColorMenuHeader);
}

/* Links Colors */
.footer-navbar-wrapper .links-section a {
    color: var(--Upwork-DarkThemeMutedColorText);
}

/* Social Media Icons */
.footer-navbar-wrapper .social-icons-wrapper .glyphicon {
    background: #000;
    -webkit-box-shadow: 0 0 0 10px #000;
    box-shadow: 0 0 0 10px #000;
}
.footer-navbar-wrapper .social-icons-wrapper a:hover .glyphicon {
    background: #6fda44;
    -webkit-box-shadow: 0 0 0 10px #6fda44;
    box-shadow: 0 0 0 10px #6fda44;
}

/* Copyright ending link Color */
.footer-navbar-wrapper .copyright {
    color: var(--Upwork-DarkThemeGreenValley);
}



/* :::::::::::::::::::::::::::::::::::: */
/* ::   NOTIFICATIONS PAGE SECTION   :: */
/* :::::::::::::::::::::::::::::::::::: */


/* NOTIFICATION SECTION https://www.upwork.com/ab/notifications/ */
/* Notifications separator color center */
.notifications-block .notification-list [class*="col-"] {
    border-bottom: 1px solid var(--Upwork-rash-SeparatorLightColor);
}
/* Notifications separator color extremes */
.notifications-block .notification-list .notification-delete-icon::after {
    border-bottom: 1px solid var(--Upwork-rash-SeparatorLightColor);
}
.notifications-block .notification-list .notification-date-block::after {
    border-bottom: 1px solid var(--Upwork-rash-SeparatorLightColor);
}















/* :::::::::::::::::::::::::::::::: */
/* ::   CURRENT TESTS SECTION   :: */
