style.css:
@-moz-document url-prefix("https://nitro.powerhrg.com/connect") {
/*noborders*/

.section-header {
    border-bottom: 0 !important;
}

.bookmarks-bar {
    border-right: 0 !important;
}

.bookmark+.bookmark {
    border-top: 0 !important;
}

.message-input-container {
	border: none !important;
}

.message-input-container .DraftEditor-editorContainer {
	border: none !important;
}

.simple-list {
	border: none !important;
}

.message-separator, .bookmarks-subheader, .message-separator-label {
	border: none !important;
}

.bookmark-list .droppable-container > div:not(:first-child) {
        border: none !important;
}

/*compact*/

.bookmark {
	min-height: 20px;
	padding: 2px 10px;
}

.bookmark-label-dpt {
	display: none;
}

.people-bookmark section {
	margin-top: 0px;
	height: 20px;
	width: 20px;
	border-radius: 10px;
}

.people-bookmark section div {
	max-height: 20px;
	max-width: 20px;
}

.people-bookmark section div img {
	height: 20px;
	width: 20px;
}

.bookmark-image-stacked {
	margin-left: 0;
}
.section-header .bookmark .section-header-users .bookmark-image {
	margin-right: 1.1rem;
}

.bookmark-label > * {
	float: left;
}

.bookmark-group-images > div {
    top: 0rem !important;
    border: none !important;
} 

.user-presence {
	margin-top: 4px;
	margin-left: 5px;
	max-width: 12px;
	max-height: 13px;
	display: inline;
	overflow: hidden;
}

#master-indicator-div {
	display: inline-block;
}

#master-indicator .activity-badge-all {
	background: #000080;
}

.message {
	background-color: white !important;
}

.bookmarks-subheader {
	color: #637381;
	background-color: #f7f7f7;
}

.bookmark-label-group-conv {
    font-size: 1.1em !important;
    font-weight: 500 !important;
    margin-top: -2px !important;
}

.bookmark-group-conv-group {
	margin-left: 13px;
}

.bookmark-badge-group-conv-count {
	margin-top: -2px !important;
	height: 16px !important;
	border-top-left-radius: 0 !important;
	border-bottom-left-radius: 0 !important;
	border-top-right-radius: 6px !important;
	border-bottom-right-radius: 6px !important;
}

.bookmarks-subheader > span {
	color: #637381;
}

.message-separator {
	background-color: #f7f7f7 !important;
}

/* Group Conversation avatars */
.section-header .bookmark .section-header-users .bookmark-image img {
    border: none;
}

.section-header .bookmark .bookmark-image {
	margin-top: -1px !important;
}

/* Correct alignment of like button */
.message-buttons {
	top: 10px !important;
}

/* DARK MODE: */

/* loading indicators */

.alert-info {
	background: transparent !important;
}

/* main chatter area: */

.message {
	background-color: #2F3136 !important;
	margin: 0 !important;
}

.message:hover {
	background-color: #2B2C31 !important;
}

body, .messages {
	color: #DDDDDD !important;
	background: #2F3136 !important;
}

.message-body {
	color: #DDDDDD !important;
	background: transparent !important;
}

.user-mention {
	color: #2F3136 !important;
}

.inline-url, .inline-file {
	color: #999999 !important;
}

/* markdown */

.code-inline {
	background-color: #222 !important;
}

/* emoji reactions: */

.emoji-reaction-text {
	background: #2B2C31 !important;
	border: 1px solid #36393F !important;
}

.emoji-highlighted-reaction-text {
	background: #2B2C31 !important;
	border: 1px solid #36393F !important;
}

.emoji-reaction-text:hover {
	background: #36393F !important;
	border: 1px solid #36393F !important;
}

.emoji-highlighted-reaction-text:hover {
	background: #36393F !important;
	border: 1px solid #36393F !important;
}

/* emoji reactions floating favorites: */

.messages .message .emoji-favorites {
	background: #2B2C31 !important;
}

.emoji-favorites {
	border: 1px solid #36393F !important;
	transition: none !important;
	box-shadow: none !important;	
}

.emoji-card {
	background: #2B2C31 !important;
	border: 1px solid #36393F !important;
}

.emoji-card:hover {
	background: #36393F !important;
}

.smile-plus-icon {
	color: white !important;
}

/* emoji all picker */

.emoji-mart {
	background: #2B2C31 !important;
	border: none !important;
}

.info-content {
	background: #2B2C31 !important;
}

.emoji-mart-category-label span {
	background: #2B2C31 !important;
	color: white !important;
}

/* side bar: */

.rooms-bookmark, .people-bookmark {
	background: #36393F !important;
}

.bookmark-badge-group-conv-count {
	background: #2B2C31 !important;
	color: #999999 !important;
}

.bookmark, .bookmark-label-strong, .bookmark-label-group-conv {
	color: #DDDDDD !important;
}

.active-bookmark {
	background: #2B2C31 !important;
}

.bookmarks-bar .sticky-container {
	background: #36393F !important;
}

/* darker gray bars between bookmark sections and message blocks: */

.message-separator, .bookmarks-subheader, .message-separator-label {
	color: #999999 !important;
	background: #2B2C31 !important;
}

/* input text area: */

.message-input-container {
	background: #2F3136 !important;
}

.message-input-container .DraftEditor-editorContainer {
	background: #484C52 !important;
	color: #DDDDDD !important;
}

/* scroll bar */

::-webkit-scrollbar:vertical, ::-webkit-scrollbar-track:vertical, ::-webkit-resizer:vertical, ::-webkit-scrollbar-corner:vertical {
  background-color: #2F3136 !important;
}

::-webkit-scrollbar-thumb:vertical {
	background-color: #2F3136 !important;
}

::-webkit-scrollbar-thumb:vertical:hover {
	background-color: #2B2C31 !important;
}

::-webkit-scrollbar {
	height: 0 !important;
}

/* notification settings prefs popover */

.notification-preferences-container h2 {
	background-color: #2B2C31 !important;
}

.notification-preferences-container {
	background: #2F3136 !important;
}

.notification-preferences-container input {
	background: #484C52 !important;
	color: #DDDDDD !important;
	border: 1px solid #2F3136 !important;
}

.notification-preferences-container .room-name-label {
	background: #2F3136 !important;
	border-radius: 10px !important;
	border: 1px solid #484C52 !important;
}

/* notification preview prefs popover */

.notif-preview-opts {
	background: #2F3136 !important;	
}

.notifications-preview-popover h2 {
	background: #2B2C31 !important;
}

.Popover-tip {
	fill: #2B2C31 !important;
}

/* reaction list popover */

.bookmarks-selector-header, .bookmarks-selector-footer {
	background-color: #2B2C31 !important;
}

/* roster popover */

.room-roster-popover-body {
	border-radius: 0 !important;
}

.roster-header {
	background: #2F3136 !important;
	border-top-left-radius: .5rem !important;
	border-top-right-radius: .5rem !important;
}
.roster-header span {
	font-size: 14pt !important;
}

.room-roster-popover h2 {
	background-color: #2F3136 !important;
}

.roster-list-container .bookmark {
	background-color: #36393F !important;
}

.room-roster-add {
	background-color: #2F3136 !important;
}

.room-roster-popover .roster-header {
	background-color: #2F3136 !important;
}

.room-roster-add input {
	border: 1px solid #2B2C31 !important;
	border-radius: .5rem !important;
	background: #484C52 !important;
	color: #DDDDDD !important;
}

.roster-list-container {
	background-color: #36393F !important;
}

/* darker blue bars */

.section-header {
	background-color: #4867ad !important;
}

.btn-add {
	margin-right: 10px !important;
	background-size: 20px !important;
	background-color: #4867ad !important;
	width: 50px !important;
	height: 50px !important;
}

/* New Conversation big modal: */

.modal-overlay, .modal-overlay-light, .room-form {
	background: #2B2C31 !important;
}

.bookmarks-selector-tabs .is-active {
	color: #DDDDDD !important;
}

.bookmarks-selector hr {
	border: none !important;
}

.bookmarks-selector input, .room-form input {
	border: 1px solid #2B2C31 !important;
	border-radius: .5rem !important;
	background: #484C52 !important;
	color: #DDDDDD !important;
}

.room-form .room-name-label {
	background: #2B2C31 !important;
	border-radius: 10px !important;
	border: 1px solid #484C52 !important;
}

.room-form .bookmark {
	background: #36393F !important;
}

.memberships-container {
	border: none !important;
	background: #36393F !important;
}

.bookmark-selector-list-container {
	border: 1px solid #282828;
	background: #2B2C31 !important;
}

.bookmark-selector-list-container .filterable-list {
	background: #2B2C31 !important;
}

}
