style.css:
@-moz-document domain("twitch.tv") {
:root {
    --better-dark: hsla(240, 6%, 8%, 1);
    --better-dark--lighter: hsla(240, 6%, 5%, 1);
}

/* Overriding variables */
:root {
    --color-text-base: #ddd;
}

.tw-root--theme-dark body {
    background-color: var(--better-dark);
}

.tw-root--theme-dark .chat-room {
    background-color: var(--better-dark);
}

.tw-root--theme-dark .channel-root__right-column {
    background-color: var(--better-dark--lighter);
}

}
