style.css:
@-moz-document domain('twitch.tv') {
  figure.tw-avatar > .tw-border-radius-rounded {
      border-radius: 0 !important;
  }

  body {
      font-family: Helvetica !important; 
  }

  .top-nav__menu {
      background-color: #3A2E5C !important;
  }

  body,
  .tw-c-background-alt,
  .side-nav__overlay-wrapper,
  .chat-room,
  .rooms-header,
  .channel-header
  {
      background-color: #0F0E13 !important;
  }

  .side-nav {
      border-right: var(--border-width-default) solid var(--color-border-base)!important;
  }

  .tw-upcase {
      text-transform: capitalize !important;
  }

  h2[data-a-target="stream-title"] {
      font-size: 1.7rem !important;
      font-weight: 500
  }
}
