style.css:
@-moz-document domain(twitch.tv) {
p, h1, h2, h3, h4, h5, span, a {
    font-family:"Helvetica Neue", Helvetica, Arial, sans-serif !important;
    font-weight: 100 !important;
}

.tw-flex-grow-1 {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
}


.tw-font-size-4 {
    font-size: 1.5rem!important;
}


.tw-upcase {
    text-transform: none!important;
}

.top-nav__menu.tw-align-items-stretch.tw-c-background-base.tw-elevation-1.tw-flex.tw-flex-nowrap.tw-full-height {
    background-color: #3a2e5c !important;
}

.tw-root--theme-dark .navigation-link {
    color: #e8e8e8;
}

.tw-root--theme-dark .navigation-link.active, .tw-root--theme-dark .navigation-link:hover {
    color: #fff;
}

.tw-semibold, .tw-strong {
    font-weight: 100 !important;
}

.tw-root--theme-dark .side-nav__overlay-wrapper {
    background-color: #0f0e13 !important;
    border-right: 1px solid #3a2e5c !important;
}

.tw-root--theme-dark .channel-header {
    background-color: #19171c !important;
}

main.tw-flex.tw-flex-column.tw-flex-grow-1.tw-full-height.tw-full-width.tw-overflow-hidden.tw-relative.tw-z-default.twilight-main {
    background-color: #0f0e13 !important;
}

ul.tw-align-items-center.tw-flex.tw-flex-grow-1.tw-flex-wrap.tw-font-size-4.tw-full-height.tw-justify-content-center {
    justify-content: left !important;
}

.tw-relative.tw-transition-group {
    border-top: 1px solid #3a2e5c;
}

.rooms-header.tw-align-items-center.tw-border-b.tw-flex.tw-flex-shrink-0.tw-full-width.tw-justify-content-center.tw-pd-l-1.tw-pd-r-1 {
    background-color: #0f0e13 !important;
}

.tw-border-b {
    border-bottom: 1px solid #3a2e5c!important;
}

.chat-list.tw-flex.tw-flex-column.tw-flex-grow-1.tw-flex-nowrap.tw-overflow-hidden {
    background-color: #0f0e13 !important;
}

.chat-input.tw-block.tw-pd-b-1.tw-pd-x-1 {
    background-color: #0f0e13 !important;
}
}
