style.css:
@-moz-document url-prefix("https://translation.frozendistrict.com") {
/* Black - #000000 */
/* Body - #2a2a2a; */
/* Lighter - #3b3b3b */

body {
	background-color: #2a2a2a;
    color: #dddddd;
}
.navbar {
	background-color: #000000;
}
.breadcrumb {
	background-color: #2a2a2a;
}
tr:nth-of-type(odd) {
	background-color: #3b3b3b !important;
}
.form-control {
	background-color: #3b3b3b;
    color: #dddddd;
    border-radius: 0;
}
.stripe-odd {
	background-color: #3b3b3b;
}
#history td {
	border: 0;
}
#activity-month span {
	color: #dddddd;
}
.ct-grid {
	stroke: #dddddd;
}
.ct-label {
	color: #dddddd;
}
.panel-body {
	background-color: #2a2a2a;
}
.panel-footer {
	background-color: #2a2a2a;
    border-top: 0;
}
pre, .pre-scrollable {
	background-color: #3b3b3b;
    color: #dddddd;
}
.table {
	background-color: #2a2a2a;
    color: #dddddd;
}
.multi-wrapper .search-input, .multi-wrapper .non-selected-wrapper, .multi-wrapper .selected-wrapper {
	background-color: #3b3b3b;
}
.multi-wrapper .header, ::placeholder {
	color: #dddddd;
}
.zen-unit:nth-child(even) {
	background-color: #3b3b3b;
}
.zen-unit .translatetext {
	background-color: #333333;
}
.list-group-item {
	background-color: #3b3b3b;
    color: #dddddd;
}
button {
	color: #333333;
}
ins {
    color: #dddddd;
    background-color: #307530;
}
del {
	color: #dddddd;
    background-color: #f74a4a;
}
a.list-group-item-success {
	color: #7dff7f;
}
a.list-group-item-danger {
	color: #ff6461;
}
a.list-group-item-info {
	color: #57c7ff;
}
a.list-group-item-warning {
	color: #ffca6e;
}
footer a {
	color: #1fa385;
}
}
