style.css:
@-moz-document domain("autotrader.co.uk") {


.wide-page--search {
    background-color: #1b1b1b;
}
.search-listing {
    position: relative;
    background: #212121;
    border-radius: 4px;
    box-shadow: 0 1px 2px 0 rgba(0,0,0,.1);
    margin: 0;
    font-size: 0;
    overflow: hidden;
}

.search-form__form {
    position: relative;
    background-color: #252525;
    margin-bottom: 16px;
    border-radius: 4px;
}

.search-form__header-content {
    border-radius: 4px 4px 0 0;
    border: 1px solid #212121;
    padding: 8px 0 4px;
}

.search-form__fields-list {
    list-style-type: none;
    margin: 0;
    padding: 0;
    border: 1px solid #212121;
    border-top: none;
    border-radius: 0 0 4px 4px;
}

.search-form__fields-list>li {
    position: relative;
    border-bottom: 1px solid #212121;
}

.vehicle-price {
    color: #9bb9f7;
    margin-bottom: 4px;
    text-align: center;
}

.search-form__count {
    margin: 0 auto 4px;
    font-weight: 700;
    font-size: 20px;
    line-height: 24px;
    color: #90b4ff;
    text-align: center;
}

.sort-option__label {
    height: 28px;
    vertical-align: middle;
    padding: 0 4px;
    color: #91b5f7;
}

.breadcrumbs {
    background-color: #212121;
    border-bottom: 1px solid #212121;
}
.selectbox__label {
    border-color: #313944;
    border-radius: 4px;
    color: #666;
}
.selectbox__label {
    height: 30px;
    width: 100%;
    border: 1px solid #212121;
    color: #46505a;
    background: #232323;
    border-radius: 3px;
    z-index: 1;
    font-size: 13px;
    line-height: 28px;
    padding: 0 25px 0 10px;
    position: absolute;
    top: 0;
}

.at-rebrand .input {
    border-color: #2e313c;
    color: #666;
    border-radius: 4px;
}
.search-form__location-field #postcode {
    height: 30px;
    background-color: #232323;
}
.atc-pushswitch__inner:before {
    background-color: #383838;
    color: #06a09d;
    content: " ";
    padding-right: 10px;
    text-align: right;
}
.c-form__toggle-switch__item.is-selected {
    color: #313c53;
    box-shadow: 0 0 0 1px #06a09d, inset 0 0 0 1px #06a09d;
    border-radius: 20px;
    background-color: #313131;
}
.c-form__toggle-switch {
    display: flex;
    background-color: #232323;
    border: 1px solid #383838;
    border-radius: 20px;
    padding: 0;
    list-style: none;
    font-size: 13px;
    color: #666;
}
.c-form__toggle-switch__item.is-selected {
    color: #376bd6;
    box-shadow: 0 0 0 1px #06a09d, inset 0 0 0 1px #06a09d;
    border-radius: 20px;
    background-color: #313131;
}
.sf-accordion__select-options select {
    text-indent: 5px;
    -webkit-appearance: none;
    -moz-appearance: none;
    background-color: #232323;
}
.sf-accordion__select-options .select-options__overlay, .sf-accordion__select-options select {
    text-align: left;
    background-color: #fff;
    width: 73%;
    height: 30px;
    border-radius: 3px;
    vertical-align: middle;
    display: inline-block;
    border: 1px solid #2c2e3a;
    background-repeat: no-repeat;
    background-size: 10px;
    background-position: 95%;
    text-indent: 10px;
}
.sf-accordion__select-options .select-options__overlay, .sf-accordion__select-options select {
    text-align: left;
    background-color: #232323;
    width: 73%;
    height: 30px;
    border-radius: 3px;
    vertical-align: middle;
    display: inline-block;
    border: 1px solid #2c2e3a;
    background-repeat: no-repeat;
    background-size: 10px;
    background-position: 95%;
    text-indent: 10px;
}
.at-rebrand .input {
    border-color: #2e313c;
    color: #666;
    border-radius: 4px;
    background: #232323;
}
.button--square {
    padding: 0 15px;
    height: 30px;
    background-color: #272727;
    background: -owg-linear-gradient(#fdfdfd,#e0e0e0);
    background: linear-gradient(#232323,#232323);
    border-color: #2d3140;
    color: #267ec8;
    font-size: 13px;
    line-height: 30px;
    cursor: pointer;
}
.module {
    background-color: #f4f4f4;
    border-radius: 5px/5px;
    border: 1px solid #232323;
    padding: 10px;
    margin: 10px 0;
}
.search-page__left .module {
    margin-bottom: 16px;
    background-color: #232323;
}
.searchResults__buttonBar-tab button {
    font-size: 14px;
    line-height: 18px;
    font-weight: 400;
    color: #666;
    background-color: #232323;
    border: none;
    color: #2a65ba;
    display: block;
    height: 37px;
    line-height: 16px;
    width: 100%;
    outline: 0;
    top: 0;
}
.atc-tabline-group {
    border-bottom: 1px solid #232323;
    border: #232323;
    display: flex;
    margin: 0;
    padding: 0;
}
.atc-foundation-header {
    background-color: #232323;
    box-shadow: 0 2px 4px 0 rgba(0,0,0,.2), 0 1px 2px 0 rgba(0,0,0,.1);
    color: #fff;
    position: absolute;
    top: 0;
    width: 100%;
    z-index: 200;
}
.atc-card {
    background-color: #232323;
    border-radius: 4px;
    display: flex;
    flex-direction: column;
    position: relative;
}
.pagination {
    background-color: #232323;
    position: relative;
    padding: 8px;
    height: 40px;
    width: 100%;
}
body {
    margin: 0;
    font-family: 'Fabriga Regular', Helvetica, Arial, sans-serif;
    font-size: 75%;
    background-color: #232323;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}
.fpa__wrapper {
    max-width: 1272px;
    background-color: #212121;
    margin: 0 auto;
    padding: 0 16px 16px;
}
.contact-card {
    background-color: #232323;
    padding: 16px;
    margin: 0 0 24px;
}
#chatAndTextOverlayCard {
    position: fixed;
    z-index: 3;
    background: #232323;
    box-shadow: 0 19px 38px rgba(49,60,83,0.1), 0 8px 12px rgba(49,60,83,0.22);
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    bottom: 0;
    right: 32px;
    transition: height 1s, width 1s, top 1s;
    width: 192px;
}
.deal-builder__tab-container {
    background-color: #232323;
    border: 4px solid #232323;
    box-shadow: 0 1px 2px 0 rgba(0,0,0,0.1);
    border-top: 0;
    border-radius: 4px;
}
.atc-tab--selected {
    background-color: #232323;
    border-top-color: #06a09d;
    position: relative;
}
.deal-builder__tab-label {
    color: #2065ad;
    font-size: 16px;
    position: relative;
    line-height: 1;
}
.advert-heading__title {
    color: #86a2dc;
    padding: 0;
    margin: 0 0 4px;
}
.advert-price__cash-price {
    color: #8eaff3;
}
.other-vehicles-from-seller {
    background-color: #212121;
    padding: 32px 0 24px;
}
.p-card {
    position: relative;
    margin: 0;
    border-radius: 4px;
    background: #232323;
    height: 100%;
    display: flex;
    flex: 1 0 auto;
    flex-wrap: wrap;
}
.p-card--vehicle .p-card__header {
    border-bottom: 1px solid #353535;
}
.buying-advice-link {
    background-color: #232323;
    display: block;
    font-size: 14px;
    padding-bottom: 16px;
    padding-top: 16px;
    text-align: center;
    transition: box-shadow .1s ease-in-out;
}
.footer__nav {
    background-color: #212121;
}
.fpa {
    background-color: #212121;
}
.basic-check-m {
    border: 4px solid #292929;
    border-radius: 4px;
    padding: 16px;
    margin: 0 0 64px;
}
.interested-in-vehicle {
    border: 4px solid #292929;
    padding: 16px 16px 0;
}
.ov-bg-colour--at-grey {
    background-color: #212121;
}
.template1--width.brand-new-rec {
    margin: 0 auto;
    background-color: #232323;
    padding: 32px 0 16px;
}
.brand-new-rec--maintitle {
    margin-bottom: 16px;
    color: #98b9ff;
}
.p-quick-search .header-1 {
    font-size: 26px;
    line-height: 28px;
    margin: 0;
    padding: 0;
    font-weight: 400;
    margin: 0 0 8px 0;
    color: #ffffff;
}
.p-quick-search .c-form__input, .p-quick-search .c-form__select {
    background-color: #252525;
    height: 2.5em;
    line-height: 1.1;
    -webkit-transition: background 0.1s ease-out, color 0.1s ease-out;
    transition: background 0.1s ease-out, color 0.1s ease-out;
}
.c-form__input {
    display: block;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    position: relative;
    border-radius: 4px;
    padding: 8px;
    font-size: 14px;
    color: #666;
    border: solid 2px #444444;
    background-color: #fff;
    margin: 0 0 8px 0;
    outline: 0;
}
.c-form__select {
    display: block;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    position: relative;
    border-radius: 4px;
    padding: 8px;
    font-size: 14px;
    color: #666;
    border: solid 2px #424242;
    background-color: #fff;
    margin: 0 0 8px 0;
    outline: 0;
    background-image: url(https://www.autotrader.co.uk/images/at3/icon-chevron-down.svg);
    background-repeat: no-repeat;
    background-position: 98% 50%;
    background-size: 8px 8px;
}
.c-form__select.is-selected {
    color: #9ebeff;
}
.recently-viewed-vehicles {
    margin: 0 auto;
    background-color: #232323;
    padding: 32px 0 0;
}
.sell__nav.sell__nav--restyled.cars {
    margin: 0;
    padding: 0;
    background-color: #232323;
}
.c-form__input {
    display: block;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    position: relative;
    border-radius: 4px;
    padding: 8px;
    font-size: 14px;
    color: #666;
    border: solid 2px #444444;
    background-color: #232323;
    margin: 0 0 8px 0;
    outline: 0;
}
.p-action-tile {
    background-color: #232323;
    border-radius: 4px;
    position: relative;
    height: 100%;
}
.products-module {
    padding: 32px 0 16px;
    background-color: #232323;
}
.products__nav, .content__nav, .alt-search__nav, .recommendation__nav, .editorial__nav {
    background-color: #232323;
}
.footer__nav-to-top section {
    background-color: #232323;
}
.editorial__nav .media .homepage-editorial-imageholder, .editorial__nav .media .media__body {
    display: block;
    background-color: #252525;
}
.p-quick-search {
    position: absolute;
    width: 100%;
    padding: 8px 16px;
    font-size: 14px;
    background-color: #212121;
    border-radius: 4px;
    color: #666;
}
.legal-info__alert-security {
    background-color: rgba(16, 16, 16, 0.95);
    color: #666;
}
.expert-review {
    padding-bottom: 4px;
    margin-bottom: 15px;
    border-bottom: 3px solid #232323;
    list-style: none;
    height: 109px;
}

}
