style.css:
@-moz-document domain("amazon.fr") {

/* fond couleur site*/
#pageContent {
	background-color: #111;
}
#a-page {
	background-color: #111;
}
/* fond de couleur du site*/
#gw-layout {
	background-color: #111;
	padding-left: 50px;
	padding-right: 50px;
}
/* pour le Header 
 Change la couleur : */
#navbar.nav-bluebeacon #nav-main {
	background-color: #111;
}
#navbar.nav-bluebeacon #nav-belt {
	background-color: #111;
}
/* simplifier la navbar en enlevant des éléments */
#navbar[data-template=layoutSwapToolBar] #nav-swmslot {
	display: none;
}
#nav-link-prime {
	display: none;
}
#nav-orders {
	display: none;
}
.nav-packard-glow #nav-global-location-slot .nav-a-2 {
	display: none;
}
#nav-xshop-container {
	display: none;
}
#nav-main.nav-sprite {
	display: none;
}
.nav-sprite.nav-logo-tagline.nav-prime-try {
	display: none;
}
/* redesign du header */
#nav-belt {
	border-bottom: 40px solid #111;
	border-top: 25px solid #111;
	padding: 15px;
}
/* carousel design */
#desktop-banner {
	margin: auto;
}
/* nettoyage de la page web*/
.quadrant-overlay {
	background-color: #9990;
}
#rhf {
	display: none;
}
#desktop-1 {
	display: none;
}
#desktop-2 {
	display: none;
}
#desktop-5 {
	padding: 10px;
}
.a-section.as-title-block {
	display: flex;
	align-items: center;
	flex-direction: column;
	margin-bottom: 25px;
	margin-top: 25px;
}
#desktop-6 {
	display: none;
}
#desktop-7 {
	display: none;
}
.a-link-normal.see-more.truncate-1line {
	color: black
}
#gw-card-layout {
	display: flex;
	justify-content: space-evenly;
	align-content: center;
	padding: 25px;
}
#desktop-btf-grid-2 {
	display: none;
}
#desktop-btf-grid-1 {
	color: white;
}
#desktop-btf-grid-3 {
	color: white;
}
#desktop-btf-grid-8 {
	color: white;
}
#desktop-btf-grid-9 {
	color: white;
}
.a-color-base.headline.truncate-1line {
	display: flex;
	justify-content: space-around;
	margin-top: 10px;
	margin-bottom: 25px;
}
.a-link-normal.see-more.truncate-1line {
	display: flex;
	justify-content: space-around;
	padding-top: 25px;
	padding-bottom: 10px;
}
.a-spacing-top-mini.a-size-base.description.truncate-2line {
	display: none;
}
#gw-content-grid {
	display: flex;
	justify-content: space-around;
	padding: 23px;
}
/* footer nettoyage et redesign*/
#navFooter.navLeftFooter {
	background-color: #111;
}
#navFooter.navLeftFooter .navFooterBackToTop {
	background-color: #111;
}
.navFooterLine.navFooterLinkLine.navFooterPadItemLine {
	display: none;
}
.navFooterLine.navFooterLinkLine.navFooterDescLine {
	display: none;
}
.navFooterLine.navFooterLinkLine.navFooterPadItemLine.navFooterCopyright {
	display: contents;
	display: flex;
	justify-content: center;
	background-color: #111;
}
#navFooter .navAccessibility.navFooterVerticalColumn {
	display: none;
}
#navFooter.navLeftFooter .navFooterCopyright {
	background-color: #111;
	display: flex;
	flex-direction: column;
	padding-bottom: 25px;
}
div.navFooterLine {
	line-height: 35px;
}
/* adblock*/
#desktop-ad-btf {
	display: none;
}
.a-section.a-spacing-none.gw-col.gw-fixed-col.gw-card-layout {
	display: none;
}
#desktop-ad-center-1 {
	display: none;
}
/*pop-up compte et listes*/
.nav-signin-tt.nav-flyout {
	background: #0009;
	border: 1px solid grey;
}
#nav-signin-tooltip .nav-signin-tooltip-footer {
	color: white;
}
#nav-signin-tooltip .nav-signin-tooltip-footer .nav-a {
	color: #ffc854;
}
#nav-flyout-accountList {
	background-color: #111;
}
#nav-flyout-ya-signin a.nav-a,
#nav-flyout-ya-signin a.nav-a:link,
#nav-flyout-ya-signin a.nav-a:visited {
	color: #fff;
}
#nav-flyout-accountList .nav-al-flyout-header {
	color: #ffffffe6;
}
.nav-tpl-discoveryPanelList .nav-text,
.nav-tpl-discoveryPanelSummary .nav-text,
.nav-tpl-itemList .nav-text {
	color: #999999d4;
}
#nav-flyout-ya-signin .nav_pop_new_cust {
	color: #ffffffd9;
}
#nav-cover {
	background-color: #111;
}
/* hamburger menu*/
#hmenu-container #hmenu-canvas #hmenu-customer-profile {
	background-color: #111;
}
#hmenu-container #hmenu-canvas #hmenu-content {
	background-color: #111;
}
#hmenu-container #hmenu-canvas #hmenu-content ul.hmenu li a,
#hmenu-container #hmenu-canvas #hmenu-content ul.hmenu li a:hover,
#hmenu-container #hmenu-canvas #hmenu-content ul.hmenu li a:visited {
	color: #999;
}
#hmenu-container #hmenu-canvas #hmenu-content ul.hmenu li .hmenu-item.hmenu-title {
	color: white;
}
#hmenu-container #hmenu-canvas #hmenu-content ul.hmenu.hmenu-visible {
	padding: 25px;
}
/* tester prime pop-up*/
#nav-flyout-prime {
	background-color: black;
}
.nav-npm-title-a,
a.nav-npm-title-a,
a.nav-npm-title-a:link,
a.nav-npm-title-a:visited,
a.nav-npm-title-a:hover,
a.nav-npm-title-a:active {
	color: white;
}
.nav-npm-text-title,
a.nav-npm-text-title-a,
a.nav-npm-text-title-a:link,
a.nav-npm-text-title-a:visited,
a.nav-npm-text-title-a:hover,
a.nav-npm-text-title-a:active {
	color: white;
}
.nav-npm-text-detail {
	color: #999;
}
#nav-npm-footer .prime-button-try {
	border-color: white;
	background: white;
}
#nav-npm-prime-logo {
	display: none;
}
/* suggestion de la barre de recherche*/
#nav-flyout-searchAjax {
	background-color: #1119;
}
#suggestions {
	color: white;
}
/* conditions d'utilisations*/
.text-link-stripe {
	background-color: #111;
}
.a-color-base {
	color: #999;
}
.a-size-base.a-link-normal {
	color: white;
}
/* dégradé gris bizarre*/
.a-divider-inner {
	display: none;
}
}
