style.css:
@-moz-document domain("postfinance.ch") {
body { 
    background-image: linear-gradient(#190093, #ffcaff);
    
}
.footer_contact {
    background-color: #cca6ea;
}

.header .header_main--section.header_main--section-border-right {
    background-color: #6a58bf;
}

.header{
    background-color: #200796;
}

.header--sticky.is-detached .header_main:before {
    background-color: #6a58bf;
}

.teaser_basic--body {
    background: #fff;
    border-bottom: 3px solid #44f4f4;
    color: #f6feff;
    background-color: #6000a2b3;
}

.teaser_rich--body {
    background-color: #6000a2b3;
    color: #f6feff;
}

.teaser_rich--content .button {
    background-color: #44f4f4;
    border: 1px solid transparent;
}

.card--button {

    background-color: #482faa;
}

cb_flowbox--trigger-btn {
    background-color: #44f4f4;
    border: 1px solid transparent;
}

 
html {
    color: #f6feff;
    background: #fff;
}

.footer {
    background-color: #6a58bf;
    color: #ad9b9b;
}

.footer_contact--item--link {
    background-color: #cca6eb
}

.footer_nav--category--title {
    color: #f6feff;
}

.line_table a:not([class]) {
    text-decoration: none;
    color: #f6feff;
}

 
.footer .link {
    color: #f6feff;
}

.footer .link:hover {
    color: #795993;
}

p {
    color: #f6feff;
}

.rich_list_item--content {
    color: #f6feff;
}
.rich_list_item--pretitle {
    color: #f6feff;
}

.header_menu_flyout--inner{
    background-color: #6a59bf;
}

.header_meta_nav .header_meta_nav--context[href]:hover, .header_meta_nav .header_meta_nav--context[href]:focus, .header_meta_nav .header_meta_nav--context[href].is-active, .header_meta_nav .header_meta_nav--item--link[href]:hover, .header_meta_nav .header_meta_nav--item--link[href]:focus, .header_meta_nav .header_meta_nav--item--link[href].is-active {
      color: #f6feff;
    
}

.header_meta_nav .header_meta_nav--context, .header_meta_nav .header_meta_nav--item--link {
   color: #f6feff;
}
.card--link{
     background-color: #6a59bf;  
}

.footer_row--label{
    color: #f6feff;
}

.footer_row--bold{
      color: #f6feff;
}

.js-languageselect {
     color: #795993;
}

.footer_contact--item--link{
         color: #fff;
}

.button--inner{
    color: #795993;
}

.footer_social--item--link ico-twitter link{
    filter: invert(1);
}

.header_main_nav_submenu--sublinks--list--link{
  color: #f6feff;
}

.header_main--inner js-link-indicator{
    background-color: #795993;
}


a.link.link-lightbox, a.link.link-arrow, a.link, button.link, .line_table a:not([class]) {
    text-decoration: none;
    color: #eafdff;
}

.header_meta{
    background-color: #31285d;
}

a.rich_list_item:before {
    content: '';
    border-bottom: 1px solid #44f4f4;
    border-top: 1px solid #44f4f4;
    background-color: #795993;
}

.teaser_basic--body:before {
    background-color: #44f4f4;
}

.header_main--inner {
    background-color: #6a58bf;
}

.header .header_main--section.header_main--section-border-right {
    border-right: 1px solid #e7feff;
}

*, *:before, *:after {
    color: #f6feff
}

.teaser_rich--image{
    filter: blur(0.5px) grayscale(50%);
}

.teaser_basic--image{
   filter: blur(0.5px) grayscale(50%);
}

a.link.link-arrow:before{
    color: #f6feff
}

.header_logo {
    background-color: #44f4f4;
}

.header_logo--img{
filter: hue-rotate(290deg);
}

.card--picto, .card--image{
    filter: hue-rotate(3000deg);
}

.header_main_nav--indicator-indicator {
    color: #44f4f4;
}

.footer_contact--item--link:hover {
background-color: #795993;
}

.cb_flowbox--trigger-btn {
    color: #f00;
    background-color: #44f4f4;
    border: 1px solid rgba(255, 255, 255, 0);
}

#pfde .nw_TitleBar, #pffr .nw_TitleBar {
    background: #44f4f4;
    color: #000000;
}

