style.css:
@-moz-document domain("justdancenow.com") {
@keyframes BeatCoach
{
  100%
  {
    opacity: 1;
    transform: scale(1.0, 1.0);
  }


  00%
  {  
  opacity: 1;
   transform: scale(1.1, 1.0);
  }
}
#beat .particle-system
{
  display: none;
    opacity: 0
}

@keyframes PictoFade
{
    0%
    {
        opacity: 0.5 !important
    }
    
    100%
    {
        opacity: 1 !important
    }
}
#beat
{
  width: 42.2%;
  height: 90% !important; 
  bottom: -86.5% !important;
    left: -48%;
  background-image: url(https://i.imgur.com/e8jRoOD.png);
  background-repeat: no-repeat !important;
  background-size: 100% 100% !important;
  background-position: bottom !important;
    opacity: 1 !important
}


html:not(.ipad) .hud.beat>#pictos>#beat
{
  -webkit-animation-name: BeatCoach !important;
  animation-name: BeatCoach !important;
}
@keyframes pictoOut
{
  0%
  {
    background-size: 100% 100%;
    filter: brightness(250%);
    opacity: 1;
  }


  5%
  {
    filter: brightness(100%);
  }


  100%
  {
    background-size: 100% 0%;
    opacity: 0;
    filter: brightness(100%);
  }
}
 #pictos {
    width: 20% !important;
}
#pictos.multi-coach {
    width: 20%!important;
}
.picto {
    left: -60%!important;
}
.multi-coach .picto {
    left: -70%!important;
}

@keyframes CoachBeat
{
    0%
    {
        top: 2%;
        transform: scale(0.97);
        -webkit-filter: brightness(120%);
        filter: brightness(120%);
    }
    
    100%
    {
        top: 0%;
        transform: scale(1.0);
        -webkit-filter: brightness(100%);
        filter: brightness(100%);
    }



  
}

#lyrics {
    font-family: Google Sans ;
    left: 5.2%;
    top:88.77%;
    font-size: 16%;
}

