html
{
  overflow-y: hidden;
}

html:not(weak-platform) #players .star::after
{
  background-image: url(https://dl.dropbox.com/s/hyw87vurd3a2dw0/Glowing.png?dl=0);
  transform: rotate(15deg)!important;
}

html:not(.weak-platform) #players.animate .star::after
{
  animation: NewStarOutline .85s ease-in-out .75s;
}
#racetrack .platform
{
  width: 120%;
}

#racetrack .star
{
  width: .55rem;
  height: .55rem;
}

#racetrack
{
  width: 8.5%;
  height: 56.2%;
  left: 1.5%;
}

@keyframes NewStarOutline
{
  0%
  {
    opacity: 1;
    transform: scale(1,1);
  }

  100%
  {
    opacity: 0;
    transform: scale(4,4);
  }
}
html:not(.weak-platform) #racetrack .star[data-visible=true]::after
{
  background-image: url(https://dl.dropbox.com/s/l6pwb495c5gxmnq/NewStar.png?dl=0);
  width: 200%;
  left: -50%;
  top: 20%;
}

#racetrack.animate .star::after
{
  -webkit-animation: newStarMG .88s;
  animation: newStarMG .88s;
}

@keyframes newStarMG
{
  0%
  {
    opacity: 1;
    transform: scaleX(2.2);
    -webkit-filter: brightness(100%);
  }

  70%
  {
    opacity: 0;
    transform: scaleX(4.4);
    -webkit-filter: brightness(150%);
  }
}

.hud.beat>#racetrack.animate .star:not(.no-beat)
{
  animation: NewStarFB .88s linear;

}

.star[data-visible=true].star2,.star[data-visible=true].star4
{
  animation: pulsebeat2 1s both infinite!important;
}

.star[data-visible=true].star1,.star[data-visible=true].star3,.star[data-visible=true].star5
{
  animation: pulsebeat 1s linear infinite!important;
}
.hud.beat #players .feedback-label
{
  background-repeat: no-repeat;
  background-image: url(https://i.imgur.com/hbcFwiq.png);
}

#players .feedback
{
  background-repeat: no-repeat;
  width: 100%;
  height: 100%;
  background-image: url(https://i.imgur.com/uf3LARU.png);
}

#players .feedlabel-good,
#players .feedlabel-perfect,
#players .feedlabel-yeah
{
  top: 20%;
  left: -10%;
  width: 120%;
  height: 120%;

}
#players .feedlabel-ok
{
  left: 4%;
  top: 50%;
}

#players .feedback-good
{
  width: 140%;
  height: 140%;
  left: -20%;
  top: 0%;
}

#players .feedback-ok
{
  top: -15%;
  left: 1%;
}

#players .feedback-perfect
{
  width: 140%;
  height: 140%;
  left: -20%;
}

#players .feedback-yeah
{
  width: 100%;
  height: 100%;
}
    /* ---------------- ROOM_INFO ---------------- */
    
.state-dance.buffering .fullscreen-spinner
{
  pointer-events: none;
}

.state-dance #room-info,
    .state-dance .toggle-fullscreen,
    .state-dance .toggle-settings
{
  opacity: .4!important;
  z-index: 9999999!important;
}

.state-dance #room-info .account-name,
    .state-dance #room-info .account-name .icon,
    .state-dance #room-info .room-number .icon,
    .state-dance .danceroom__qr-code-wrapper,
    .state-dance #just-dance-now .danceroom__label
{
  display: none;
}

.state-dance #room-info .room-number
{
  font-size: 0%;
  text-shadow: none;
  color: none;
}

.state-dance #room-info .room-number span.number
{
  margin-left: 0;
  margin-right: 0%;
}

.state-dance #room-info
{
  display: block;
  top: 0%;
  left: 0%;
  opacity: .0;
  width: 0%;
  text-align: none;
  z-index: 0;
}

#racetrack.animate .star::after
{
  -webkit-animation: newStarMG .88s;
  animation: newStarMG .88s;
}


.star[data-visible=true].star2,.star[data-visible=true].star4
{
  animation: pulsebeat2 1s both infinite!important;
}

.star[data-visible=true].star1,.star[data-visible=true].star3,.star[data-visible=true].star5
{
  animation: pulsebeat 1s linear infinite!important;
}

@keyframes pulsebeat2
{
  100%
  {
    transform: rotate(-15deg) scale(1.1);
  }

  0%,79%,80%
  {
    transform: rotate(-15deg) scale(1);
  }
}

@keyframes pulsebeat
{
  80%
  {
    transform: rotate(-15deg) scale(1.1);
  }

  0%,59%,60%
  {
    transform: rotate(-15deg) scale(1);
  }
}
}
