style.css:
@-moz-document domain("translate.google.com") {

.filled.gt-score-dot {
	background-color: #f6d876 !important;
}

.empty.gt-score-dot{
	background-color: #191a21 !important;
}

.sl-sugg .goog-inline-block.jfk-button-checked, .tl-sugg .goog-inline-block.jfk-button-checked {
		
	color: #6272a4 !important;
}

.sl-sugg .goog-inline-block.jfk-button-checked, .tl-sugg .goog-inline-block.jfk-button-checked,
.goog-inline-block.jfk-button-checked, .tl-sugg .goog-inline-block.jfk-button-checked,
.gt-lc.gt-lc-mobile .gt-cd-pos {
	color: #f6d876;
}

.goog-inline-block.jfk-button-checked::after{
	border-color: #6272a4;
}

.gt-is-tr,
.ft-icon-txt,
.show-as-one-card span.gt-cc-tc .gt-ct-translit,
.gt-card-ttl-txt,
.gt-def-row {
	color: #6979ad !important;
}

::selection {
	background: #4d5b9a;
}

.gt-cd-tl,
.sl-sugg .goog-inline-block.jfk-button, .tl-sugg .goog-inline-block.jfk-button,
.result-transliteration-container .transliteration-content, .result-transliteration-container .truncate-link,
.source-transliteration-container .transliteration-content,
.gt-lc.gt-lc-mobile .gt-def-example {
	color: #51557d;
}

.trans-edit, .trans-target-highlight {
	background-color: #21222c;
}

.gt-lc.gt-lc-mobile .gt-def-num {
	border-color: #191a21;
	color: #a1b3c9;
	border-width: 2px;
}

.trans-verified-button {
	filter: invert(100%) sepia(1) saturate(400%) hue-rotate(-8deg);	
}

.gt-cd-pos-pop .help-icon-container .help-icon,
.back-image-black,
.sl-more, .tl-more {
	filter: sepia(1) hue-rotate(182deg) brightness(72%);	
}

.gt-is-mobile .gt-is-ca,
.jfk-button-img,
.src-tts .jfk-button-img, .ttsbutton-res .jfk-button-img, .clear .jfk-button-img,
.gt-cc-t .gt-cc-tc .gt-ct-tts.trans-listen-button .jfk-button-img {
	filter: invert(52%) sepia(1) hue-rotate(182deg);	
}

.results-container:not(.empty) .placeholder, .result-dict-wrapper .translation, .contribute-target, #st-buttons .jfk-button,
.gb_ja {
	color: #ffffff;
}

#gb {
	border-color:#191a21
}

.header-button {
	border-color:#191a21 !important;
}

.ls-wrap .sl-sugg, .goog-inline-block.jfk-button {
	transition: .14s color;
}

.language_list_item_wrapper.item-emphasized:not(.item-selected) .language_list_item_icon {
	filter: invert(70%) sepia() hue-rotate(190deg);
}

.frame a:link,
.ls-wrap .sl-sugg, .goog-inline-block.jfk-button:hover {
	cursor: pointer;
	color: #f6d876 !important;
}

.frame a:link.tlid-send-feedback-link {
	color: #525c7a !important;
}

.language_list_item_wrapper.item-selected .language_list_item_icon,
.gt-spell-icon {
	filter: hue-rotate(194deg) brightness(138%);	
}

.ls-wrap .tl-sugg .goog-inline-block.jfk-button:focus,
.ls-wrap .sl-sugg .goog-inline-block.jfk-button:focus {
	background-color: #525c7a;
}

.gt-cc-t .gt-ct-text,
.results-container, .result, .gt-edit, .contribute-target {
	color: #ffffff;
	background-color:#252632 !important;
}

.frame::before,
body {
	color: #ffffff;
	background-color:#252632 !important;
}

.gt-is-itm-hover {
	background-color: #252632;
	cursor: pointer;
}

.gt-is-mobile,
.gt-is-mobile .gt-is-ld,
.language-list-toolbar,
.gt-lc.gt-lc-mobile.show-as-one-card .gt-cc,
.gt-lc.show-as-one-card .gt-cc-r,
.gt-lc.gt-lc-mobile.show-as-one-card,
.gt-cc-t {
	border-color: #191a21;	
}

.ita-kd-menuitem {
	color: #ffffff;
}

.gt-is-mobile .gt-is-sg {
	color: #f6d876 !important;
}

.ita-kd-menuitem-hover {
	color: #f6d876 !important;
	background-color: #252632 !important;
	cursor: pointer;
}

.language_list_item_wrapper:hover, .language_list_item_wrapper:focus {
	background-color: #20212c !important;
}

.language_list_item_language_name {
	color: #ffffff;
}

.language_list_item_wrapper.item-selected .language_list_item {
	color: #f6d876 !important;
}

.language_list_item_wrapper.item-selected {
	background-color: #252632;
}

.language_list_section,
.language_list_item_wrapper,
.language_list_sl_list, .language_list_tl_list,
.language-list {
	background-color:#282a36;
}

#sl_list-search-box, #tl_list-search-box,
.language-list-search-box {
	background-color:#252632;
	color: #ffffff;
}

.detect_language,
::placeholder {
	color: #a6b6f1;
}

.moremenu,
.ita-kd-dropdown-menu {
	border:none;
	border-radius: .4rem;
	background-color:#16171f;
	box-shadow: 0 1px 8px #0f141d;
	padding-bottom: 1rem;
	color: #ffffff;
}

.homepage.translate-text .input-button.text-icon, .homepage.translate-docs .input-button.documents-icon {
	background-color:#282a36 !important;
}

.gt-baf-word-selected,
.gt-lc-mobile .gt-baf-word-clickable:hover {
	background: transparent !important;
}

.gt-lc-mobile .gt-baf-word-selected .gt-baf-word-clickable,
.gt-lc.gt-lc-mobile .gt-cd-cl, .gt-lc.gt-lc-mobile .gt-cd-ncl {
	background: transparent;
	border-color: #1b1b23;
	color: #6979ad;
}

.gt-lc-mobile .gt-baf-hl,
.gt-lc-mobile .gt-baf-word-clickable:hover {
	background-color: #21222c;	
	color: #ffffff;
}

path[d="M6 17h3l2-4V7H5v6h3zm8 0h3l2-4V7h-6v6h3z"] {
	fill: #f6d876;	
}

.gt-is-mobile,
.st-stp1-epu-text,
.gt-related-suggest-message,
.ft-icon-oval,
.gt-spell-correct-message,
.gt-lc.gt-lc-mobile.show-as-one-card,
.gt-cc-t,
.swap-wrap, .swap.jfk-button,
.gt-lc.gt-lc-mobile .gt-cc-r-i,
.gt-lc.gt-lc-mobile .gt-cd,
.orig,
.source-input,
.source-wrap,
.ls-wrap,
*[style*="background-color:rgba(255,255,255"], 
*[style*="background-color:rgb(255,255,255"], 
*[style*="background-color:#ffffff"] {
	background-color:#282a36 !important;
	color: #ffffff;
	border-color:#191a21
}

.gt-cd-pos-pop,
.gt-lc-mobile div.gt-baf-translations,
.gt-baf-word-clickable,
.gt-lc.gt-lc-mobile .cd-expand-label,
.gt-lc.gt-lc-mobile .gt-ex-text,
.gt-lc.gt-lc-mobile .gt-def-synonym-title,
.st-stp1-epu-text {
	color: #6979ad;
}

.ls-wrap .sugg-fade {
	background: -webkit-linear-gradient(left,#282a3600,#282a36ff);
}
}
