style.css:
@-moz-document url-prefix("https://www.brick-hill.com/") {
/* Theme made by rowbot :) */
/* 484 */
/* SGD BKNBJ HR SHBJHMF.
SGD SHLD HR BNLHMF SN ZM DMC.
SGD SHLD'R DMC. */

body {
    background-color: #00062c;;
    color: #ccc;
}

/* font */
.agray-text {
    color: #ccc;
}

.ablack-text {
    color: #eee
}

.darkest-gray-text {
    color: #ccc
}

.dark-gray-text {
    color: #ccc
}


.shop-categories .category {
    color: #c1c1c1;
}

.shop-categories .category.active,
.shop-categories .category a:hover {
    color: #FF6100;
}

.sellers .price .serial {
    color: #333
}

.card .item-creator {
    color: #ccc
}

.card .creator {
    color: #bbb;
}

.card .item-card-details .price,
.card .item-content .price {
    color: #aaa;
}


.card .name {
    color: #ccc;
}

.label.dark {
    color: #eee
}

.label {
    color: #ccc
}

.smedium-text.bold {
    color: #eee
}

.board-info a,
.stat .title {
    color: #ccc
}

.trade-ev {
    font-weight: 600;
    color: #ccc;
    font-size: 18px;
}

.trade-item .trade-name {
    color: #ccc;
}

textarea {
    background-color: #3d3d3d
}

.status-dot {
    background-color: #b71717;
}

.clan-img-holder img {
    background-color: #333;
}



/* navBar */
nav div.primary {
    background-color: #3D3D3D;
    color: #fff;
}

nav div.secondary {
    background-color: #FF6100;
    color: #fff;
}

nav .info {
    background-color: #FF6100;
}

/* Cards Color */
.card .content {
    background-color: #555;
    color: #e4e4e4
}


.card .top:not(.absolute) {
    color: #999;
    background-color: #3d3d3d;
    border-color: #999;
}

.card .top:not(.absolute).blue {
    color: #ff6100;
    background-color: #3d3d3d;
    border-color: #FF6100
}

.card .top:not(.absolute).green {
    color: #03c303;
    background-color: #3d3d3d;
    border-color: #03c303
}

.card .top:not(.absolute).red {
    color: #FF6100;
    background-color: #3d3d3d;
    border-color: #FF6100
}

.card .top:not(.absolute).orange {
    color: #ba3ff2;
    background-color: #3d3d3d;
    border-color: #ba3ff2
}

.modal .modal-content {
    background-color: #1d1d1d;
}

.card .footer {
    border-top: 1px solid #ccc;
    padding: 10px;
    background-color: #3d3d3d;
}

.card .item-card-details,
.card .item-content {
    background-color: #3d3d3d;
    border-top: 1px solid #3d3d3d;
}

.item-card-container {
    background-color: #3d3d3d;
    -webkit-box-shadow: 0 2px 5px rgba(0, 0, 0, .2);
    box-shadow: 0 2px 5px rgba(0, 0, 0, .2);
}

.trade-serial {
    background-color: #111;
    color: #ccc;
}

.item-card-image {
    border-bottom: 1px solid #555;
}

.box.item-img.owns {
    border: 5px solid #7C10AD;
}

.owned-check-tri.owns {
    border-top: 50px solid #7C10AD;
}

.profile-card.crate img {
    border: 1px solid #999;
    background-color: #555;
}


.avatar-card.crate img {
    height: 95px;
    background-color: #555;
}

.box.shaded {
    background-color: #3d3d3d;
}

.toggle-user-desc:not(.open):after {
    background-color: #555;
    color: grey;
}

.read-more-desc {
    background-color: #555;
}

.profile-card:hover {
    background-color: #3d3d3d
}


.outfit-overlay {
    background-color: #22222278;
}


/* Forums */
/*thread label*/
.thread-label.blue {
    background-color: #FF6100
}

.thread-label.green {
    background-color: #03c303
}

.thread-label.red {
    background-color: #FF6100
}

.thread-label.orange {
    background-color: #7C10AD
}

.forum-links a:hover {
    color: #af4eff;
}

.hover-card:hover {
    background-color: #464646;
}

.hover-card.viewed {
    background-color: #3D3D3D;
}

.thread-row {
    border-bottom: 2px solid #3d3d3d;
}

.thread {
    border-bottom: 1px solid #3d3d3d;
}

.blockquote.red,
blockquote.red {
    border-color: #FF6100
}

.blockquote.green,
blockquote.green {
    border-color: #03c303
}

.blockquote.blue,
blockquote.blue {
    border-color: #FF6100
}

.blockquote.orange,
blockquote.orange {
    border-color: #7C10AD
}

.report:hover {
    color: #7C10AD;
    cursor: pointer
}

.forum-reply:hover {
    color: #FF6100
}

.forum-quote:hover {
    color: #03c303
}
.forum-links a {
    color: #b141e3;
}

.forum-tag {
    background: #3d3d3d;
    color: #fff;
}

.forum-tag:after {
    border-right-color: #3d3d3d;
}

.forum-bar a {
    color: #ccc;
}

.forum-bar .fa-angle-double-right {
    color: #ff6100;
}

/* Buttons */

.button, button {
    color: #cbcbcb;
    background-color: #3d3d3d;
    border-color: #6f6f74;
}

.button:not(.plain).blue, button:not(.plain).blue {
    color: #fff;
    background-color: #d25000;
    text-transform: uppercase;
    border-color: #ff6100
}

.button:not(.plain).green, button:not(.plain).green {
    color: #fff;
    background-color: #03c303;
    text-transform: uppercase;
    border-color: #0f0
}

.button:not(.plain).red, button:not(.plain).red {
    color: #fff;
    background-color: #d22000;
    text-transform: uppercase;
    border-color: #ff3700
}

.button:not(.plain).orange, button:not(.plain).orange {
    color: #fff;
    background-color: #7C10AD;
    text-transform: uppercase;
    border-color: #b000ff
}

.input-group .input-button {
    border-left: 1px solid #999;
    color: #fff;
    background-color: #ff6100;
}


.button.tab-button.transparent,
button.tab-button.transparent {
    color: #fff
}

.pages .page.active {
    color: #ff6100
}

.crate-types {
    border-right: 2px solid #999;
}

.crate-types .active,
.crate-types :hover {
    background-color: #3d3d3d;
    border-left: 4px solid #ff6100
}

.login-button {
    border: 1px solid #ff6100;
    color: #fff;
}

.login-button:hover {
    background-color: #ff6100
}

.register-button {
    border: 1px solid #7C10AD;
}

.register-button:hover {
    background-color: #7C10AD
}

.dropdown-content.logout-dropdown li * {
    color: #fff;
    background-color: #222;
}

.dropdown-content.logout-dropdown .dropdown-arrow {
    border-bottom: 7px solid #222;
}

.dropdown-content li * {
    color: #ccc;
    background-color: #222;
}

.dropdown-content .dropdown-arrow {
    border-bottom: 7px solid #222;
}


.dropdown-content li :hover {
    background-color: #636363
}

.friend-card:hover {
    background-color: #3d3d3d
}

.profile-card:hover {
    background-color: #3d3d3d
}

.fa-caret-square-down:before {
    color: #ccc;
}

/* tabs */
.tabs .tab {
    color: #999;
    background-color: #3d3d3d;
    border-color: #999;
}

.tabs .tab-body {
    background-color: #555;
    border-color: #999;
    border-top: 0px solid;
}

.tabs .tab.active {
    background-color: #555;
    border-color: #999;
    border-bottom: 1px solid #555;

}

.button.tab-button.transparent:hover,
button.tab-button.transparent:hover {
    background-color: #7c10ad7d
}

.line,
hr {
    border-top: 1px solid #3d3d3d;
    border-bottom: 1px solid #3d3d3d;
}


.select,
select {
    border-radius: 3px;
    font-weight: 600;
    padding: 8px;
    color: #ccc;
    background: #3d3d3d
}

input.rigid,
textarea.rigid {
    background-color: #3d3d3d;
    color: #eee;
    border: 1px solid #999;
}

input,
textarea {
    background-color: #3d3d3d;
    border: 1px solid #999;
    color: #eee;
}

/* Membership */
.mem-cols ul li b {
    color: #fc9700;
}
/* minti */
.mint-gradient {
    background-image: linear-gradient(270deg,#0088E6,#3d3d3d);
}

/* ace */
.ace-gradient {
    background-image: linear-gradient(270deg,#d40005,#3d3d3d);
}

/* royala */
.royal-gradient {
    background-image: linear-gradient(270deg,#E99E00,#3d3d3d);
}
}

@-moz-document url-prefix("https://blog.brick-hill.com/") {
body {
    background-color: #070F1C;
    color: #ccc;
}

.site-wrapper {
    background: #333;
}

.post-title a {
    color: #eee;
}

.post-template .post-title, .page-template .post-title {
    color: #eee;
}

a {
    color: #FF6100;
}

h2 {
    color: #eee;
}

.post-footer h4 a {
    color: #ccc;
    text-decoration: none;
}

blockquote {
    border-left: #FF6100 .4em solid;
}

}
