style.css:
@-moz-document domain("htmanga2.top"), domain("ssmh01.top"), domain("hentaicomic.org"), regexp("^https?:\/\/(?:www\.)?wnacg.*\.(?:com|net|org)\/.*$") {
body>div[align="center"][style="margin:20px;"],
	iframe,
	#tuzaoblock,
	#bread>a,
	script+a,
	.result,
	.logo img,
	.footer,
	.comment_wrap,
	.grid .sel_on .pic_ctl,
	.grid_2 .cw,
	#img_load~div,
	.title_sort>script+div,
	.dlh>script+div,
	img[src="/themes/weitu/images/bg/shoucang.jpg"] {
		display: none !important;
	}
	
	::-webkit-scrollbar,
	::-webkit-scrollbar-track,
	::-webkit-scrollbar-corner {
		background: none;
	}
	
	::-webkit-scrollbar {
		max-width: 1mm;
		max-height: 1mm;
	}
	
	::-webkit-scrollbar-thumb {
		background: rgba(200, 80, 0, .7);
		border-radius: 1mm;
		box-shadow: inset 0 0 0 1px #aaa;
	}
	
	html,
	body {
		height: auto;
		min-height: 100%;
		font: 400 12pt sans-serif;
	}
	
	body {
		padding: 0 0 16mm;
		margin: 0 auto;
		color: #444;
		background: #999 linear-gradient(90deg,
				#888 10%,
				#aaa 40%,
				#aaa 60%,
				#888 90%);
	}
	
	html,
	body {
		font-size: calc(8pt + .4vw)
	}
	
	.nav li a {
		font-size: 1.1rem
	}
	
	img {
		object-fit: contain
	}
	
	#header .logo:before {
		display: inline-block;
		content: "紳士漫畫";
		color: #ddd;
		font: 600 20pt Helvetica;
		padding: 0 3mm;
	}
	
	#bread,
	#bodywrap,
	.header,
	.title_sort,
	.bodywrap {
		width: calc(100% - 8mm);
		min-width: 680px;
		max-width: calc(100vw - 20mm);
		background: none;
	}
	
	.header.cc {
		position: relative;
		display: flex;
		z-index: 20000;
	}
	
	.header .logo,
	.header #settings_person {
		flex: 0 0 auto;
	}
	
	.header .nav {
		flex: 0 1 calc(30em + 16%);
		margin: 0 auto;
		transition: 0s;
	}
	
	#album_tabs {
		display: inline-flex;
		width: 100%;
		text-align: center;
		margin: 0 auto;
	}
	
	.nav li {
		flex: 1 1 auto;
	}
	
	.nav li a {
		width: 100%;
	}
	
	.search {
		height: 30px;
		background: #fff;
		border-radius: 1em;
	}
	
	.search input {
		font: 300 1rem sans-serif;
		line-height: 30px;
		margin: 0 0 0 .7em;
	}
	
	.search button {
		width: 30px;
		height: 30px;
		padding: 0;
	}
	
	.search button::before {
		display: inline-block;
		width: 28px;
		height: 28px;
		line-height: 28px;
		font-size: 25px;
		content: "⚲";
		color: #ccc;
		background: #666;
		border-radius: 1em;
		transform: rotate(-33deg);
		transition: .2s;
	}
	
	.search button:hover::before {
		color: #fff;
		background: #333;
		box-shadow: 0 0 0 2px #f70;
	}
	
	#photo_body .photo {
		height: 92vh;
		padding: 0;
		margin: 0 auto 3mm;
		background: none;
		border: none;
	}
	
	#img_list img {
		width: auto !important;
		max-width: 100%;
		height: calc(100vh - 68px);
	}
	
	#img_list span {
		color: #fff !important;
		text-shadow: 0 0 1mm #333;
	}
	
	.userwrap {
		border: none;
		margin: 0;
	}
	
	.userwrap .asTB {
		width: 100%;
		height: auto;
		display: inline-block;
		background: #ced0d8;
		max-height: 220px;
		overflow: hidden;
		border-radius: 2mm;
		box-shadow: 0 0 2mm #666;
		transition: .3s;
	}
	
	.userwrap .asTB:hover {
		max-height: 80vh;
		overflow: scroll;
		transition: .3s .3s;
	}
	
	.userwrap .asTB>* {
		padding: 8px
	}
	
	.uwthumb {
		display: block;
		float: left;
		width: 300px;
		padding: 0;
	}
	
	.uwthumb img {
		float: left;
		margin: 0;
		width: auto;
		max-width: 160px;
		height: 200px;
	}
	
	.uwconn {
		display: block;
		float: left;
		width: calc(100% - 628px) !important
	}
	
	.uwuinfo {
		display: block;
		float: right;
		width: 280px;
		height: 100%;
	}
	
	.selectlist {
		border-radius: 5mm;
	}
	
	.tagshow,
	.btnNarrow,
	.btntuzao {
		display: inline-block;
		line-height: 1.8;
		padding: 0 2.5mm;
		border-radius: 1em;
	}
	
	#bread {
		padding: .2em 0;
		margin-bottom: 3px;
		line-height: 1.5em;
	}
	
	#bread .bread {
		background-position: 0 35%;
	}
	
	.selectlist li:first-child a {
		padding-left: 1em;
	}
	
	.selectlist li:last-child a {
		padding-right: 1em;
	}
	
	.title_h2 em {
		color: #058;
	}
	
	.grid ul {
		font-size: 0;
	}
	
	.grid li {
		font-size: 11pt
	}
	
	.gallary_wrap .cc {
		display: flex;
		flex-wrap: wrap;
	}
	
	.gallary_wrap li.li {
		vertical-align: top;
		flex: 1 1 15em;
		max-width: 24em;
		height: auto;
		padding: 2mm 2mm 0;
		margin: 0 1mm 2mm;
		border-radius: 2mm;
		float: none;
		box-shadow: 0 0 1mm #bbb;
		background: rgba(255, 255, 255, .4);
		transition: .3s;
	}
	
	.gallary_wrap li.li:hover {
		box-shadow: 0 0 2mm #666;
		background: rgba(255, 255, 255, .8);
		transition: .1s;
	}
	
	.grid .pic_box {
		position: static;
		margin: 0;
		overflow: hidden;
		border-radius: 1mm;
	}
	
	.grid .pic_box[class*="cate-"]::before {
		position: absolute;
		right: unset;
		top: unset;
		margin: 22em 2mm -2em;
		background: rgba(30, 0, 80, .3);
		text-shadow: 0 0 1mm #000,
			0 0 1mm #000;
	}
	
	.grid li:hover .pic_box {
		box-shadow: 0 0 0 1px #666;
	}
	
	.grid .pic_box img {
		width: 100%;
		height: 100%;
		min-height: 16em;
		max-height: 20em;
		background: #666;
		object-fit: cover;
		object-position: 50% 20%;
		padding: 0;
		margin: 0;
		border: none;
		border-radius: 1mm;
		box-shadow: none;
		transition: .2s;
	}
	
	.grid li.li:hover .pic_box img {
		transform: scale(107%);
	}
	
	.grid .info {
		margin-top: 1mm
	}
	
	.grid .info .title {
		width: auto;
		max-height: none;
	}
	
	.grid .info .title a,
	.grid .infot .title a {
		width: auto;
		height: 3em;
		max-height: 3em;
		text-align: center;
		color: #333;
		display: -webkit-box;
		-webkit-line-clamp: 2;
		-webkit-box-orient: vertical;
		overflow: hidden;
	}
	
	.grid .infot .tags_2 {
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
	}
	
	.grid .info .title a:hover,
	.grid .infot .title a:hover {
		text-decoration: none;
		color: #626;
	}
	
	.grid .info .info_col {
		min-width: 18em;
		text-align: right;
		color: #447;
		font-size: 10pt;
		line-height: 1.4em;
		padding: 3px 0 2mm;
	}
	
	.drop_dwon {
		left: 0;
		right: 0;
		border-radius: 0 0 2mm 2mm;
		box-shadow: 0 0 5px #888;
		transition: .5s;
	}
	
	li:hover>.drop_dwon {
		max-height: 80vh;
		overflow: auto;
	}
	
	#drop a {
		width: 100%;
	}
	
	#header {
		width: 100%;
		padding: 0;
		z-index: 1000;
		box-shadow: 0 0 0 1px #ccc, 0 0 2mm #000;
		opacity: .2;
		backdrop-filter: blur(2mm);
		-webkit-backdrop-filter: blur(2mm);
		transition: opacity .6s
	}
	
	.newpagewrap,
	.bot_toolbar {
		position: fixed;
		left: 0;
		bottom: 0;
		width: 100vw;
		padding: 0;
		background: rgba(0, 0, 0, .5);
		text-align: center;
		box-shadow: 0 0 0 1px #ccc, 0 0 2mm #000;
		opacity: .2;
		backdrop-filter: blur(3mm);
		-webkit-backdrop-filter: blur(3mm);
		transition: opacity .6s
	}
	
	#header:hover,
	.newpagewrap:hover,
	.bot_toolbar:hover {
		opacity: 1;
		transition: opacity .2s;
	}
	
	.newpageblock,
	.paginator.f_left {
		position: relative;
		display: inline-block;
		float: none;
		text-align: center;
		width: 75%;
		min-width: 55em;
		margin: .5em auto;
	}
	
	.paginator.f_left .next {
		position: absolute;
		right: 0;
	}
	
	.paginator.f_left .prev {
		position: absolute;
		left: 0;
	}
	
	.paginator .thispage,
	.paginator a {
		display: inline-block;
		float: none;
		min-width: 1em;
		line-height: 1;
		border-radius: 2mm;
		margin-right: 1mm;
		padding: .4rem;
	}
	
	.paginator .prev {
		float: left;
		background: none;
	}
	
	.paginator .next {
		float: right;
		background: none;
	}
	
	.paginator .prev a,
	.paginator .next a {
		font-size: 0;
	}
	
	.paginator .prev a::before {
		font-size: 1rem;
		content: "⬅️";
	}
	
	.paginator .prev a::after {
		font-size: 1rem;
		content: "前一頁";
		margin: 0 .4em;
	}
	
	.paginator .next a::before {
		font-size: 1rem;
		content: "下一頁";
		margin: 0 .4em;
	}
	
	.paginator .next a::after {
		font-size: 1rem;
		content: "➡️";
	}
	
	.paginator {
		font-family: Sans-Serif;
		height: auto;
		line-height: 1;
	}
	
	.paginator a:hover {
		padding: .4rem;
		color: #830;
		background: #ede;
	}
	
	.newpagelabel {
		position: absolute;
		right: -100px;
		font-size: 18pt;
		color: #fff;
	}
	
	.newpage {
		position: relative;
		left: 0;
		top: 0;
	}
	
	.adbox {
		position: relative;
		width: 70vw;
		min-width: 300pt;
		max-width: 500pt;
		height: auto;
		padding: 6mm;
		margin: 30vh auto 10mm;
		background: rgba(255, 252, 248, .7);
		box-shadow: inset 0 0 0 1px #eee,
			0 2mm 12mm rgba(0, 0, 0, .7);
		border-radius: 5mm;
		z-index: 2;
		backdrop-filter: contrast(.5) blur(5mm);
		-webkit-backdrop-filter: contrast(.5) blur(5mm);
	}
	
	.btn {
		text-align: center;
		width: auto !important;
		min-width: 5em;
		height: auto;
		padding: .2em .5em;
		font: normal 13pt sans-serif;
		border-radius: 2mm;
		box-shadow: none;
	}
	
	.btn:hover {
		background: linear-gradient(0deg, #6b6 0%, #7c7 100%) !important;
		box-shadow: 0 0 1mm #aaa;
	}
	
	.btn:active {
		background: linear-gradient(0deg, #494 0, #5a5 100%) !important;
		box-shadow: inset 0 0 1mm #777;
	}
	
	.download_panel,
	.download_btn {
		width: auto;
		height: auto;
		padding: 0;
		margin: 0;
		background: transparent;
		box-shadow: none;
	}
	
	.download_filename {
		font-size: 1.2rem;
		white-space: normal;
		padding: 0;
	}
	
	.download_btn {
		display: flex;
	}
	
	.down_btn {
		flex: 1 1 auto;
		float: none;
		font-size: 1.2rem;
		line-height: 24mm;
		margin: 10mm 1mm 1mm;
		border-radius: 2mm;
		background: rgba(20, 80, 120, .7);
		transition: .2s;
	}
	
	.down_btn:hover {
		color: #ff2;
		background: rgba(20, 80, 120, .8) !important;
		box-shadow: 0 1px 4px rgba(0, 0, 0, .8),
			inset 0 0 0 1px #ccc;
	}
	
	.down_btn:active {
		box-shadow: inset 0 1px 4px rgba(0, 0, 0, .8),
			0 0 0 1px #eee;
	}
	
	.demo-error,
	.gallary_wrap>.link>.container {
		width: auto;
		float: none;
	}
	
	.gallary_wrap li.list {
		display: inline-block;
		vertical-align: top;
		float: none;
		background: rgba(255, 255, 255, .2);
		height: auto;
		padding: 2mm;
		margin: 1mm;
	}
	
	.grid .pic_boxm,
	.grid .infot {
		float: none;
		width: auto;
	}
	
	.grid .pic_boxm {
		text-align: center;
	}
	
	#img_load {
		position: fixed;
		left: 3%;
		bottom: 3mm;
		padding: 3mm;
		border-radius: 3mm;
		background: rgba(255, 255, 255, .6);
	}
	
	#meiu_float_box {
		padding: 1em;
	}
	
	@media screen and (min-width:0) {
		.grid .info .title a {
			font-size: 10pt;
		}
	}
	
	@media screen and (min-width:700pt) {
		.grid .info .title a {
			font-size: 11pt;
		}
	}
	
	@media screen and (min-width:950pt) {
		.grid .info .title a {
			font-size: 12pt;
		}
	}
	
	@media screen and (min-width:1200pt) {
		.grid .info .title a {
			font-size: 13pt;
		}
	}
	
	@media screen and (min-width:1800pt) {
		.grid .info .title a {
			font-size: 13pt;
		}
	}
	
	@media screen and (min-width:133vh) {
		#img_list {
			max-width: min(150vh, calc(100vw - 15mm));
			text-align: center;
			padding: 2mm 0;
			margin: -7mm auto -10mm;
			direction: rtl;
			border-radius: 5mm;
			box-shadow: 0 0 0 1px #a73;
		}
	
		#img_list:hover {
			box-shadow: inset 0 0 8mm #b84;
		}
	
		#img_list>div {
			display: inline-block;
		}
	
		#img_list:hover>div:first-of-type {
			display: block
		}
	
		#img_list:before {
			position: fixed;
			top: 20%;
			right: max(calc(50vw - 73vh), 5mm);
			direction: ltr;
			font-size: 0;
			background: rgba(0, 0, 0, .3);
			box-shadow: 0 0 1mm #eee;
			padding: 5px;
			border-radius: 5px;
			z-index: 2;
		}
	
		#img_list:before {
			opacity: .2;
			animation: pulse1 2s 1;
			content: url('data:image/png;base64,');
		}
	
		#img_list:hover:before {
			opacity: .2;
			animation: pulse2 2s 1;
			content: url('data:image/png;base64,');
		}
	}
	
	@keyframes pulse1 {
		0% {
			opacity: .2;
		}
	
		15% {
			opacity: .9;
		}
	
		100% {
			opacity: .2;
		}
	}
	
	@keyframes pulse2 {
		0% {
			opacity: .2;
		}
	
		15% {
			opacity: .9;
		}
	
		100% {
			opacity: .2;
		}
	}
}
