style.css:
@-moz-document regexp("https:\\/\\/(www\\.)?wykop\\.pl(\\.[a-z]{2})?\\/(?!(ramka|feedback)).*") {
/*
* Name:			Ciemny wykop dla niezalogowanych
* Homepage:		https://userstyles.org/styles/176253/
* Author:		Atx
* Version:		v1.0
* Date: 		13.10.2019
*/

#footer, #nav
{
	background-color: #161616 !important;
}

body, #site, tbody > tr:nth-child(2n)
{
	background-color: #1C1C1C !important;
}
  
.r-block, .grid-right > .r-block, .grid-right > div, .comments-stream, .article, .rbl-block, .comments-stream > li, .media-content .description, tbody > tr:nth-child(2n):hover, .dropdown > div
{
	background-color: #2C2C2C !important;
}

thead > tr > th
{
	background: #1C1C1C !important /*wykop cos nie ogarnia tabeli*/
}

.list > tbody > tr > th
{
	background: #3C3C3C !important /*wykop cos nie ogarnia tabeli*/
}

.tag.create:hover, .showSpoiler, .show-more, ul.related li > div span.data > span.cover, .button, button, input.button, input[type="submit"], .drop-cal > div ul, .summary, input[type="text"].create:hover, input[type="text"].create:focus, .tagChartTooTip, .newregister .form-marked, .newregister-switch-wrapper > div:last-child, tbody > tr:nth-child(2n+1), .form-big > div:first-child, .newregister-drop li.mark-bg.text-center
{
	background-color: #3C3C3C !important;
}

.drop-cal > div ul li a:hover, tbody > tr:nth-child(2n+1):hover, .unmarked-fb-button:hover, .unmarked-fb-button:focus, .unmarked-button:hover, .unmarked-button:focus
{
	background-color: #4C4C4C !important;
}
.mark-bg
{
	background: #444 !important;
}
  
div.m-set-fullwidth, .vC > b:nth-child(2) > span
{
	color: #B3B3B3 !important;
}

.article > div p.text a, .nav > ul li a, .entry.iC, .text, .space.information.bdivider, a.button, ul.sub-menu.inline-list.clear-top li, #owncontent > p, .description > .fright.small, label.inline, .vC[data-vc="0"], .r-block.search li a, h3, h4, h6, blockquote p.description, .media-header p.description, .tagChartTooTip > div, .newregister input[type="email"], .newregister input[type="text"], .newregister input[type="password"], .rbl-block.space > .static-text-wraper > .static-content-wraper, #advancedSearchDetails > ul > li, #advancedSearchDetails > p, .grid.m-reset-float, .profilesRankTable.list, .newregister-drop, .drop-cal > div ul li a
{
	color: #CCC !important;
}

.article > div p.text a:hover, .nav > ul li a:hover, .nav .info, .lcontrast .actions a.affect, .lcontrast a.affect, .lcontrast:hover .actions a.affect, .lcontrast:hover a.affect, .dark, body a#register.link:hover, .showlinks:hover a, body a:hover, a.button:hover, .slide.title > div > a, .number-small, .fa.fa-caret-left,  .r-block.search li a.active, .r-block.search li .active i.fa-check-square, .author.ellipsis > a[href="https://www.wykop.pl/ludzie/wykop/"] > b, .newregister .register-switch
{
	color: #FAFAFA !important;
}

.type-warning.space
{
	color: #0A0A0A !important;
}

.tag, #div:not(.voters-list) a
{
	color: #55B5DB !important;
}

.comments-stream > li, .article > div p.text a:hover, .rbl-block, .author, .author.hlight, .r-block, .grid-right > .r-block, .grid-right > div, .menu-list li, .tag.create:hover, h4, .tdivider, .nav, ul.related li > div div.content, .dropdown > div, .bdivider, .media-content .description, .button, button, input.button, input[type="submit"], .sub-menu, .drop-cal > div ul, #surveyBox ul li > div:last-child, #surveyBox fieldset, .summary, .touch-content .width-one-third, .comments-stream.touch-content, .twitter-cover, .w-item.mini span.image, .tagChartTooTip, .newregister-switch-wrapper > div:last-child, .newregister input[type="email"], .newregister input[type="text"], .newregister input[type="password"], .newregister .form-marked, .static-menu-wraper li, .static-text-wraper header, th, td, .form-big > div:first-child, .form-big, textarea, input[type="text"], input[type="password"], input[type="date"], input[type="number"], .apisandbox-menu input, .static-text h2
{
	border-color: #4C4C4C !important;
}

.newregister .newregister-header a.active
{
	border-color: #6C6C6C !important;
}

.newregister-drop li.mark-bg.text-center::after
{
	border-bottom-color: #3C3C3C !important;
}

.nav li.active a, .newregister .newregister-header a
{
	border-color: #CCC !important;
}

.diggbox span:first-child, .rdiggbox
{
	filter: invert(100%) hue-rotate(180deg) !important;
}

body.screening .wrapper
{
	max-width: 1180px !important;
}
}
