style.css:
@-moz-document domain("buymeacoffee.com") {
/*Site*/

body, html,

.dashboard-bg,

.sticky-toolbar.home-tab-web,

.creator-pg-bg-web,

.pe-menubar,
.post-single-page
{
  background: #3d3d3f;
}

.custom-color-page .curved-label.followed-user
{
  background: #3d3d3f !important;
}

.inline-btn-md,
.md-btn-w-l,

.modal-content,

.bmc-dropdown-style-menu,
.bmc-dropdown-with-pop:after,

.emojionearea .emojionearea-picker,
.emojionearea .emojionearea-picker .emojionearea-scroll-area .emojionearea-category-title,

.sm-bmc-btn,

.bg-dark-op-l, .bmc-btn-grey, .inactive-btn, .inactive-btn:hover, .bmc-text-field:hover, .bmc-ex-editor .note-editable:hover,

.bg-light,

.publish-section
{
    background: #363639;
}

holdinput,

.bmc-header,
.bmc-header .dropdown-menu,

.bg-fff,

.creator-footer,

.emojionearea .emojionearea-picker .emojionearea-filters,

.on_loading[style*="eee"],

.bg-dark-l,
.bmc-select-box .select-box-child,

.bmc-text-search-curved, .bmc-text-search-curved,

.sidebar-dd
{
    background: #2e2e2f !important;
}

.inline-btn-md:hover,
.md-btn-w-l:hover,

.sidebar-item-each.active,

.main-comment .emojionearea .emojionearea-editor,

.custom-color-page .orange-dim-bg-border,

.coffee-count-wrapper,

.br-r-45,

.bmc-dropdown-style-menu ul li:hover,

.bmc-input-box,

.sm-bmc-btn:hover,

.trans-table tr:hover td,
.pd-8:hover,

.bg-dark-op-h, .bg-dark-op-l:hover, .bmc-btn-grey:hover, .inactive-btn:hover, .bmc-text-field:hover, .bmc-ex-editor .note-editable:hover,

.bmc-select-box .bmc-text-field,

.post-list:hover,

.bmc-comment-emoji-opener .emojionearea, .bmc-comment-emoji-opener .emojionearea:hover
{
    background: #12121354 !important;
}

.custom-color-page .sup-comment-curved-arrow
{
  background: #252526;
}

.bg-white,

.sticky-toolbar.home-tab-web[style*="99"],

.active-option-wrapper,
.custom-color-page .active-option,

.emojionearea .emojionearea-picker .emojionearea-filters .emojionearea-filter.active,

.editor-title,
.editor__content,

.ty-share-wrapper
{
  background: #0000 !important;
}

.bmc-dropdown.dropdown-menu
{
  background: #252526 !important;
}

.bg-primary, .bmc-btn-primary,

.publish-button
{
    background-color: #9b7000;
}

.read-more-post
{
  background: linear-gradient(#0000, #3d3d3f);
}
.on_loading
{
  background: linear-gradient(90deg, #363639, 2e2e2f) !important;
}

body, input, ::placeholder, p, span, 

.color-dark, .bmc-btn-xlg, .bmc-btn-lg, .bmc-btn-md, .bmc-btn-sm, .tab-each:hover, .tab-each:active, .tab-each.active,

.sidebar-item-each.active .sidebar-item-each-text,
.sidebar-item-each:hover .sidebar-item-each-text,

.bmc-input-emoji-opener .emojionearea .emojionearea-editor,
.bmc-input-emoji-opener .emojionearea .emojionearea-editor:empty:before,

.bmc-dropdown li .drop-link:hover,

.no-featured-wrapper .creator-pg-head-content,
.orange-on-hover.active-creator-tab,

.clr-dark,

.supp-wrapper a,

.color-0d0, .color-0D0, .color-22,

.clr-grey,

.bio-para-creator p, .bio-para-creator div,
.editor-title
{
  color: #ccc !important;
}

.color-grey, .tab-each
{
  color: #999;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

path[stroke*="222"], circle[stroke*="222"],

path[stroke*="717171"]
{
  stroke: #ccc !important;
}
path[fill*="222"],

.active-option .fill-if-active-supp,

.drop-more-opt:hover svg path, .post-more-option:hover svg path,

.pe-menubar__button,
path[fill*="#0D0C22"],
polygon[fill*="000000"]
{
  fill: #ccc !important;
}
path[fill*="white"]
{
  fill:red;
}

.sticky-toolbar.home-tab-web,

.editor-title,
.editor__content,
.pe-menubar
{
  border-bottom: 1px solid #ccc5 !important;
}
.br-btm-1px-dark, .tab-each.active:after
{
  border-bottom: 2px solid #ffdd00 !important;
}

.emojionearea .emojionearea-picker
{
  box-shadow: 0px 0px 5px 0px #000 !important;
}

img[src*="ftr-logo"] 
{
    filter: invert(1);
}

.bmc-input-emoji-opener .emojionearea .emojionearea-editor:before,
.bmc-input-emoji-opener .emojionearea .emojionearea-editor
{
  padding-left: 10px !important;
}

.sticky-toolbar.home-tab-web[style*="99"]
{
  padding-top: 31px
}

.trans-type-bg > svg
{
  position: absolute;
  top: 30%;
  left: 30%;
  transform: translate(-50%, -50%);
}

.mg-t-12,

.av-heavy-accented,

.color-grey, .tab-each
{
    font-family: avenir roman !important;
}

.custom-color-page .sup-comment-box:after
{
  display: none;
}

.bmc-header-logo.xs-dis-none,

.cp-header .head-logo
{
  background-image: url(https://i.imgur.com/cnCaXMS.png);
  background-position: center;
  background-size: contain;
  background-repeat: no-repeat;
  width: 180px;
}
.bmc-header-logo.xs-dis-none *,
.cp-header .head-logo *
{
  opacity: 0;
  width: 100%;
}
}

@-moz-document domain("buymeacoffee.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }
::-webkit-scrollbar-corner { background: #0000; }
}
