style.css:
@-moz-document domain("ko-fi.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,

.swal2-show,

.horizontal-scroll, .wf-setting-box,

.body-content,
.item-tab-category,

.white-bg, .ibox-content,

.bg-light
{
  background: #3d3d3f !important;
}

.kofi-container, .flat-panel,

.white-onboarding-container,

.simple-panel, .tabs, .addcontent-panel, .payment-panel, .upgrade-panel, .postUpdateTextBoxContainer, .update-bubble, .grey-panel-dashed, .btn-next-steps,
.btn-showmore, .album-item,

.sidenav, .kfds-srf-sidebar, .kfds-btn-sidemenu, .wf-setting-tab-fixed, .chosen-container-multi .chosen-choices li.search-choice,
.list-group-item,

.tiles-wrap li, .item-tab-category-selected,

.widget-head-color-box, #dash-profile-card-div, .dash-panel, .gold-feature,

.input-group-addon:not(:first-child):not(:last-child), .input-group-btn:not(:first-child):not(:last-child), .input-group .form-control:not(:first-child):not(:last-child), .input-group-text,

button.swal2-cancel.swal2-styled,
.swal2-popup .swal2-styled,

.gallery-item-modal-container, .loading-bg, .item-current-bg,

.kfds-c-srf-personal-info-panel, .content-upload-panel,
.kfds-c-feed-update,

.kfds-hz-follow-suggestion-box,

.kfds-hz-featured-creator, .kfds-hz-commission-each,

.kfds-c-vertical-tab .kfds-c-vertical-tablinks, .kfds-c-vertical-tab .kfds-c-gold-custom-vertical-tablinks, 
.kfds-c-srf-testimonial-card,

.kfds-c-srf-post-controller,
.fr-box.fr-basic .fr-wrapper,
.fr-desktop .fr-command:hover:not(.fr-table-cell), .fr-desktop .fr-command:focus:not(.fr-table-cell), .fr-desktop .fr-command.fr-btn-hover:not(.fr-table-cell), .fr-desktop .fr-command.fr-expanded:not(.fr-table-cell),

.fr-popup, .fr-popup .fr-buttons.fr-tabs,
.fr-toolbar .fr-command.fr-btn.fr-btn-active-popup, .fr-popup .fr-command.fr-btn.fr-btn-active-popup, .fr-modal .fr-command.fr-btn.fr-btn-active-popup,
.fr-popup .fr-input-line input+label, .fr-popup .fr-input-line textarea+label,

.btn-addsomething
{
    background: #363639 !important;
}

input, textarea, select,
.navbar-default, footer,

.kfds-srf-shadow,

.dropzone, .button-content-type, .label-tag, .btn-upgrade, .ghost-s-button,

.chosen-container-multi .chosen-choices,

.gold-price-white-panel, .ui-card,

.btn-xs, .btn-group-xs > .btn,

.selectabletextbox,

.sticky-footer,

.kfds-c-feed-interaction-btn,

.gallery-item-header, .gallery-item-container-top,
.like-box, .tiny-support-button, .dropdown-menu,
.wf-breadcrumb-bg,

.kfds-c-shadow-buble,

.fixed-tab-menu,
.kfds-btn-tertiary-light, .kfds-btn-primary-light, .kfds-btn-tertiary-light-icon, .kfds-c-btn-secondary-light, .kfds-c-btn-primary-light,

.social-feed-box,

.kfds-font-size-hint.kfds-font-bold.kfds-font-clr-dark-op-6.kfds-c-srf-text-wrapper-commission, .kfds-hz-category-box,

.kfds-c-navtab-not-login, 
.kfds-c-claim-wrapper,
.kfds-font-16-to-14-landing,

.kfds-c-srf-search-bar,
.kfds-font-size-hint[style*="background: #ececec;"],

.btn-secondary,

.second-toolbar, .fr-toolbar, .fr-toolbar .fr-newline,

.fr-popup .fr-checkbox *,
.btn-cancel,

.badge-draft,
.kfds-c-btn-teri-no-outline:hover,

.kfds-btn-ghost-dark, li>.kfds-btn-ghost-dark
{
    background-color: #2e2e2f !important;
}

.ds-c-inner-tile-wrapper
{
    background-color: #12121354;
}

.ds-c-inner-tile-wrapper:hover
{
    background: #363639;
}

.swal2-icon.swal2-success [class^='swal2-success-circular-line'], .swal2-icon.swal2-success .swal2-success-fix,
[type=radio]:checked ~ label,
.setting-tab-link.active,
.panel,
.dropzone .dz-preview.dz-image-preview,

.content-link-post,

.kfds-c-vertical-tabcontent, .kfds-c-vertical-tab, .kfds-c-srf-step-wrapper, .kfds-c-srf-explore-creator-wrapper,

#show-more-content-link,

.person-list-footer, .person-list-ul,

.no-cover-visitor > .kfds-c-srf-gradient-profile-bg,

.ds-support-panel-wrapper
{
  background: none !important;
}

.kfds-srf-linear-btm 
{
    background: linear-gradient(180deg,#2e2e2f00 0%,#2e2e2f66 8%,#2e2e2f 17%,#2e2e2fbb 50%);
}

body, input, textarea, select,
.navbar-default .navbar-nav > li > a,
h1, .h1, h2, .h2, h3, .h3, .heading, h1, h2, h3, h4, h5, h6,
.footer-mini-copyright-text, .footer-mini-link,

.input-group-addon,
.swal2-popup .swal2-content,

.tab label, .text-black,
.button-content-type, .pn-flex, .update-bubble, name, .ghost-m-button,
.add-album-box, .kfds-btn-sidemenu, .setting-tab-link,

.gold-sub,.gold-feature-title, .gold-feature-description,
.gold-price-white-panel  *,
.ui-testimonials .item .ui-card p, .text-dark-gray, .heading.text-dark-gray,

.tiles-wrap p, .explore-card,

#A_8, #dash-profile-info *,
#chart_div *, 
.widget-feature-description,

.modal-h2,

.section .section-heading.center .paragraph, .sticky-footer, .footer-linktext,

.kfds-font-clr-dark-op-6,.content-link-text,.kfds-c-feed-interaction-btn,

.verified-check,

.tiny-support-button, .dropdown-menu li a,
.wf-breadcrumb-bg, .like-box,

.kfds-c-srf-stroke, .kfds-btn-tertiary-light, .kfds-btn-tertiary-light-icon, .kfds-c-btn-secondary-light, .kfds-c-btn-primary-light,

.kfds-font-clr-dark,

.kfds-c-follow-suggestion-text-height *,

.kfds-c-navtab-not-login *,

.kfds-c-vertical-tab .kfds-c-gold-custom-vertical-tablinks, .kfds-c-vertical-tab .kfds-c-vertical-tablinks,
.kfds-c-srf-testimonial-card > a,

.kfds-font-text-limit-titlepost,

.article .ibox-content *,

.kfds-font-clr-dark-op-8,

.person-row-name-link,

.fr-box.fr-basic .fr-element,
.fr-popup,

.ibox .row,

.ds-c-announcement-tile-wrapper,

.ds-c-action-tile
{
  color: #ccc !important;
}


.article-body, .article h1
{
  filter: invert(1) hue-rotate(195deg) saturate(2);
}

.feeditem-action,
.dropdown-menu li a:hover
{
  color: #999 !important;
}
.setting-tab-link.active
{
  color: #ff5e5b !important;
}

.ui-testimonials .item .ui-card::after
{
  display: none;
}

*,
.border-bottom
{
  border: none !important;
  box-shadow: none !important;
}

.profile-header h1
{
  text-shadow: none;
}

button.close
{
    filter: invert(1) hue-rotate(180deg) saturate(2) brightness(.8);
    mix-blend-mode: color-dodge;
}

.kfds-c-page-ending-bg
{
    filter: invert(1);
    mix-blend-mode: color-dodge;
}

footer {
  position: absolute;
  bottom: 1;
  left: 0;
  width: 100%;
}

.onboarding-logo-container,
.navbar .ui-variable-logo .logo-default,
.upgrade-panel .mt,

.kfds-c-navtab-not-login > div > div img
{
  content: url(https://i.imgur.com/h4TysiN.png) !important;
}

.onboarding-logo-container
{
  margin: auto;
}

.update-bubble:after
{
  border-color: #2e2e2f transparent;
}

.fr-popup
{
  box-shadow: 0 0 15px 5px #2e2e2f !important;
}

.social-feed-box
{
  border-radius: 15px 5px 5px 5px;
}

text
{
  stroke: #ccc;
}

#chart_div > div > div > svg > g:nth-child(2) *
{
  fill: #2e2e2f;
}
}
