style.css:
@-moz-document url-prefix("https://aur.archlinux.org/"), url-prefix("https://archlinux.org/"), url-prefix("https://security.archlinux.org/"), url-prefix("https://bugs.archlinux.org/") {
/** ArchLinux.org Dark
 * 
 * v0.31
 * By nakh
 */

/*** Custom Colors ***/
:root {
    --light: #ccc;
    --lighter: #e8e8e8;
    --dark: #03344d;
    --darker: #022739;
    --code-highlight: #103740;
    
    --dark-bg: #2b2b2b;
}

/*** General Styles ***/
body {
    background-color: var(--dark-bg) !important;
}

a:link,
a:visited {
    color: #4ca6d3 !important;
}

select {
    color: var(--lighter) !important;
    background-color: var(--dark-bg) !important;
}

/*** Main Page (Home) ***/
#nav-sidebar > h4 {
    color: var(--lighter) !important;
}

#news h3 a {
    color: var(--lighter) !important;
}

pre {
    /* Change the color of the padding around certain code blocks */
    background: var(--code-highlight) !important;
}

dl dt {
    color: var(--light) !important;
}

/*** Packages ***/
#pkglist-search {
    background-color: var(--dark-bg) !important;
}

#pkglist-results {
    background-color: var(--dark-bg) !important;
}

#pkglist-results-form th,
#pkglist-results-form tr {
    background-color: var(--dark-bg) !important;
}

#pkglist-results-form tr:hover {
    background-color: var(--dark) !important;
}

#pkglist-about {
    background-color: var(--dark-bg) !important;
}

table.results {
    background-color: var(--dark-bg) !important;
}

#exact-matches {
    /* Exact Matches box */
    background-color: var(--dark-bg) !important;
}

#exact-matches tr, #exact-matches th {
    background-color: var(--dark-bg) !important;
}

/*** Package Page ***/
#pkgdetails {
    background-color: var(--dark-bg) !important;
}

#actionlist {
    background-color: var(--dark) !important;
}

#actionlist h4 {
    color: var(--light);
}

/* Forums TODO */
/* Wiki TODO */
/* Bugs */
#container {
    background: var(--dark-bg) !important;
    color: var(--light) !important;;
}

div#showvotes, div.box, p.box, table.userlist, div#menu, div#pm-menu, div#search, div#tasklist, div#taskdetails, div.redirectmessage, table.history, div.popup, div.denyform, div.tbuttons, div.tab {
    background: var(--dark-bg);
}

button {
    background: var(--dark-bg);
}

#comments {
    background: var(--dark-bg) !important;
    color: var(--lighter);
}

#related #tasks_related,
#related #duplicate_tasks{
    color: var(--lighter);
}

/* Security */
h1 {
    color: var(--lighter);
}

b {
    color: var(--light);
}

table.styled-table {
    background: var(--dark-bg);
}

table.styled-table th {
    color: var(--light);
    background: var(--dark-bg) !important;
}

table.styled-table td {
    color: var(--light);
}

table.styled-table tr:hover {
    background: var(--darker);
}

table#pagenumbers {
    color: var(--lighter);
}

ul#submenu li a {
    background: var(--dark-bg);
}

ul#submenu li a.active {
    background: var(--dark);
}

/*** AUR ***/
#intro {
    background-color: #1a1a1a !important;
}

#news {
    color: var(--light) !important;
}

div.box {
    color: var(--lighter) !important;
}

#pkgsearch {
    background: var(--dark) !important;
}

#pkg-updates {
    background: #1a1a1a !important
}

#pkg-stats {
    background: var(--dark) !important;
}

#footer  {
    color: #f4f4f4 !important;
}

code {
    background: var(--code-highlight) !important;
}

.package-comments {
    color: var(--light) !important;
}

/* Downloads */
#arch-downloads {
    background: var(--dark-bg);
}

/*** TODO Pages. No, not things I will finish later. These are the actual WIPs in /todo/ ***/
#dev-todo-details {
    background-color: var(--dark-bg) !important;
}

.filter-criteria {
    background-color: var(--dark-bg) !important;
}

#dev-todo-pkglist tr,
#dev-todo-pkglist th {
    background-color: var(--dark-bg) !important;
}

#dev-todo-pkglist tr:hover {
    background-color: var(--dark) !important;
}
}

@-moz-document url-prefix("https://security.archlinux.org/") {
#anb-security a {
    color: white !important;
}
}
