style.css:
@-moz-document domain("ivkhk.siseveeb.ee") {

body { 
    background-color: #100f0f;
    color: #fff;
    vertical-align: middle;
}
.panel {
    border: none;
    border-radius: 8px;
    background-color: #282828;
    margin-bottom: 16px !important;
    box-shadow: none;
}
.well {
    background-color: #282828;
    border: none;
    border-radius: 8px;
}
.panel-default>.panel-heading {
    color: #fff;
    background-color: #323232;
    border: none;
}
.panel-heading {
    border-bottom: none;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
}
.panel-primary>.panel-heading {
    background-color: #3c3c3c;
}
.panel-info>.panel-heading {
    color: #fff;
}
.panel>.table:last-child>tbody:last-child>tr:last-child td:first-child {
    border-bottom-left-radius: 8px;
}
.panel>.table:last-child>tbody:last-child>tr:last-child td:last-child {
    border-bottom-right-radius: 8px;
}
.navbar-inverse {
    background-color: #282828;
    border: none;
}
.navbar-inverse .navbar-nav>.open>a {
    background-color: #3c3c3c;
}
.navbar-inverse .navbar-nav>.open>a:hover, .navbar-inverse .navbar-nav>.open>a:focus {
    background-color: #3c3c3c;
}
.navbar-nav .open .dropdown-menu {
    background-color: #3c3c3c;
    box-shadow: none;
}
.navbar-inverse .navbar-nav .open .dropdown-menu>li>a {
    color: #fff;
}
.nav-tabs {
    border-bottom: 0;
}
.nav-tabs>li.active>a {
    color: #fff;
    background-color: hsla(0, 0%, 0%, 0);
    border-bottom-color: #fff;
}
.nav-tabs>li>a {
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
}
.nav>li>a:focus, .nav>li>a:hover, .nav>li.active>a:focus, .nav>li.active>a:hover {
    background-color: rgba(255, 255, 255, 0.2);
    color: #fff;
    border-bottom-color: #fff;
}
.nav-pills>li.active>a:hover {
    border-radius: 20px !important;
    background-color: #3c3c3c;
}
.nav-pills>li.active>a, .nav-pills>li.active>a:focus {
    border-radius: 20px !important;
    background-color: #282828;
}
.nav-pills>li>a {
    border-radius: 20px !important;
}
.panel-body {
    padding: 16px;
}
.container {
    margin-right: auto;
    margin-left: auto;
    padding-left: 0;
    padding-right: 0;
}
.form-control {
    background-color: #3c3c3c;
    color: #fff;
    border: 0;
    border-radius: 15px;
    margin-bottom: 16px;
}
.form-control[disabled], .form-control[readonly] {
    background-color: #3c3c3c;
}
a.list-group-item {
    color: #fff;
}
.list-group-item {
    display: flow-root;
    background-color: #282828;
    border: 0;
    border-radius: 8px;
    border-bottom-right-radius: 8px;
    border-bottom-left-radius: 8px;
    margin-bottom: 5px;
}
.list-group-item:first-child {
    border-bottom-right-radius: 8px;
    border-bottom-left-radius: 8px;
    border-top-right-radius: 8px;
    border-top-left-radius: 8px;
}
.list-group-item:last-child {
    border-bottom-right-radius: 8px;
    border-bottom-left-radius: 8px;
    border-top-right-radius: 8px;
    border-top-left-radius: 8px;
}
.list-group.panel>.list-group-item {
    border-bottom-right-radius: 8px;
    border-bottom-left-radius: 8px;
    border-top-right-radius: 8px;
    border-top-left-radius: 8px;
}
svg.menu-collapse-icon {
    color: rgb(80, 80, 80);
}
.text-warning {
    color: #fff !important;
}
.text-uppercase {
    text-transform: uppercase;
    font-weight: bold;
}
.qTip {
    background-color: #3c3c3c;
    border: none;
    border-radius: 15px;
    color: #fff;
}
h3 {
    margin-bottom: 20px;
    margin-top: 20px;
    font-weight: bold;
    line-height: 1;
    color: #fff;
}
h3 small {
    color: #fff;
}
.bg-warning {
    background-color: #3c3c3c;
}
.table>tbody>tr>td {
    border-top: 2px solid;
    border-color: #7d7d7d;
}
.table-hover>tbody>tr:hover {
    background-color: #505050;
}
a {
    color: #ffffff;
}
a:hover, a:focus {
    color: #fff;
}
.ui-widget-content {
    border: none;
    color: #fff;
    background: none;
    background-color: #3c3c3c !important;
}
.ui-state-active, .ui-widget-content .ui-state-active, .ui-widget-header .ui-state-active, .ui-state-default {
	background: #3c3c3c !important;
    border: 2px solid #fff;
}
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    border: 2px solid #fff;
}
.ui-state-default a, .ui-state-default a:link, .ui-state-default a:visited {
	color: #fff;   
}
.ui-corner-top, .ui-corner-all {
    border-top-right-radius: 8px;
    border-top-left-radius: 8px
}
.fixed_header_copy {
    background-color: #3c3c3c !important;
}
.fixed_header_copy:not(.table-bordered) {
    margin: 0;
}
.progress-bar {
    background-color: #282828;
}
.daybook_S {
    background-color: rgba(170, 255, 170, 0.2) !important;
}
.daybook_P {
    background-color: rgba(170, 170, 255, 0.2) !important;
}
.daybook_V {
    background-color: rgba(255, 170, 170, 0.2) !important;
}
.daybook_T {
    background-color: hsla(270, 100%, 80%, 0.2) !important;
}
.daybook_R {
    background-color: hsla(300, 100%, 97%, 0.2) !important;
}
.daybook_empty {
    background-color: hsla(0, 0%, 94%, 0.2) !important;
}
h2{
    margin-top: 20px;
    margin-bottom: 20px;
    font-size: 23px;
    font-weight: bold;
    color: #fff;
}
h2 small {
    font-weight: normal;
    color: #fff;
}
legend {
    font-size: 19.5px;
    line-height: 1.1;
    color: #fff;
    padding-bottom: 10px;
    border: 0;
    border-bottom: 1px solid #e5e5e5;
}
.btn {
    border-radius: 15px;
    margin-left: 8px;
}
.btn-default {
    color: #fff !important;
    background-color: rgba(255, 255, 255, 0.1) !important;
    border: 0;
}
.btn-default .badge {
    color: #000;
    background-color: #fc0;
}
.label-default {
    background-color: #fff;
}
.label-warning {
    background-color: #fc0 !important;
}
.label-danger {
    background-color: #ff3c3c;
}
.label-primary {
    background-color: #fc0;
}
.btn-default.active {
    color: #fff;
}
.btn-primary {
    color: #fff;
    background-color: #323232;
    border: 0;
    border-radius: 15px;
}
.btn-warning, .btn-warning:hover, .btn-warning:focus, .btn-warning:active, .btn-warning.active, .btn-warning.focus {
    background-color: #fc0 !important;
    color: #000 !important;
}
.btn-primary:hover {
    background-color: #505050 !important;
}
.btn-primary:focus, .btn-primary:active, .btn-primary.focus, .btn-primary.active {
    background-color: #141414 !important;
}
.btn-sm, .btn-xs {
    border-radius: 15px;
}
.btn-group {
    vertical-align: top;
}
.label {
    border-radius: 15px;
    color: #000;
}
.label-info {
    background-color: #39f !important;
    color: #fff !important;
}
.label.label-plan-teacher {
    color: #000;
}
.badge {
    color: #000 !important;
    background-color: #fff !important;
}
a.fc-event:not(.kiosk_plan_cell) {
    background-color: rgb(35, 35, 35) !important;
    color: #fff !important;
}
a.fc-event:not(.kiosk_plan_cell) .fc-time {
    background-color: #323232 !important;
    color: #fff !important;
}
.fc-event {
    border: 0;
}
.fc-unthemed td, .fc-unthemed th {
    border-color: rgb(50, 50, 50) !important;
}
.fc-unthemed td.fc-today {
    background: #505050 !important;
}
.fc-time-grid-event:not(.lesson_time):hover {
    border: 2px solid #fff !important;
}
.list-group-item.active {
    background-color: rgb(60, 60, 60) !important;
}
.panel-footer {
    background-color: hsla(0, 0%, 0%, 0);
}
.panel-info>.panel-heading {
    background-color: hsla(0, 0%, 0%, 0);
}
.chosen-container-active.chosen-with-drop .chosen-single {
    background: none;
    background-image: none;
    background-color: hsla(0, 0%, 0%, 0);
    color: #fff;
    border: 1px #fff solid !important;
    border-image: 0 !important;
    border-radius: 15px;
    box-shadow: none;
}
.chosen-container-single .chosen-single {
    background: none;
    background-image: none;
    background-color: hsla(0, 0%, 0%, 0);
    color: #fff;
    border: 1px #fff solid !important;
    border-image: 0 !important;
    border-radius: 15px;
    box-shadow: none !important;
}
.chosen-container {
    margin-left: 8px;
    width: 250px !important;
}
.modal-content {
    background-color: #100f0f;
}
.modal-header {
    border-bottom: 2px solid #7d7d7d !important;
}
.modal-footer {
    border-top: 2px solid #7d7d7d !important;
}
.modal-open .modal {
    overflow-x: hidden;
    overflow-y: hidden;
}
@media (min-width: 768px) {
.modal-dialog {
    width: 600px;
}}
h4, h5 {
    color: #fff;
}
.close {
    color: #fff !important;
    text-shadow: none;
    opacity: 1 !important;
}
hr {
    margin-top: 0;
    margin-bottom: 0;
    border-top: 0;
}
.exports {
    border: 0;
}
.ui-state-default, .ui-widget-content .ui-state-default {
    color: #fff;
}
.ui-widget-header {
    color: #000;
}
.dropdown-menu {
    background-color: #3c3c3c;
}
.dropdown-menu>li>a {
    color: #fff;
}
.chosen-container .chosen-drop {
    box-shadow: none;
    background: #3c3c3c;
}
.chosen-container .chosen-results {
    color: #fff;
}
.chosen-container .chosen-results li.no-results {
    background: #3c3c3c;
    color: #fff;
}
.chosen-container-single .chosen-drop {
    margin-top: 0;
    border-radius: 0;
    border: 0;
}
.chosen-container .chosen-results li.highlighted {
    background-image: none;
    background-color: #505050;
}
.alert-info {
    background-color: hsla(48, 100%, 50%, 0.2);
    color: #fff;
    border-radius: 8px;
    border: 0;
}
.alert-success {
    background-color: rgba(170, 255, 170, 0.2);
    color: #fff;
    border: 0;
    border-radius: 8px;
}
.input-group-addon:last-child {
    border-top-right-radius: 15px;
    border-bottom-right-radius: 15px;
}
}
