style.css:
@-moz-document domain("jkanime.net") {
/* scrollbar */
::-webkit-scrollbar {
    width: 14px;
    border-radius: 1px;
    border: 1px solid hsl(0, 0%, 0%);
    background: #101010;
}

::-webkit-scrollbar-thumb {
    border-radius: 1px;
    border: 1px solid hsl(0, 0%, 0%);
    background-color: #161515;
}

::-webkit-scrollbar-thumb:active {
    background: #191818;
    border: 1px solid #000
}


#slider3 .viewport::-webkit-scrollbar-track {
    border: 1px solid hsl(0, 0%, 0%);
    background-color: #161515;
}

#slider3 .viewport::-webkit-scrollbar-thumb {
    width: 12px;
    border-radius: 1px;
    border: 1px solid hsl(0, 0%, 0%);
    background: #35a9e2;
}

#slider3 .viewport::-webkit-scrollbar-thumb:active {
    background: #1194d5;
    border: 1px solid #000
}

/*-----------------------------*/
/* cuandro de busqueda */
.form_search2 input {
    border-radius: 5px;
    padding: 13px 35px 13px 15px;
    background: #070707;
    display: inline-block;
    border: 1px solid #070707;
    box-shadow: 0 0 20px #000000;
}

/*------------------------------*/
/* Cuerpo page */
body {
    background: #151515;
}

/*-----------------------------*/
/* barra de titulo */
#site-header {
    margin: 0;
    padding: 0;
    width: 100%;
    height: 120px;
    background: #181818;
    float: left;
}

/*------------------------------*/
/* barra de menu titulo */
ul.menu-links a {
    transition: .15s ease-in-out;
    text-decoration: none;
    font-size: 12px;
    padding: 8px 55px;
    background: #121212;
    color: #fff;
    border: 1px solid #070707;
    border-radius: 4px;
    box-shadow: 0 0 20px #000000;
}

ul.menu-links a:hover {
    background: #1b91cc;
}

/*----------------------------*/
/* cuerpo container */
#container {
    margin: 0;
    padding: 10px 0 0 0;
    width: 100%;
    background: #151515;
    float: left;
}

/*---------------------------*/
/* barra de titulo container */
.letras-box h3 {
    margin: 0;
    padding: 6px 10px;
    font-size: 12px;
    color: #FFFFFF;
    background: #121212;
    float: left;
}

/*-------------------------*/
/* barra letra box */
.letras-box ul {
    margin: 0;
    padding: 0;
    width: 100%;
    height: 46px;
    list-style: none;
    background: #212529;
    float: left;
}

.letras-box ul li a.letra-link:hover {
    background: #1b91cc;
    color: #fff;
}

.letras-box ul li.box-link a {
    margin: 0;
    padding: 0 10px;
    line-height: 46px;
    font-size: 15px;
    color: #FFFFFF;
    background: #495057;
    display: block;
}

li.box-link a[href*="tipoint"] {
    margin-left: 11px;
    border-right: 0 !important;
    background: #212529 !important;
}

.letras-box ul li.box-link a:hover {
    background: #1b91cc !important;
}

/*-----------------------*/
/* barra de programacio9n */
.prog-header {
    position: relative;
    margin: 0 0 5px 0;
    padding: 6px 0 0 10px;
    width: 391px;
    height: 30px;
    font-size: 17px;
    color: #FFFFFF;
    background: #35a9e2;
    border-radius: 3px 0 0 3px;
    float: left;
}

.prog-header:after {
    left: 100%;
    top: 50%;
    border: solid transparent;
    content: " ";
    height: 30px;
    width: 0;
    position: absolute;
    pointer-events: none;
    border-color: rgba(232, 76, 61, 0);
    border-left-color: #35a9e2;
    border-width: 16px;
    margin-top: -17px;
}

/*--------------------------*/
/* cuerpo de la barra de programacion */
#programacion .overview {
    background: #35a9e2;
    position: relative;
    height: auto !important;
    padding: 0;
    margin: 0;
    width: 100%;
    list-style: none;
    float: left;
}

#programacion .overview a.odd,
#programacion .overview a.even {
    background: #151515;
    position: relative;
    margin: 0 20px 2px 0;
    padding: 0;
    width: 381px;
    height: 73px;
    border: 1px solid #35a9e2;
    border-right: none;
    border-radius: 3px 0 0 3px;
    float: left;
}

.prog-thumb:before {
    border-color: rgba(204, 204, 204, 0);
    border-left-color: #35a9e2;
    border-width: 38px;
    margin-top: -38px;
}

.prog-thumb:after {
    border-color: rgba(255, 255, 255, 0);
    border-left-color: #ffffff00;
    border-width: 37px;
    margin-top: -37px;
}

.prog-thumb .arrowed {
    position: absolute;
    top: 0;
    right: 0;
    width: 37px;
    height: 76px;
    background: none;
    display: block;
    z-index: 10;
}

.play-button {
    position: absolute;
    top: 16px;
    right: -12px;
    width: 36px;
    height: 45px;
    background: none;
    display: block;
    z-index: 10;
}

#programacion .overview a.odd,
#programacion .overview a.even {
    position: relative;
    margin: 0 20px 2px 0;
    padding: 0;
    width: 381px;
    height: 73px;
    border: 1px solid #35a9e2;
    border-right: none;
    border-radius: 3px 0 0 3px;
    float: left;
}

#programacion .overview a.odd:after,
#programacion .overview a.even:after,
#programacion .overview a.odd:before,
#programacion .overview a.even:before {
    left: 100%;
    top: 50%;
    border: solid #35a9e2;
    content: "";
    height: 0;
    width: 0;
    position: absolute;
    pointer-events: none;
}

#programacion .overview a.odd:before,
#programacion .overview a.even:before {
    border-color: rgba(245, 245, 245, 0);
    border-left-color: #35a9e2;
    border-width: 37px;
    margin-top: -37px;
}

#programacion .overview a.odd:after {
    border-color: rgba(232, 76, 61, 0);
    border-left-color: #151515;
    border-width: 36px;
    margin-top: -36px;
}

#programacion .overview a:hover {
    background-color: #1b91cc !important;
    border-color: #1b91cc !important;
}

#programacion .overview a:hover::after,
#programacion .overview a:hover::before {
    border-left-color: #1b91cc;
}

::selection {
    background: #35a9e2;
    color: #fff;
    text-shadow: none;
}


/*--------------------*/
/* BARRA HOME TITLE */
.ovas-home,
.ovas-container {
    margin: 0;
    padding: 0;
    width: 100%;
    float: left;
    border: 2px solid #000;
}

.home-title {
    margin: 0;
    padding: 0;
    width: 100%;
    height: 36px;
    border-radius: 3px;
    background: #212529 !important;
    float: left;
}

.ovas-container .odd {
    background-color: #151515;
}

v-content img {
    position: absolute;
    top: 0px;
    left: 1px;
    margin: 0;
    padding: 0;
    width: 80px;
    float: left;
}

.ov-content {
    position: relative;
    margin: 0;
    padding: 0;
    width: 33.333333333333%;
    height: 88px;
    overflow-y: hidden;
    float: left;
    border: 2px solid #35a9e2;
    -o-transition: .5s;
    -ms-transition: .5s;
    -moz-transition: .5s;
    -webkit-transition: .5s;
    transition: .5s;
}

.ov-content:hover {
    background-color: #1b91cc !important;
}

.ov-content h2 {
    margin: 10px 5px 0 0;
    padding: 0px 0px 0px 96px;
    font-size: 13px;
    color: #3498DC;
    line-height: 130%;
    font-weight: normal;
    float: left;
    overflow: hidden;
    text-overflow: ellipsis;
    width: 100%;
}

/*----------------------------*/
/* barra top animes */
.fday-header {
    margin: 0;
    padding: 0;
    width: 100%;
    height: 38px;
    background: #212529;
    float: left;
}

ul.tab-day a.current,
ul.tab-day a.current:hover,
ul.tab-day li.current a {
    background: #35a9e2;
    cursor: default !important;
    color: #eeeeee !important;
}

.topten-ul .topten-1 .first-place {
    position: absolute;
    top: 10px;
    left: 0;
    padding: 4px 10px;
    font-size: 13px;
    color: #FFFFFF;
    background: #35a9e2;
    z-index: 5;
}

.gradient1 {
    background: linear-gradient(to bottom, #495057 0%, #212529 100%);
}

.gradient2 {
    background: linear-gradient(to bottom, #495057 0%, #212529 100%);
}

.share-count-gray {
    margin: 0;
    padding: 5px 10px;
    font-size: 13px;
    color: #fff;
    background: #35a9e2;
    float: left;
}

.topten-ul .topten-2 h2,
.topten-ul .topten-3 h2 {
    margin: 0;
    padding: 0;
    font-size: 18px;
    font-weight: normal;
    color: #f8f9fa;
    display: block;
}

.topten-ul .topten-1 .big-play,
.medium-play,
.small-play {
    display: none;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: #1b91cc;
    opacity: 0.9;
    z-index: 4;
}

.small-count {
    position: absolute;
    top: 5px;
    left: 5px;
    padding: 3px 6px 3px 6px;
    font-size: 12px;
    line-height: 100%;
    color: #FFFFFF;
    background: #35a9e2;
    z-index: 5;
}

.small-shares {
    position: absolute;
    bottom: 5px;
    right: 0;
    padding: 3px 6px;
    font-size: 12px;
    color: #fff;
    background: #35a9e2;
    float: left;
}

.share-count-blue {
    margin: 0;
    padding: 5px 10px;
    font-size: 13px;
    color: #fff;
    background: #35a9e2;
    float: left;
}

/*------------------------------*/
/* barra home agregados*/
.home-title {
    margin: 0;
    padding: 0;
    width: 100%;
    height: 36px;
    border-radius: 3px;
    background: #212529 !important;
    float: left;
}

.portada-box h2 {
    position: absolute;
    top: 0;
    left: 0;
    margin: 0;
    padding: 6px 3%;
    width: 100%;
    font-size: 13px;
    color: #FFFFFF;
    line-height: 120%;
    font-weight: normal;
    background: #35a9e2;
    border-radius: 3px 3px 0 0;
    display: block;
    z-index: 2;
}

.portada-box img {
    position: relative;
    width: 100%;
    height: auto;
    min-height: 220px;
    border-radius: 3px;
    border: 2px solid;
}

/*--------------------*/
/* barra agregados recientes */
.side-box {
    margin: 3px 0 15px 0;
    padding: 3px;
    width: 263px;
    border-radius: 3px;
    float: left;
    border: 2px solid deepskyblue;
    background: #070707;
    box-shadow: 0 0 20px #000000;
}

.side-box h3 {
    margin: 0 0 5px 0;
    padding: 6px 5px;
    font-size: 17px;
    color: #ffffff;
    font-weight: normal;
    line-height: 100%;
    background: #151515;
    display: block;
    border-bottom: 2px solid deepskyblue;
}

.side-menu li.odd a {
    background-color: #070707;
}

.side-menu li.even a {
    background-color: #070707;
}

.side-menu li a {
    margin: 0;
    padding: 5px 10px 4px 30px;
    font-size: 12px;
    color: #3498DC;
    display: block;
}

.side-menu li a:hover {
    background-color: #1b91cc;
    color: #fff;
}

/*--------------------------*/
/* page dos*/
/* barra titulo video */
.video-header {
    margin: 0;
    padding: 0 1%;
    width: 100%;
    height: 46px;
    color: #fff;
    background: #151515;
    float: left;
}

.dwnload-form select {
    color: #666;
    background: #212529;
}

a#cmode i {
    display: inline-block;
    padding: 9px 15px;
    color: #22303f;
    background: #656c7a;
    border-radius: 3px;
}

ul.server-tab {
    list-style: none;
    margin: 0 0 10px 0 !important;
    padding: 0;
    width: 100%;
    height: 38px;
    background: #0b0b0b;
    float: left;
}

ul.server-tab a.current,
ul.server-tab a.current:hover,
ul.server-tab li.current a {
    background: #151515;
    cursor: default !important;
    color: #fff !important;
}

ul.server-tab a {
    margin: 5px 0 0 6px;
    padding: 0 10px;
    position: relative;
    height: 28px;
    line-height: 28px;
    font-size: 13px;
    color: #fff;
    font-weight: normal;
    text-align: left;
    text-decoration: none;
    background: #121212;
    border-radius: 4px;
    display: block;
}

#reproductor-box {
    margin: 5px 0 5px 0;
    padding: 0;
    width: 100%;
    background: #0b0b0b;
    float: left;
}
.vnav-left, .vnav-right {
    margin: 0;
    padding: 0;
    width: 45%;
    height: 46px;
    line-height: 46px;
    font-size: 16px;
    color: #FFFFFF;
    background: #1b91cc;
    float: left;
}
.vnav-left:hover, .vnav-right:hover {
    background-color: #1C86B9;
    color: #fff;
}
.video-download {
    margin: 0;
    padding: 13px 0;
    width: 100%;
    background: #212529;
    color: #fff;
    font-size: 17px;
    line-height: 17px;
    text-transform: uppercase;
    font-weight: 600;
    text-align: center;
    border-radius: 3px;
    float: left;
}
div#combtns a.a {
    pointer-events: none;
    background: #070707;
    color: #dee2e6;
}
div#combtns a {
    display: inline-block;
    width: 50%;
    text-align: center;
    padding: 0;
    height: 50px;
    line-height: 50px;
    background: #070707;
    color: #fff;
    white-space: nowrap;
}
.video-download:hover {
    color: #fff;
    background: #343a40;
}
.absolutian .capnum {
    padding: 0 5px;
    height: 39px;
    line-height: 39px;
    color: #5F6B83;
    font-weight: bold;
    text-transform: uppercase;
    background-color: #212529;
    float: left;
}
.video-info {
    margin: 0;
    padding: 13px 2%;
    width: 100%;
    font-size: 14px;
    color: #dee2e6;
    background: #070707;
    border-radius: 3px;
    float: left;
}

/*--------------------*/

/* page tres */

/* barra imfor series*/

.serie-info {
    margin: 0 0 10px 0;
    width: 100%;
    background: #151515;
    border: 1px solid #070707;
    border-radius: 4px;
    box-shadow: 0 0 20px #000000;
    padding: 4px;
    float: left;
}

.info-box {
    margin: 12px 0 12px 12px;
    padding: 0 12px 0 0;
    width: 425px;
    border-right: 1px solid #1b91cc;
    float: left;
}

.sinopsis-links {
    margin: 0;
    padding: 10px;
    width: 96%;
    font-size: 12px;
    color: #535353;
    background: #151515;
    border-radius: 3px;
    display: block;
}

/*-----------------------------*/
/* sub barra ordenar*/
a#inverso {
    background: #343a40;
    padding: 5px;
    color: #1c95cf;
    border-radius: 4px;
    font-size: 13px;
}

/* barra proxep */
div#proxep {
    display: inline-block;
    width: 96%;
    text-align: center;
    background: #343a40;
    padding: 9px 5px;
    margin: 5px;
    border-radius: 3px;
    border: 1px solid #0e0e0e;
    font-size: 12px;
    color: #1d95cf;
    text-shadow: 1px 1px #2db1f1;
    margin-bottom: 12px;
}

/*---------------------------*/
/* barra numero navegacion */
.navigation a.numbers {
    margin: 0 0 0 12px;
    padding: 3px 10px;
    font-size: 13px;
    color: #2e9fff;
    font-weight: 600;
    background: #212529;
    border: 1px solid #343a40;
    border-radius: 4px;
    float: left;
}

/*----------------------------*/
/* barra frontante */
.alt-widget {
    margin: 0 0 20px 0;
    padding: 0;
    width: 100%;
    background: #EEEEEE;
    /* border-radius: 3px; */
    background: #151515;
    border: 1px solid #070707;
    border-radius: 4px;
    box-shadow: 0 0 20px #000000;
    float: left;
}

.alt-widget h3 {
    margin: 0 0 10px 0;
    padding: 0 2%;
    width: 100%;
    height: 35px;
    line-height: 35px;
    font-size: 16px;
    color: #FFFFFF;
    font-weight: normal;
    background: #1C86B9;
    border-radius: 3px 3px 0 0;
}

/*--------------------------*/
/* barra dimension comentarios */
.accordian li.even {
    background: #121212;
    color: #fff;
    ;
    border: 1px solid #070707;
    border-radius: 4px;
    box-shadow: 0 0 20px #000000;
}

.dimension {
    margin: 0 !important;
    padding: 15px 2% !important;
    width: 100%;
    background: #070707;
    border: 2px solid #121212;
    border-radius: 4px;
    box-shadow: 0 0 20px #000000;
}

.textarea-outer-wrapper .textarea-wrapper {
    border: 1px solid #121212;
    padding: 5px 10px;
    margin: 5px 6px 10px;
    border-radius: 4px;
    white-space: nowrap;
}

.nav.nav-primary {
    margin: 0 0 24px;
    border-bottom: 2px solid #6c757d;
}

.textarea-outer-wrapper {
    -webkit-appearance: none;
    background: #171719;
    border: 2px solid #121212;
    position: relative;
    border-radius: 4px;
    margin: 0;
}

.input--text {
    background: #171719;
    border: 2px solid #121212;
    transition: all .2s linear;
    padding: 5px 9px;
    margin: 0;
    border-radius: 4px;
    font-size: 13px;
    height: 32px;
}

.disqus-footer__wrapper {
    border-top: 2px solid #212529;
    padding: 7px 0 10px;
}








.placeholder {
    color: #09f;
    line-height: 30px;
    font-size: 15px;
    position: absolute;
    top: 7px;
    left: 10px;
    right: 9px;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
    cursor: text;
}
}
