style.css:
@-moz-document domain("v3rmillion.net") {
/* Kosaic#6378 made this simple theme. https://v3rmillion.net/member.php?action=profile&uid=68861 */
    .thead {
        background: #1c1c1c;
    }
    .tcat {
        background: #1e1e1e;
    }
    .trow_sep {
        background: #30303029;
    }
    #logo img,
    .fa {
        filter: brightness(0) invert(1)!important;
    }
    #bridge,
    .thread_head {
        border-bottom: 5px solid #131313!important;
    }
    ul.menu li a.active,
    td.tfoot {
        background: #131313!important;
    }
    ul.menu li ul,
    #footer,
    .panel_buttons {
        border-top: 5px solid #131313!important;
    }
    .pagination a,
    a.button:link,
    .postbit_buttons>a:link,
    a.postbit_find,
    button,
    input.button {
        background: #535353!important;
        border: 1px solid #535353!important;
    }
    a.button:visited,
    a.button:active,
    a.button:hover,
    .postbit_buttons>a:hover,
    button:hover,
    input.button:hover {
        background: #404040!important;
        border: 1px solid #404040!important;
    }
    i.fa.fa-thumbs-o-up,
    i.fa.fa-thumbs-o-down,
    i.fa.fa-search {
        filter: none!important;
    }
}
