}
.post__title_link:hover {
  color: #eee;
}
#facebook_like_box,
#vk_groups,
.comment-form__preview img,
.comment__message img,
.megapost-cover__img,
.page-header__banner img,
.post-share__buttons,
.post__text-html .oembed,
.post__text-html img,
.post__text-html pre code,
.sidebar-block__banner-image,
.teaser__image-pic,
