.post__text-html img:hover,
.post__text-html pre code:hover,
.sidebar-block__banner-image:hover,
.teaser__image-pic:hover,
video:hover {
  filter: brightness(1);
}
.post_preview .post__text-html .oembed,
.post_preview .post__text-html img {
  position: relative;
  width: unset;
  max-height: 300px;
  margin: 5px;
}
.post_preview .post__meta {
  position: absolute;
  right: 15px;
  bottom: 20px;
}
.post_preview .post__footer {
  position: absolute;
  bottom: -5px;
  left: 50%;
  background: #111;
  transform: translateX(-50%);
}
.post_preview .post__text-html .oembed {
  max-width: 533px;
}
.post__title {
  margin-bottom: 3px;
  padding-bottom: 10px;
  border-bottom: 1px solid #181818;
}
.post_preview .post__hubs + .post__marks {
  position: absolute;
  top: 0;
  right: 0;
}
.post__body_crop {
  padding-top: 15px;
}
.post__body {
  margin-bottom: 10px;
}
.post__habracut-btn {
  margin-top: 15px;
}
.content-list__item_post,
.content-list__item_post:first-child,
.content-list__item_post:last-child {
  padding: 17px 0;
}
article {
  padding: 15px 15px 1px;
  background: #111;
  border-radius: 3px;
  box-shadow: 0 0 0 1px #151515;
}
.post-additionals .post-stats {
  position: relative;
  top: -40px;
  left: 200px;
}
