}

#content.jvchat-night-mode .titre-info-fofo, #content.jvchat-night-mode .risi-smitemactif, #content.jvchat-night-mode .risi-smitem:hover, #content.jvchat-night-mode .jvchat-author {
    color: #FFFFFF !important;
