style.css:
@-moz-document url("https://www.wcostream.org/")
{
/* Ads & Annoyances */
  html body > div:not(#wrap)
  {
    display: none !important;
  }

  html *[style*="clear:both"]
  {
    display: none !important;
  }

/* Main Background */
  html body
  {
    background: rgb(30,34,36);
  }

/* Nav */
  html body #wrap
  {
    width: 100%;
    margin: 0;
  }

  #wrap .topb
  {
    background: rgb(30, 34, 36);
    position: fixed;
    width: 100%;
    height: 60px;
    border-bottom: 2px inset rgb(200, 80, 0);
    box-shadow: 0px 2px 8px rgb(10, 14, 16);
    z-index: 2;
  }

  #wrap .topb > tbody > tr > td:nth-of-type(1)
  {
    display: none;
  }

  #wrap .topb > tbody > tr > td:nth-of-type(2) > table > tbody
  {
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
  }

  #wrap .topb > tbody > tr > td:nth-of-type(2) > table > tbody > tr:nth-of-type(1)
  {
    display: none;
  }

  #wrap .topb > tbody > tr > td:nth-of-type(2) > table > tbody > tr:nth-of-type(2)
  {
    flex: 1 1 auto;
  }

  #wrap #navbar
  {
    margin: 0;
  }

  #wrap #navbar > #nav
  {
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    flex: 0 0 auto;
  }

  #wrap #navbar > #nav > li > a
  {
    padding: 0;
    height: 40px;
    width: 120px;
    line-height: 40px;
    margin: 10px 4px;
    text-align: center;
    color: rgb(220, 220, 220);
    border-radius: 4px;
    transition: .2s linear;
  }

  #wrap #navbar > #nav > li > a:hover
  {
    background: rgb(250, 120, 0);
    color: white;
  }

  #wrap .bh
  {
    display: none;
  }

  #wrap .animesearch
  {
    background: rgb(30, 34, 36);
    padding: 0;
    margin: 0;
    height: 60px;
  }

  #wrap .animesearch > form#konuara
  {
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    flex: 0 0 auto;
    padding: 10px 0;
  }

  #wrap .animesearch > form#konuara > input#searchbox
  {
    background: rgb(20, 24, 26);
    height: 40px;
    margin: 0;
    padding: 0 10px 0 10px;
    float: unset;
    border: none;
    color: rgb(220, 220, 220);
  }

  #wrap .animesearch > form#konuara > .searchboxtop
  {
    margin: 0;
  }

  #wrap .animesearch > form#konuara > .searchboxtop > select
  {
    padding: 0;
    height: 40px;
    width: 140px;
    text-align: center;
    border: none;
    background: rgb(40, 44, 46);
    box-shadow: none;
    color: rgb(220, 220, 220);
    font-weight: 600;
  }

  #wrap .animesearch > form#konuara > .searchboxtop > select:hover
  {
    background: rgb(50, 54, 56);
  }


/* Content Main Page */
  html body #wrap #content
  {
    padding: 80px 0 0 0;
    background: rgb(30,34,36);
    width: 100%;
    margin: 0;
    display: grid;
    grid-template-columns: auto 150px 250px;
    grid-template-rows: 400px auto 40px;
  }

  html body #wrap #content > .alert
  {
    display: none;
  }


/* RecentReleases */
  #wrap #content > table
  {
    width: calc(100% - 20px);
    grid-area: 1/1/2/4;
  }

  #wrap #content > table > tbody > tr > td:nth-of-type(1)
  {
    background: transparent !important;
    height: 400px !important;
  }

  #wrap #content > table > tbody > tr > td:nth-of-type(1) > table
  {
    width: unset;
    margin-right: 10px;
    height: 400px;
  }

  #wrap .manset
  {
    background: transparent;
    float: unset;
    margin: 0;
    padding: 0;
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    flex: 0 0 auto;
  }

  #wrap .manset > .shadetabs
  {
    padding: 0;
    margin: 0;
    text-align: center;
    height: 400px;
    width: 120px;
    flex: 0 0 auto;
    display: flex;
    flex-direction: column;
    flex-wrap: nowrap;
    background: rgb(60, 64, 66);
    box-shadow: 2px 0 4px rgb(30, 34, 36);
    z-index: 1;
    border-radius: 4px 0 0 4px;
  }

  #wrap .manset > .shadetabs > li
  {
    background: transparent;
    border: transparent;
    display: block;
    margin: 20px 0;
    padding: 0;
    height: 40px;
    width: 120px;
    flex: 0 0 auto;
  }

  #wrap .manset > .shadetabs > li > a
  {
    height: 40px;
    width: 120px;
    padding: 0;
    display: block;
    color: rgb(220, 220, 220);
    line-height: 40px;
    background: rgb(60, 64, 66);
    border-radius: 0;
    transition: .2s;
  }

  #wrap .manset > .shadetabs > li > a:hover
  {
    background: rgb(250, 120, 0);
    border-radius: 4px;
    color: white;
    box-shadow: 0 2px 4px rgb(20, 24, 26);
  }

  #wrap .tabcontent > .mansetlisteleme
  {
    float: unset;
    height: 400px;
  }

  #wrap .tabcontent > .mansetlisteleme > ul
  {
    float: unset;
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    flex: 0 0 auto;
    padding: 10px 0;
    background: rgb(40, 44, 46);
    margin: 0;
    height: 380px;
    width: unset;
    overflow: hidden;
    border-radius: 0 4px 4px 0;
  }

  #wrap .tabcontent > .mansetlisteleme > ul > li
  {
    background: transparent;
    float: unset;
    height: 50px;
    width: 48%;
    margin: 0 auto;
  }

  #wrap .tabcontent > .mansetlisteleme > ul > li > a
  {
    background: rgb(80, 84, 86);
    color: rgb(220, 220, 220);
    text-shadow: none;
    float: unset;
    padding: 10px;
    display: block;
    border-radius: 4px;
    transition: .2s;
    height: 20px;
  }

  #wrap .tabcontent > .mansetlisteleme > ul > li > a:hover
  {
    background: rgb(250, 120, 0);
    color: white;
    box-shadow: 0 4px 4px rgb(20, 24, 26);
  }

  #wrap #content > table > tbody > tr > td:nth-of-type(2)
  {
    display: none;
  }


/* Today's Anime */
#content > table > tbody > tr > td:nth-of-type(3) {width: 400px;}
#content > table > tbody > tr > td:nth-of-type(3) > table {height:300px !important;width:400px;background: rgb(50, 54, 56) !important;border-radius: 4px;}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(1) {display:none}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table {height:320px;width:400px;}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(1) > td {height:40px;width:400px}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(1) > td > .last {height:40px;width:400px;margin: 0;display: block;}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(1) > td > .last a {height:40px;width:400px;display: block;margin: 0;text-align: center;line-height: 40px;font-weight: 600;font-size: 16px;box-shadow: 0 2px 4px rgb(20, 24, 26);background:rgb(60, 64, 66);border-radius:4px 4px 0 0}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(2) > td {display:flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;height: 220px;background: rgb(40, 44, 46);padding: 10px 0;}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(2) > td > .catimg {height: 200px;width: 150px;margin: 10px;}
#content > table > tbody > tr > td:nth-of-type(3) > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr:nth-of-type(2) > td > .catimg > img {border: none;border-radius:4px}


/* Content Header bar */
html body > div#wrap > div#content > div:nth-of-type(2) {display:none}
html body > div#wrap > div#content > div:nth-of-type(3) {max-width: unset !important;float: unset !important;margin: 0 10px 0 10px !important;grid-area: 2/1/3/3;}
html  body > div#wrap > div#content > div:nth-of-type(3) > div:nth-of-type(1) {display:none}
#wrap .rr_header_bar {margin-top: 10px;}
#wrap .rr_header_bar > ul {background: rgb(60, 64, 66);border-radius: 4px;margin: 0;display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;justify-content: space-evenly;}
#wrap .rr_header_bar > ul > li {height: 40px;width: 120px;margin: 0 10px;}
#wrap .rr_header_bar > ul > li > a {background: rgb(60, 64, 66);padding: 0 10px;height: 40px;width: 120px;text-align: center;line-height: 40px;border-radius:4px;transition: .2s;color: rgb(220, 220, 220);}
#wrap .rr_header_bar > ul > li > a:hover {background:rgb(250, 120, 0);color:white}
#wrap .recent-release {margin-top: 20px;height: 60px;border-radius: 4px 4px 0 0;color: rgb(220, 220, 220);font-size: 20px;line-height: 60px;padding: 0 20px;font-weight: 600;background: rgb(40, 44, 46);text-align: center}
#wrap .sidebar_body {background:rgb(60, 64, 66);border:none;max-width: unset;border-radius: 0;padding: 0}
#wrap .sidebar_body > .items {margin:0 20px;padding: 0;display: flex;flex-direction: row;flex-wrap: wrap;flex: 0 0 auto;justify-content: space-around}
#wrap .sidebar_body > .items > li {width: 150px;height: 260px;display: flex;flex-direction: column;flex: 0 0 auto;flex-wrap: nowrap;justify-content: flex-start;margin: 10px 20px;padding: 10px 20px;background: rgb(30, 34, 36);border-radius: 4px;transition:.2s linear}
#wrap .sidebar_body > .items > li:hover {background:rgb(250, 120, 0)}
#wrap .sidebar_body > .items > li:hover > .recent-release-episodes > a {color:white}
#wrap .sidebar_body > .items > li > .img {height: 200px;width: 150px;position: unset;overflow: unset;margin:auto}
#wrap .sidebar_body > .items > li > .img:before {display: none}
#wrap .sidebar_body > .items > li > .img > a {position: unset;top: unset;left: unset;display: flex;flex-direction: column;flex-wrap: nowrap;flex: 0 0 auto;height: 200px;width: 150px;}
#wrap .sidebar_body > .items > li > .img > a > img {height: 200px;width: 150px;max-width: unset}
#wrap .sidebar_body > .items > li > .recent-release-episodes {padding: 0;height: 60px;margin:10px auto 0;overflow:hidden}
#wrap .sidebar_body > .items > li > .recent-release-episodes > a {display: block;height: 60px;width: 150px;color: rgb(220, 220, 220);}


/* Popular & Ongoing Series */
#content > div:nth-of-type(4) {grid-area: 2/3/3/4;float: unset !important;margin: 10px 0 0 0 !important;max-width: unset !important;width: 240px}
#wrap .bartitle {height: 40px;width: 240px;background: rgb(60, 64, 66);margin: 0;padding: 0;border-radius: 4px 4px 0 0;}
#wrap .bartitle > h3 {font-size: 14px;color: rgb(220, 220, 220);font-weight: 600;margin: 0;padding: 0;line-height: 40px;text-align: center;}
#content > div:nth-of-type(4) > table > tbody > tr:nth-of-type(2) > td {background: rgb(40, 44, 46) !important}
#wrap .menu {width: 240px;margin: 0;padding: 0;}
#wrap .menu > .menustyle {border: none;width: 240px;}
#wrap .menu > .menustyle > ul > li {width: 220px;margin: 10px;padding: 0;min-height: 20px;background: rgb(60, 64, 66);border: none;display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;border-radius:4px;}
#wrap .menu > .menustyle > ul > li > a {color: rgb(220, 220, 220);display: block;min-height: 10px;line-height: 14px; font-size:12px;font-weight: 600;height: 100%;width: 100%;padding: 8px 10px;transition:.2s}
#wrap .menu > .menustyle > ul > li > a:hover {background:rgb(250, 120, 0);color:white}
#wrap .menu > .menustyle > ul > li > a:visited {color:rgb(250, 120, 0)}
#wrap .menu > .menustyle > ul > li > a:hover:visited {color:white}
#wrap #content > div:nth-of-type(4) > table > tbody > tr:nth-of-type(3) {display:none}


/* Footer */
#wrap #content > div:nth-of-type(5) {display:none}
#wrap #footer {height: 40px;width: unset;background: rgb(60, 64, 66);margin: 10px 0 0 0;padding: 0;color: rgb(220, 220, 220);font-size: 12px;grid-area: 3/1/4/4;}
#wrap #footer > div:nth-of-type(1) {display:none}
#wrap #footer > table {margin: 0 !important;width: 100%;}
#wrap #footer > table > tbody > tr:nth-of-type(1) {display:none}
#wrap #footer > table > tbody > tr:nth-of-type(2) > td {height:40px;}
#wrap #footer > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr > td:nth-of-type(1) {display:none}
#wrap #footer > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr > td:nth-of-type(2) {width: 100%;display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;justify-content: center;}
#wrap #footer > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr > td:nth-of-type(2) > a {font-weight: 700;color: rgb(220, 220, 220);font-size: 12px;height: 20px;}



}

@-moz-document url-prefix("https://www.wcostream.org/dubbed-anime-list"), url-prefix("https://www.watchcartoononline.io/cartoon-list"), url-prefix("https://www.watchcartoononline.io/subbed-anime-list"), url-prefix("https://www.watchcartoononline.io/movie-list"), url-prefix("https://www.watchcartoononline.io/ova-list") {
/* Ads & Annoyances */
html body > div:not(#wrap) {display:none !important}
html *[style*="clear:both"] {display: none !important}
html .clear {display: none !important}

/* Main Background */
html body {background: rgb(30,34,36)}

/* Nav */
html body #wrap {width: 100%;margin: 0;}
html body #wrap .topb{background: rgb(30, 34, 36);position: fixed;width: 100%;height: 60px;border-bottom: 2px inset rgb(200, 80, 0);box-shadow: 0px 2px 8px rgb(10, 14, 16);z-index: 2;}
html body #wrap .topb > tbody > tr > td:nth-of-type(1){display: none;}
html body #wrap .topb > tbody > tr > td:nth-of-type(2) > table > tbody{display: flex;flex-direction: row;flex-wrap: nowrap;}
html body #wrap .topb > tbody > tr > td:nth-of-type(2) > table > tbody > tr:nth-of-type(1){display: none;}
html body #wrap .topb > tbody > tr > td:nth-of-type(2) > table > tbody > tr:nth-of-type(2){flex: 1 1 auto;}
html body #wrap #navbar{margin: 0;}
html body #wrap #navbar > #nav{display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;}
html body #wrap #navbar > #nav > li > a{padding: 0;height: 40px;width: 120px;line-height: 40px;margin: 10px 4px;text-align: center;color: rgb(220, 220, 220);border-radius: 4px;transition: .2s linear;}
html body #wrap #navbar > #nav > li > a:hover{background: rgb(250, 120, 0);color: white;}
html body #wrap .bh{display: none;}
html body #wrap .animesearch{background: rgb(30, 34, 36);padding: 0;margin: 0;height: 60px;}
html body #wrap .animesearch > form#konuara{display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;padding: 10px 0;}
html body #wrap .animesearch > form#konuara > input#searchbox{background: rgb(20, 24, 26);height: 40px;margin: 0;padding: 0 10px 0 10px;float: unset;border: none;color: rgb(220, 220, 220);}
html body #wrap .animesearch > form#konuara > .searchboxtop{margin: 0;}
html body #wrap .animesearch > form#konuara > .searchboxtop > select{padding: 0;height: 40px;width: 140px;text-align: center;border: none;background: rgb(40, 44, 46);box-shadow: none;color: rgb(220, 220, 220);font-weight: 600;}
html body #wrap .animesearch > form#konuara > .searchboxtop > select:hover{background: rgb(50, 54, 56);}


/* Content */
html body #wrap #content {background: rgb(30,34,36);width: 100%;margin: 0;}
html body #wrap #content > #content {padding-top:80px}

html body #wrap #content > table > tbody > tr > td:nth-of-type(2) {display:none}

html body #wrap #content > table > tbody > tr{display:grid;grid-template-columns:auto 250px}
html body #wrap #content > table > tbody > tr > td:nth-of-type(1) {grid-column:1/2}
html body #wrap #content > table > tbody > tr > td:nth-of-type(2) {grid-column:2/3}

/* Header */
html body #wrap .vsbaslik {background: transparent;font-size: 20px;height: 40px;width: 100%;}
html body #wrap .vsbaslik h2 {padding: 0;}
html body #wrap .vsbaslik h2 a {font-size: 24px;color: rgb(220,220,220);line-height: 40px;text-align: center;display: block;background: rgb(40,44,46);border-radius: 4px;}

/* List */
html body #wrap #sidebar_right {width: unset;background: rgb(40,44,46);margin: 10px 0;padding: 0;border: none;display: flex;flex-direction: column;flex-wrap: nowrap;width: 100%;}
html body #wrap #blog {width: unset;margin: 0;flex: 1 1 auto;}
html body #wrap #blog > div:not(.letter) {display:none !important}

html body #wrap #blog > .letter {margin: 0;background: rgb(60,64,66);}
html body #wrap #blog > .letter > ul {display: flex;flex-direction: row;flex-wrap: wrap;background:rgb(50,54,56)}
html body #wrap #blog > .letter > ul > li:nth-of-type(1) {padding: 0 !important;width: 100%;height: 40px;}
html body #wrap #blog > .letter > ul > li:nth-of-type(1) > span > a {padding: 0;display: block;text-align: center;width: 100%;line-height: 40px;font-size: 16px;color: white;background: rgb(60,64,66);}
html body #wrap #blog > .letter > ul > li {margin: 0;background: rgb(50,54,56);border: none;width: 3.7%;height:  30px;}
html body #wrap #blog > .letter > ul > li > a {height: 100%;width: 100%;padding: 0;font-size: 16px;color: rgb(220,220,220);line-height: 30px;}
html body #wrap #blog > .letter > ul > li > a:visited {color:rgb(250, 120, 0)}
html body #wrap #blog > .letter > ul > li > a:hover {background:rgb(250, 120, 0);color:white}

html body #wrap #ddmcc_container > .ddmcc {width: 100%;padding: 0;margin: 0;float: unset;}
html body #wrap #ddmcc_container > .ddmcc > ul {display: grid;grid-template-columns: 60px auto;margin: 0;}
html body #wrap #ddmcc_container > .ddmcc > ul > a {margin-top:-70px;padding-top:70px;z-index: -1;}
html body #wrap #ddmcc_container > .ddmcc > ul > p {grid-column: 1/2;height: unset;width: 60px;margin: 0;border: none;background: rgb(50, 54, 56);color: rgb(220, 220, 220);border-bottom:5px solid rgb(40,44,46)}
html body #wrap #ddmcc_container > .ddmcc > ul > ul {display: flex;flex-direction: column;flex-wrap: nowrap;flex: 0 0 auto;margin: 10px 10px;border: none;padding: 0;}
html body #wrap #ddmcc_container > .ddmcc > ul > ul > li {margin: 0;}
html body #wrap #ddmcc_container > .ddmcc > ul > ul > li > a {color: rgb(220, 220, 220);height: 20px;display: block;width: max-content;}
html body #wrap #ddmcc_container > .ddmcc > ul > ul > li > a:hover {color:rgb(220, 120, 0)}
html body #wrap #ddmcc_container > .ddmcc > ul > ul > li > a:visited {color:rgb(220, 120, 0)}
html body #wrap #content > div#content > table > tbody > tr > td:nth-of-type(2) {display:none}





/* Popular & Ongoing Series */
html body #wrap #sidebar {width: 240px;margin:0 10px}
html body #wrap #sidebar > table > tbody > tr:nth-of-type(1) > td {height: 40px;width: 240px;background: rgb(60, 64, 66);margin: 0;padding: 0;border-radius: 4px 4px 0 0;}
html body #wrap #sidebar > table > tbody > tr:nth-of-type(1) > td > h3 {font-size: 14px;color: rgb(220, 220, 220);font-weight: 600;margin: 0;padding: 0;line-height: 40px;text-align: center;}
html body #wrap #sidebar > table > tbody > tr:nth-of-type(2) > td {background: rgb(40, 44, 46) !important}
html body #wrap .menu {width: 240px;margin: 0;padding: 0;}
html body #wrap .menu > .menustyle {border: none;width: 240px;}
html body #wrap .menu > .menustyle > ul > li {width: 220px;margin: 10px;padding: 0;min-height: 20px;background: rgb(60, 64, 66);border: none;display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;border-radius:4px;}
html body #wrap .menu > .menustyle > ul > li > a {color: rgb(220, 220, 220);display: block;min-height: 10px;line-height: 14px; font-size:12px;font-weight: 600;height: 100%;width: 100%;padding: 8px 10px;transition:.2s}
html body #wrap .menu > .menustyle > ul > li > a:hover {background:rgb(250, 120, 0);color:white}
html body #wrap .menu > .menustyle > ul > li > a:visited {color:rgb(250, 120, 0)}
html body #wrap .menu > .menustyle > ul > li > a:hover:visited {color:white}
html body #wrap #sidebar > table > tbody > tr:nth-of-type(3) {display:none}

/* Footer */
html body #wrap #content > div:nth-of-type(5) {display:none}
html body #wrap #footer {height: 40px;width: unset;background: rgb(60, 64, 66);margin: 10px 0 0 0;padding: 0;color: rgb(220, 220, 220);font-size: 12px;grid-area: 3/1/4/4;}
html body #wrap #footer > div:nth-of-type(1) {display:none}
html body #wrap #footer > table {margin: 0 !important;width: 100%;}
html body #wrap #footer > table > tbody > tr:nth-of-type(1) {display:none}
html body #wrap #footer > table > tbody > tr:nth-of-type(2) > td {height:40px;}
