html body #wrap .animesearch > form#konuara > .searchboxtop{margin: 0;}
html body #wrap .animesearch > form#konuara > .searchboxtop > select{padding: 0;height: 40px;width: 140px;text-align: center;border: none;background: rgb(40, 44, 46);box-shadow: none;color: rgb(220, 220, 220);font-weight: 600;}
html body #wrap .animesearch > form#konuara > .searchboxtop > select:hover{background: rgb(50, 54, 56);}


/* Content */
html body #wrap #content {background: rgb(30,34,36);width: 100%;margin: 0;}
html body #wrap #content > #content {padding-top:80px}

html body #wrap #content > table > tbody > tr > td:nth-of-type(2) {display:none}

html body #wrap #content > table > tbody > tr{display:grid;grid-template-columns:auto 250px}
html body #wrap #content > table > tbody > tr > td:nth-of-type(1) {grid-column:1/2}
html body #wrap #content > table > tbody > tr > td:nth-of-type(2) {grid-column:2/3}

/* Header */
html body #wrap .vsbaslik {background: transparent;font-size: 20px;height: 40px;width: 100%;}
html body #wrap .vsbaslik h2 {padding: 0;}
html body #wrap .vsbaslik h2 a {font-size: 24px;color: rgb(220,220,220);line-height: 40px;text-align: center;display: block;background: rgb(40,44,46);border-radius: 4px;}

/* List */
html body #wrap .fontx {width:100%;margin: 0;}
html body #wrap #sidebar_right {width: unset;background: rgb(40,44,46);margin: 10px 0;padding: 0;border: none;display: flex;flex-direction: column;flex-wrap: nowrap;width: 100%;border-radius: 0 0 4px 4px;}
html body #wrap #blog {width: unset;margin: 0;flex: 1 1 auto;}

html body #wrap #blog > .letter {margin: 0;background: rgb(60,64,66);}
html body #wrap #blog > .letter > ul {display: flex;flex-direction: row;flex-wrap: wrap;background:rgb(50,54,56)}
html body #wrap #blog > .letter > ul > li:nth-of-type(1) {padding: 0 !important;width: 100%;height: 40px;}
html body #wrap #blog > .letter > ul > li:nth-of-type(1) > span > a {padding: 0;display: block;text-align: center;width: 100%;line-height: 40px;font-size: 16px;color: white;background: rgb(60,64,66);}
html body #wrap #blog > .letter > ul > li {margin: 0;background: rgb(50,54,56);border: none;width: 3.7%;height:  30px;}
html body #wrap #blog > .letter > ul > li > a {height: 100%;width: 100%;padding: 0;font-size: 16px;color: rgb(220,220,220);line-height: 30px;}
html body #wrap #blog > .letter > ul > li > a:visited {color:rgb(250, 120, 0)}
html body #wrap #blog > .letter > ul > li > a:hover {background:rgb(250, 120, 0);color:white}

html body #wrap #ddmcc_container > .ddmcc {width: 100%;padding: 0;margin: 0;float: unset;display: grid;grid-template-columns: 60px auto;border: none;}
html body #wrap #ddmcc_container > .ddmcc > ul > a {margin-top:-70px;padding-top:70px;z-index: -1;}
html body #wrap #ddmcc_container > .ddmcc > ul > p {grid-column: 1/2;height: 100%;width: 60px;margin: 0;border: none;background: rgb(50, 54, 56);color: rgb(220, 220, 220);border-bottom:5px solid rgb(40,44,46)}
html body #wrap #ddmcc_container > .ddmcc > ul {display: flex;flex-direction: column;flex-wrap: nowrap;flex: 0 0 auto;margin: 0;border: none;padding: 0;}
html body #wrap #ddmcc_container > .ddmcc > ul > li {margin: 0;}
html body #wrap #ddmcc_container > .ddmcc > ul > li:nth-of-type(1){margin-top:10px}
html body #wrap #ddmcc_container > .ddmcc > ul > li:last-of-type{margin-bottom:10px}
html body #wrap #ddmcc_container > .ddmcc > ul > li > a {color: rgb(220, 220, 220);height: 20px;display: block;width: max-content;margin:0 10px}
html body #wrap #ddmcc_container > .ddmcc > ul > li > a:hover {color:rgb(220, 120, 0)}
html body #wrap #ddmcc_container > .ddmcc > ul > li > a:visited {color:rgb(220, 120, 0)}
html body #wrap #content > div#content > table > tbody > tr > td:nth-of-type(2) {display:none}

html body #wrap #blog > div > h1 {height:40px;text-align: center;color:rgb(250,120,0);font-size:24px;line-height:40px;font-weight:600}




/* Popular & Ongoing Series */
html body #wrap #sidebar {width: 240px;margin:0 10px}
html body #wrap #sidebar > table > tbody > tr:nth-of-type(1) > td {height: 40px;width: 240px;background: rgb(60, 64, 66);margin: 0;padding: 0;border-radius: 4px 4px 0 0;}
html body #wrap #sidebar > table > tbody > tr:nth-of-type(1) > td > h3 {font-size: 14px;color: rgb(220, 220, 220);font-weight: 600;margin: 0;padding: 0;line-height: 40px;text-align: center;}
html body #wrap #sidebar > table > tbody > tr:nth-of-type(2) > td {background: rgb(40, 44, 46) !important}
html body #wrap .menu {width: 240px;margin: 0;padding: 0;}
html body #wrap .menu > .menustyle {border: none;width: 240px;}
html body #wrap .menu > .menustyle > ul > li {width: 220px;margin: 10px;padding: 0;min-height: 20px;background: rgb(60, 64, 66);border: none;display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;border-radius:4px;}
html body #wrap .menu > .menustyle > ul > li > a {color: rgb(220, 220, 220);display: block;min-height: 10px;line-height: 14px; font-size:12px;font-weight: 600;height: 100%;width: 100%;padding: 8px 10px;transition:.2s}
html body #wrap .menu > .menustyle > ul > li > a:hover {background:rgb(250, 120, 0);color:white}
html body #wrap .menu > .menustyle > ul > li > a:visited {color:rgb(250, 120, 0)}
html body #wrap .menu > .menustyle > ul > li > a:hover:visited {color:white}
html body #wrap #sidebar > table > tbody > tr:nth-of-type(3) {display:none}

/* Footer */
html body #wrap #content > div:nth-of-type(5) {display:none}
html body #wrap #footer {height: 40px;width: unset;background: rgb(60, 64, 66);margin: 10px 0 0 0;padding: 0;color: rgb(220, 220, 220);font-size: 12px;grid-area: 3/1/4/4;}
html body #wrap #footer > div:nth-of-type(1) {display:none}
html body #wrap #footer > table {margin: 0 !important;width: 100%;}
html body #wrap #footer > table > tbody > tr:nth-of-type(1) {display:none}
html body #wrap #footer > table > tbody > tr:nth-of-type(2) > td {height:40px;}
html body #wrap #footer > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr > td:nth-of-type(1) {display:none}
html body #wrap #footer > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr > td:nth-of-type(2) {width: 100%;display: flex;flex-direction: row;flex-wrap: nowrap;flex: 0 0 auto;justify-content: center;}
html body #wrap #footer > table > tbody > tr:nth-of-type(2) > td > table > tbody > tr > td:nth-of-type(2) > a {font-weight: 700;color: rgb(220, 220, 220);font-size: 12px;height: 20px;}


}
