/* ==UserStyle==
@name           Mokum.Place - NEW - 10/22/2019, 4:36:07 PM
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         enginhu
==/UserStyle== */

@-moz-document domain("mokum.place") {
    body{
        background:#3a3a3a;
        color: #ddd;
        font-family:'Ubuntu', sans-serif !important;
    }
    .row {
        display: flex;
        flex-direction: row-reverse;
    }
    div.row:nth-child(2){
        display:flex;
        flex-direction:row
    }
    .bem-signin-block_header{
        float:left;
    }
    .bem-sidebar{
        border-right: 1px solid #555;
    }
    .box-body{
        background:#3a3a3a !important;
    }
    .bem-post{
        background: #3a3a3a;
        color:#afaca7;  
    }
    .bem-post:hover{
        background:#404040;
    }
    .bem-page-post{
        border-bottom: 1px solid #555;
    }
    .bem-post__author-name{
        color:#ffa666
    }
    .bem-sidebar-userinfo__username a:hover{
        color:#a2723a;
    }
    .bem-post__author-name:hover{
        color:#a2723a
    }
    .bem-post__comment-text{
        color:#a0a0a0
    }
    .bem-post__likes-list{
        color:#9e9e9e;
    }
    a{
        color:#b38e40;
    }
    a:hover{
        color:#e6712d;
    }
    a:visited{
        color:#e89f22;
    }
    a:active{
        color:#e89f22;
    }
    a:link{
        color:#e89f22;
    }
    .bem-post__button {
        padding:5px;
        background: #5e3d05;
        color:#eee;
    }
    .bem-sidebar-menu__items a{
        color:#a2723a;
        text-transform:lowercase;
    }
    .bem-sidebar-userinfo__display-name{
        color: #999;
    }
    .bem-sidebar-menu__title{
        color:#a69c85;
        border-bottom:1px solid #555;
    }
    .bem-sidebar-menu__divider{
        border-bottom:1px solid #555;
    }
    .bem-sidebar-menu__show-button {
        color:#805307;
        margin-right:4px;
        padding:2;        
        background:#2d2d2d;
        text-transform: lowercase;
    }
    .Select-control{
        background:#3a3a3a;
    }
    .value-label{
        color:#afaca7;;
    }
    .bem-posting-form__pane{
        color:#afaca7;
    }
    .box-header-timeline{
        color:#8a8a8a !important
    }
    .btn-default{
        background:#5e3d05;
        color:#ddd
    }
    .search-input {
        color:#eee;
    }
    .bem-mokum-logo__link{
        color:#8a8a8a;
        font-size:30px;
        text-transform:lowercase;
    }
    .bem-mokum-logo__link:hover{
        color:#ddd;
    }
    .bem-mokum-logo__logo > a:nth-child(1) > small:nth-child(1){
        display:none;
    }
    .bem-page_logo-row {
        position: sticky;
        top: 0;
        z-index: 10;
        background-color: #3a3a3a;
        border-bottom: 1px solid #dddf;
    }
    .profile{
        color:#999;
    }
    .profile a{
        color:#a2723a;
    }
    .sentence {
        color:#999;
    }
    h2{
        color:#777;
    }
    .box-footer{
        background:#555;
    }
    .tile-list a:hover {
        color:#ddd;
    }
    .bootstrap-switch-label {
        background:#333 !important;
    }
    .bootstrap-switch-handle-on.bootstrap-switch-primary {
        background-color: #333 !important;
    }
    .bem-sidebar .bem-live-selector .bootstrap-switch-handle-on {
        color: #ddd;;
    }
    .bem-post__likes-counter-icon_liked {
        color:#b38e40;
    }
    .content .box-footer {
        background-color:#3a3a3a;
    }
    .bem-page__page-footer .bem-page-footer {
        color:#ddd !important;
    }
    .bem-post__posted-to_feeds {
        color: #9f9f9f;
    }
    .table-striped > tbody > tr:nth-of-type(2n+1) {
        background-color: #4d4d4d;
    }
    .bg-warning {
        background-color: #2d2d2d;
    }
    .bem-post__posted-to_direct-to {
        color:#e33535;
    }
    .bem-central-block_title {
        color: #ddd;
    }
    .bem-mokum__link-button_blue {
        color: #ddd;
    }
    .bem-page_central-block {
        background: #3a3a3a;
    }
    .bem-central-block__title {
        color: #ddd;
    }
@media screen and (max-width: 600px) {
  .row {
    display:block;
  }
    div.row:nth-child(2) {
    display: block;
    }
    .bem-phone-navigation__button {
    color: #e89f22;
    }
    .bem-post__button {
    padding: 2px;
    }
}
}
