style.css:
@-moz-document domain("chat.iconnectguam.com") {
.sidebar, .sidebar *, .sidebar__header, .rooms-list, .sidebar-footer, .sidebar-footer * {
    background-color: black;
    color: white !important;
}

.sidebar-item--active, .sidebar-item--active * {
    background-color: black;
    color: #2ecc71 !important;
}

.rc-old .page-container, .rc-old .page-container .content {
    background-color: black;
    color: white !important;
}

.rc-header, .rc-header__wrap, .rc-header__data, .wrapper {
    background-color: black;
    color: white !important;
}

.message.new-day:after, .message.new-day:before{
    background-color: black;
    color: #bdc3c7 !important;
    border-color: #bdc3c7 !important;
}

.color-primary-font-color , .message {
    background-color:  black !important;
    color: white !important;
}

.rc-popover__content, .rc-popover__content span {
    background-color: black;
    color: white;
}

.contextual-bar, .contextual-bar__header {
    background-color: black;
    color: white !important;
}

input, select, textarea, .rc-member-list__counter, .thread-list, .contextual-bar__content {
    background-color: black;
    color: white !important;
}

.footer, .rc-message-box, .rc-message-box textarea {
    background-color: black;
    color: white !important;
}

.emoji-picker {
    background-color: black;
    color: white !important;
}

.rc-modal {

    background-color: black;
    color: white !important;
}

.role-tag {
    background-color: #8e44ad !important;
    color: white !important;
    border: none;
}

.rc-message-box__typing-user {
    color: white !important;
}

.sidebar-item__user-status--online, .sidebar__header-status-bullet--online {
    background-color: #2ecc71;
}

.sidebar-item__user-status--away, .sidebar__header-status-bullet--away {
    background-color: #f1c40f;
}

.sidebar-item__user-status--offline, .sidebar__header-status-bullet--offline {
    background-color: #95a5a6;
}

.sidebar-item__user-status--busy, .sidebar__header-status-bullet--busy {
    background-color: #f5455c;
}

.rc-popover__item--online .rc-popover__icon {
    color: #2ecc71 !important;
}

.avatar {
    border-radius: 20px;
}

.message .reactions > li {
    border: none;
    background-color: #2c3e50;
    color: white;
}

.message:hover, .message:hover * {
    background-color: black !important;
    color: #bdc3c7 !important;
}

.rc-input__icon {
    background-color: transparent;
}

.message a {
    color: #2ecc71;
}

.message .reactions > li.selected {
    border: none;
    background-color: #2980b9;
    color: white;
}

.rc-old .message-popup-title {
    border-color: black;
}

.rc-old .message-popup-position .message-popup, .rc-old .message-popup-title {
    background-color:  black !important;
    color: white !important;
}

.rc-message-box__container.editing {
    background-color:  black !important;
}

.rc-icon {
    fill: #2ecc71;
}
}
