style.css:
@-moz-document domain("discogs.com") {
/* don't attempt to show Gravatar or missing Discogs avatar images at all, so when Gravatar is blocked, we won't see alt text and broken image icons */
img[src*="gravatar.com/avatar/"],
img[src$="/.jpg"] {
    display: none !important;
}

/* to prevent the non-displaying avatar thumbnail from collapsing the containing element, make that element be as wide as its container */
a[href^="/user/"] .thumbnail_center {
    width: 100% !important;
}

/* make exceptions for the //www.discogs.com/team page, because its layout is harder to work around */
.team img {
    width: 150px !important;
    height: 150px !important;
    display: block !important;
}
.team li {
    width: 150px !important;
    height: 200px !important;
}

/* where Gravatar images normally appear, fill the background with a generic avatar image, centered and sized to fit in that container */
.team li,
.user_avatar,
.user-avatar,
a[href^="/user/"] span.thumbnail_center {
    background-image: url(data:image/png;base64,) !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
    background-size: contain !important;
}

/* on the team page, the avatar needs to be at the top of the container, not the center */
.team li {
    background-position: top !important;
}
}
