style.css:
@-moz-document domain(aulis.hs-bremen.de) {

:root {
    --back-darkest: #0e0e0e;
    --back-dark: #1d1d1d;
    --back-darker: #151515;
    --gray: #636363;
    --accent: #e27d00;
}
body {
    background-color: var(--back-darkest);
    color: #e4e4e4;
}
footer.ilFooter {
    background-color: var(--back-dark);
    /*position: fixed;*/
}
div.ilTopBar {
    background-color: var(--back-dark);
    color: #1d1d1d;
}
#ilTopBarNav > li > a {
    background-color: var(--back-dark);
}
#ilTopBarNav > li > a:hover {
    background-color: #353535;
}
#ilTopBarNav > .active > a, #ilTopBarNav > .open > a, #ilTopBarNav > .active > a:hover, #ilTopBarNav > .open > a:hover, #ilTopBarNav > .active > a:active, #ilTopBarNav > .open > a:active, #ilTopBarNav > .active > a:focus, #ilTopBarNav > .open > a:focus {
    background-color: #353535;
}
#ilTopBarNav .dropdown-menu {
    background-color: #353535;
}
.ilMainHeader {
    background-color: var(--back-darker);
    border-bottom: 1px solid #1d1d1d;
}
div#il_startup_logo img, .ilMainHeader > header > .row > a.navbar-brand > img {
    filter: contrast(0) brightness(10);
    opacity: 0.7;
}
.normaltext_mmcc {
    color: var(--gray);
}
.normaltext_mmcc>br:first-child {
    display: none;
}
a, a.light:link, a.light:visited {
    color: var(--accent);
}
a:hover {
    color: var(--accent);
}
.text_hoch, .text_tief {
    color: var(--gray);
}
.ilStartupSection {
    display: none;
}
.form-horizontal {
    background-color: var(--back-darker);
}
.ilFormHeader, .ilFormFooter {
    background-color: var(--back-darker);
}
h3.ilHeader {
    padding-left: 20px;
    color: var(--accent);
}
.form-control {
    background-color: var(--back-dark);
    color: #ffffff;
}
.form-horizontal label, .small {
    color: var(--gray);
}
input.btn {
    margin-right: 15px;
    margin-bottom: 15px;
}
.btn-default {
    background-color: var(--back-dark);
    border: 1px solid #353535;
}
.btn-default:hover {
    background-color: #353535;
    border: 1px solid #555555;
}
#ilTopNav .navbar-nav > li > a {
    border-top: 0px solid var(--accent);
}
#ilTopNav .navbar-nav > .active > a, #ilTopNav .navbar-nav > .open > a, #ilTopNav .navbar-nav > .active > a:hover, #ilTopNav .navbar-nav > .open > a:hover, #ilTopNav .navbar-nav > .active > a:active, #ilTopNav .navbar-nav > .open > a:active, #ilTopNav .navbar-nav > .active > a:focus, #ilTopNav .navbar-nav > .open > a:focus {
    border-top: 2px solid var(--accent);
    background-color: var(--back-dark);
}
#ilTopNav .dropdown-menu, #ilTopBarNav .dropdown-menu {
    background-color: var(--back-dark);
}
.dropdown-header {
    background-color: var(--back-darker);
    padding-top: 8px;
    padding-bottom: 8px;
    text-align: center;
}
#ilTopNav .dropdown-menu li a, #ilTopBarNav .dropdown-menu a {
    color: #808080;
}
#ilTopBarNav > li > a {
    color: #808080;
}
#ilTopNav .dropdown-menu li a:hover, #ilTopBarNav .dropdown-menu a:hover {
    background-color: #353535;
    color: #ccc;
}
.dropdown-menu .divider {
    background-color: #353535;
}
.badge {
    background-color: #353535;
}
a#il_mhead_t_focus {
    color: var(--accent);
}
div.il_Block, table.il_Block, div.ilBlockInfo {
    background-color: var(--back-dark);
}
div.ilBlockHeader, div.ilBlockHeaderBig, td.ilBlockHeader, td.ilBlockHeaderBig, .ilBlockRow1, .ilBlockRow2 {
    border-bottom: 1px solid #353535;
}
.btn-link {
    color: #353535;
}
.ilBlockContent .il-viewcontrol-section .btn.btn-default {
    color: #808080;
}
table.calmini, table.calmini tr:nth-child(odd) {
    background-color: var(--back-darkest);
}
table.calmini tr:nth-child(even) {
    background-color: var(--back-darker);
}
table.calmini td.calminitoday {
    background-color: var(--accent);
    border: 0px solid var(--accent);
}
#il_center_col div.il_Block {
    background-color: var(--back-dark);
}
.ilObjListRow {
    background-color: var(--back-dark);
    border-bottom: 1px solid #353535;
}
.ilObjListRow:hover {
    background-color: var(--back-darker);
}
h3.ilPDBlockSubHeader {
    color: #808080;
}
.ilAwarenessItem {
    border-bottom: 1px solid #353535;
    background-color: var(--back-dark);
}
#awareness-content .media:hover {
    background-color: var(--back-darker);
}
.ilAwarenessDropDown .media-left {
    opacity: 1;
}
div[class^="il_editarea"] .ilContainerListItemOuter img, img.ilListItemIcon {
    opacity: 0.7;
}
.form-control {
    border: 1px solid #353535;
}
.btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
    background-color: var(--back-darker);
    border: 1px solid #808080;
}
.btn-default:active:hover, .btn-default.active:hover, .open > .dropdown-toggle.btn-default:hover, .btn-default:active:focus, .btn-default.active:focus, .open > .dropdown-toggle.btn-default:focus, .btn-default:active.focus, .btn-default.active.focus, .open > .dropdown-toggle.btn-default.focus {
    background-color: var(--back-darker);
    border: 1px solid #808080;
}
.dropdown-menu {
    background-color: var(--back-darker);
}
.dropdown-menu li > a:hover, .dropdown-menu li > a:focus {
    background-color: var(--back-dark);
    color: #808080;
}
span.ilNewsRssIcon {
    background-color: transparent;
    border: 1px solid var(--accent);
}
.ilLeftNav, a.ilTreeView {
    background-color: var(--back-dark);
}
a.ilTreeView {
    border: 1px solid #353535;
}
a.ilTreeView > img {
    background-color: var(--back-dark);
    border: none;
}
.jstree.jstree-default a {
    color: #808080 !important;
}
.jstree img, img.ilIcon {
    opacity: 0.7;
}
a.il_HighlightedNode, .ilHighlighted {
    background-color: transparent;
    color: var(--accent);
    padding: 0;
}
.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    background-color: var(--back-dark);
    color: #ccc;
}
.nav > li > a:hover, .nav > li > a:focus {
    background-color: var(--back-darker);
}
.btn-primary {
    background-color: #224205;
    border-color: #3a6315;
    color: #a0d689;
}
.ilInfoScreenSec.form-horizontal .form-group, .il_InfoScreenPropertyValue {
    background-color: var(--back-dark);
}
div.ilProfile {
    background-color: var(--back-darker);
    border-color: var(--back-dark);
}
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: var(--back-dark);
    color: #ccc;
}
#current_perma_link {
    color: #808080;
}
.popover {
    background-color: #353535;
}
.popover.bottom > .arrow:after {
    border-bottom-color: #353535;
}
#awareness-content .arrow-down {
    border-top: 11px solid #353535;
}
.ilAwarenessDropDown .media-body.ilAwarenessOnline {
    filter: hue-rotate(290deg) contrast(1.5);
}
.ilToolbar.navbar {
    background-color: var(--back-dark);
}
input, button, select, textarea {
    background-color: var(--back-dark);
    border: 1px solid #353535;
    padding: 4px;
}
.breadcrumb > li + li:before {
    color: #808080;
}
div.ilListItemSection {
    color: var(--gray);
}
.alert-success {
    background-color: #224205;
    border-color: #3a6315;
    color: #a0d689;
}
.alert-warning {
    background-color: #5f280a;
    border-color: #ab4513;
    color: #e68f74;
    font-weight: normal;
    padding: 15px;
}
div.ilTableOuter, div.ilTableCommandRow, div.ilTableCommandRowTop, .ilTableOuter .table {
    background-color: var(--back-dark);
}
.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: var(--back-darker);
}
.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top: 1px solid #353535;
}
.table > thead > tr > th {
    background-color: var(--back-dark);
    border-bottom: 1px solid #353535;
}
div.ilTableSelectAll {
    background-color: var(--back-dark);
}
input:invalid {
    background-color: #5f02023b;
    border-color: #8e2a2a;
}
}
