style.css:
@-moz-document domain("forum.cifraclub.com.br") {
.containerLogin h1, .voto {filter: invert(100%);}
.voto b {color:#222 !important;}
table.forums, table.frs {border-collapse:inherit;border-color:#333;}
.center, table.forums, table.frs, .ordenar {width:90%;}
body.gbody,.tbTransparent, #resposta textarea.input { background: #222; }
table.forums, table.frs, .pergunta td, #resposta, .caption4, .tbCel1 { background: #222222; }
#postar_topico .input:focus, #resposta .input:focus, #topo .input:focus, form#senha .input:focus, #rodape_foruns, #rodape, #rodape_inst, .ccid_bar, .ccid_user_name.on, .ccid_user_name:hover, #postar_topico .input, #resposta .input, #topo .input, form#senha .input, .ccid_nav a:hover, .ccid_user_drop {
    background: #222;
}
.banner_form {filter:invert(50%);}
#resposta form div a img { background: #fff }
.tbCel2 { background: #292929;}
#resposta textarea.input, #titulo_topico {width: 60%;}
table.forums, table.frs, .pergunta td, #resposta, #postar_topico .input:focus, #resposta .input:focus, #topo .input:focus, form#senha .input:focus, #resposta textarea.input, .tbTransparent, .ccid_user_drop, .votos, .gbody .title {
    color: #eee !important;
}
.caption1 i, .caption1 b { color: #f4f4b7; }
.caption1, .caption4 {border-color:transparent;}

.gbody a, .gbody a:visited, #rodape .online, #rodape .recorde, #rodape_foruns .numeros, #rodape .cadastrados { color:#ff9929;}
.gbody a:hover { color: #ff5a11; }
#postar_topico .bt, #resposta .bt, #topo .bt, form#senha .bt {
    border-color: #cc6848;
    color: #222;
    text-shadow: 0 -1px 0 #cc7d48;
    background: #cc6648;
    background: -moz-linear-gradient(top,#e08f69,#cc6648);
    background: -webkit-linear-gradient(top,#e08f69,#cc6648);
    background: linear-gradient(top,#e08f69,#cc6648);
    box-shadow: 0 1px 0 #e08f69 inset;
}
}
