style.css:
@-moz-document domain("beon.ru"), domain("mindmix.ru"), domain("ltalk.ru"), domain("carguru.ru"), domain("car-guru.com") {
:root {
    /*[[color-theme]]*/
}

.logo img {
    filter: saturate(0%) contrast(120%);
}

td, span, i, b, div, font {
    font-family: 'Calibri'!important;
}

.hrline {
    border: 1px solid #555555;
}

.crumbsbg {
    font-size: 20px!important;
}

.menubg:not(.mc_categories) a, .lefttopicparams div, .righttopicparams div, .hiddenonsmallscreen td, a.user, .commentbg span {
    font-size: 14px!important;
}

.m3, .o, .menuh, .opt, .m6 {
    font-size: 12px!important;
    color: #c0c0c0;
}

.m1, .m2, .m7, .gr, .blog_signature, .pmctime {
    font-size: 12px!important;
    color: #787878;
}

.postbg {
    font-size: 20px!important;
    color: #c0c0c0;
    padding: 0 10px 10px 10px;
    box-shadow: 0 0 4px 2px rgba(0,0,0,0.1);
    margin: 10px 0 10px 0!important;
}

.postbg td[colspan="2"] {
    padding: 2px 0 0 5px;
}

.datebg {
    border: 1px dotted var(--color_a)!important;
    margin: 20px 0 20px 0;
}

.commentbg {
    padding: 5px!important;
}

.commentbg td[colspan="2"] {
    padding-left: 5px!important;
    font-size: 14px!important;
    color: #c0c0c0;
}

.blog_title, .datesc, body[bgcolor="FFFFFF"] div {
    color: #c0c0c0!important;
}

body[bgcolor="FFFFFF"] input {
    margin-top: 5px;
}

#add_file, #add_link {
    margin-left: 5px;
}

.menubg, .info_container {
    box-shadow: 0 0 4px 2px rgba(0,0,0,0.1);
}

.chooseavatar img:not(:first-child) {
    margin-top: 5px;
}

.topic_title {
    font-size: 25px!important;
}

.bodybg, .headbg, .crumbsbg, .bottombg, .info_container, body[bgcolor="FFFFFF"], .lt {
    background: none;
    border: none;
    background-color: #333333!important;
    color: #c0c0c0!important;
    text-shadow: 0px 0px 5px rgba(0,0,0,0.3);
}

.datebg, .postbg, .commentbg, .textareabg, .chooseavatar>div, .div_user_img, .user_footer{
    border: none;
    background: none;
    background-color: #393939!important;
    color: #c0c0c0!important;
}

.tinycommsform {
    background-color: #333333!important;
}

#add_tags_div, #menu_div {
    background: #222;
}

.lb {
    margin-top: 10px;
    border: 1px solid #555555;
    background: #3f3f3f;
    font-size: 18px;
}

.lb a:link, .lb a:visited {
    color: var(--color_a);
}

input, select, textarea {
    border: 1px solid #686868;
    background: #4f4f4f;
    color: #eee;
}

.mood {
    background: #4f4f4f url('http://beon.ru/i/mood.png') no-repeat scroll 3px 1px!important;
    margin-left: 5px;
}
.music {
    background: #4f4f4f url('http://beon.ru/i/music.png') no-repeat scroll 3px 1px!important;
    margin-left: 5px;
}
.want {
    background: #4f4f4f url('http://beon.ru/i/want.png') no-repeat scroll 3px 1px!important;
    margin-left: 5px;
}
.tags {
    background: #4f4f4f url('http://beon.ru/i/tags.png') no-repeat scroll 3px 1px!important;
    margin-left: 5px;
}

.righttopicparams select, select[name="topic_options"], select[name="premoderate"] {
    margin-left: 5px;
}

.mheadred, .menuh, .info_header {
    background: none;
    background-color: #292929!important;
    border: none;
}

.menubg {
    border: none;
    background: #3d3d3d;
}

font[color="0048AB"], font[color="cc3300"] {
    color: var(--color_a);
}

a:link, A {
  color: var(--color_a);
}

a:visited, .tag:visited {
  color: var(--color_visited);
}

a:hover, a:active {
  color: var(--color_hover);
}

/*Мессенджер*/
td[id^="tab_"]:not(.activetab), td[id^="ctab"]:not(.activetab) {
    background: #424242!important;
    background-color: #424242!important;
    color: #c0c0c0!important
}

td[id^="tab_"].activetab, td[id^="ctab"].activetab {
    background: #666!important;
    background-color: #666!important;
    color: #c0c0c0!important
}

tr[id^="row"]:not(.rcurrent) {
    background: #424242!important;
    background-color: #424242!important;
    color: #c0c0c0!important
}

tr[id^="row"].rcurrent {
    background: #555!important;
    background-color: #555!important;
    color: #c0c0c0!important
}

.lasttab {
    background: #393939!important;
    background-color: #393939!important;
    color: #c0c0c0!important
}

.pm_arr {
    background: #393939 url('http://beon.ru/i/a_right.png') repeat-y;
}

.pm_arr:hover {
    background: #454545 url('http://beon.ru/i/a_right.png') repeat-y;
}
}

@-moz-document url-prefix("http://beon.ru/"), url-prefix("http://mindmix.ru/"), url-prefix("http://ltalk.ru/"), url-prefix("http://carguru.ru/"), url-prefix("http://car-guru.com/") {
.datebg {
        margin: 0px!important;
    }

    .postbg:not(:first-child) {
        font-size: 20px!important;
        color: #c0c0c0;
        padding: 0 10px 10px 10px;
        box-shadow: 0 0 4px 2px rgba(0,0,0,0.1);
        margin: 50px 0 10px 0!important;
    }
}
