style.css:
@-moz-document domain("twitch.tv") {
/* Site Settings */

* {
  letter-spacing: 1px !important;
  text-decoration: none !important;
  transition: none !important;
}

.lnnKUr {
  text-transform: capitalize !important;
}

.ScWrapper-sc-1wvuch4-0:hover .dJYDVl.dJYDVl,
.ScWrapper-sc-1wvuch4-0:hover .fNwmtl.fNwmtl,
.ScWrapper-sc-1wvuch4-0:hover .gEBqEV.gEBqEV,
.ScWrapper-sc-1wvuch4-0:hover .gMwbGx.gMwbGx,
.ScWrapper-sc-1wvuch4-0:hover .jhgGdR.jhgGdR {
  transform: none !important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.stream-display-ad__wrapper,

[data-a-target="browse-banner-ad-slot"],
[data-a-target="browse-card-ad-slot"],
[data-a-target="directory-card-ad-slot"],
[data-a-target="frontpage-headliner-layout"],
[title="Display Ad"] {
  left: -9999px !important;
  position: absolute !important;
  top: -9999px !important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.featured-content-carousel-featured-item,
.featured-content-carousel-featured-item--mischief {
  height: 32rem;
}

/* Widths 
_____________________________________________________________________________________________________*/

.side-nav {
  width: 32rem !important;
}

.bmSivE {
  width: 3rem;
}

}

@-moz-document domain("twitch.tv") {
/* Font Settings */

a[href] {
  color: #0ff !important;
  text-shadow: 1px 1px #000 !important;
}

a[href]:visited {
  color: #fc79fc !important;
  text-shadow: 1px 1px #000 !important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

[data-a-target="chat-send-button"]:hover,
[data-a-target="login-button"]:hover,
[data-a-target="side-nav-title"],
[data-a-target="signup-button"]:hover,
a[href]:hover,
h1,
input[type="checkbox"]:checked + label,
input[type="radio"]:checked + label {
  color: #ff0 !important;
}

/* White 
_____________________________________________________________________________________________________*/

[data-a-target="side-nav-game-title"] p,
body,
li,
p {
  color: #fff !important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.prime-offer__title__markdown p,

h1,
h2 {
  color: #f0e890 !important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.EpXQV,

h3,
h4 {
  color: #e0b880 !important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label {
  color: #f0a060 !important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time {
  color: #07beff !important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector {
  color: #07ff24 !important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector {
  color: #ff0707 !important;
}

/*
_____________________________________________________________________________________________________*/

[data-a-target="subscribe-button"],
input,
input[type="email"],
input[type="number"],
input[type="password"],
input[type="search"],
input[type="tel"],
input[type="text"],
select,
textarea {
  background-color: #5d1b1b !important;
  color: #fff !important;
}

/*
_____________________________________________________________________________________________________*/

::selection {
  background: #ff9 !important;
  color: #000 !important;
}

pre ::selection {
  background: #ff9 !important;
  color: #fff !important;
}

::placeholder {
  color: #f48f8f !important;
  opacity: 1;
}

* {
  --color-accent: #0000;
}

}

@-moz-document domain("twitch.tv") {
/* Background Settings */

body,
html {
  background: #000 !important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.iSvTkJ {
  background: #0000 !important;
  background-color: #0000 !important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.gJUZvG,
.krxbSe {
  background: radial-gradient(circle, #0a587fa8 0%, #000 100%) !important;
  border-radius: 22px !important;
  border: none !important;
  box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d,
    inset -2px -3px 5px #ffffff80 !important;
  color: #fff !important;
  opacity: 1 !important;
  position: relative !important;
  z-index: 1 !important;
}

.gJUZvG::before,
.krxbSe::before {
  background: radial-gradient(circle, #000 0%, #0a587f 100%) !important;
  border-radius: 22px !important;
  bottom: 0;
  color: #ff0 !important;
  content: "";
  left: 0;
  opacity: 0 !important;
  position: absolute !important;
  right: 0;
  top: 0;
  transition: opacity 0.8s linear !important;
  z-index: -1 !important;
}

.gJUZvG:hover::before,
.krxbSe:hover::before {
  opacity: 1 !important;
}

/* Red 
_____________________________________________________________________________________________________*/

[data-a-target="player-overlay-mature-accept"] {
  background: radial-gradient(circle, #870101a8 0%, #000 100%) !important;
  border-radius: 22px !important;
  border: none !important;
  box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d,
    inset -2px -3px 5px #ffffff80 !important;
  color: #fff !important;
  opacity: 1 !important;
  position: relative !important;
  z-index: 1 !important;
}

[data-a-target="player-overlay-mature-accept"]::before {
  background: radial-gradient(circle, #000 0%, #870101 100%) !important;
  border-radius: 22px !important;
  bottom: 0;
  box-shadow: 1px 1px 1px black, inset 2px 3px 5px #ffffff80,
    inset -2px -3px 5px #0000004d !important;
  color: #ff0 !important;
  content: "";
  left: 0;
  opacity: 0 !important;
  position: absolute !important;
  right: 0;
  top: 0;
  transition: opacity 0.8s linear !important;
  z-index: -1 !important;
}

[data-a-target="player-overlay-mature-accept"]:hover::before {
  opacity: 1 !important;
}

/* Purple 
_____________________________________________________________________________________________________*/

.eVWnXL,

[aria-label="Follow"],
[data-a-target="chat-send-button"],
[data-a-target="game-directory-follow-button"],
[data-a-target="signup-button"] {
  background: radial-gradient(circle, #5c16c5a8 0%, #000 100%) !important;
  border-radius: 22px !important;
  border: none !important;
  box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d,
    inset -2px -3px 5px #ffffff80 !important;
  color: #fff !important;
  opacity: 1 !important;
  position: relative !important;
  z-index: 1 !important;
}

.eVWnXL::before,

[aria-label="Follow"]::before,
[data-a-target="chat-send-button"]::before,
[data-a-target="game-directory-follow-button"]::before,
[data-a-target="signup-button"]::before {
  background: radial-gradient(circle, #000 0%, #5c16c5 100%) !important;
  border-radius: 22px !important;
  bottom: 0;
  box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d,
    inset -2px -3px 5px #ffffff80 !important;
  color: #ff0 !important;
  content: "";
  left: 0;
  opacity: 0 !important;
  position: absolute !important;
  right: 0;
  top: 0;
  transition: opacity 0.8s linear !important;
  z-index: -1 !important;
}

.eVWnXL:hover::before,

[aria-label="Follow"]:hover::before,
[data-a-target="chat-send-button"]:hover::before,
[data-a-target="game-directory-follow-button"]:hover::before,
[data-a-target="signup-button"]:hover::before {
  opacity: 1 !important;
}

/* Green 
_____________________________________________________________________________________________________*/

[data-a-target="login-button"] {
  background: radial-gradient(circle, #066503a8 0%, #000 100%) !important;
  border-radius: 22px !important;
  border: none !important;
  box-shadow: 1px 1px 1px black, inset 3px 4px 5px #0000004d,
    inset -2px -3px 5px #ffffff80 !important;
  color: #fff !important;
  opacity: 1 !important;
  position: relative !important;
  z-index: 1 !important;
}

[data-a-target="login-button"]::before {
  background: radial-gradient(circle, #000 0%, #066503 100%) !important;
  border-radius: 22px !important;
  bottom: 0;
  box-shadow: 1px 1px 1px black, inset 3px 4px 5px #0000004d,
    inset -2px -3px 5px #ffffff80 !important;
  color: #ff0 !important;
  content: "";
  left: 0;
  opacity: 0 !important;
  position: absolute !important;
  right: 0;
  top: 0;
  transition: opacity 0.8s linear !important;
  z-index: -1 !important;
}

[data-a-target="login-button"]:hover::before {
  opacity: 1 !important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.modal-wrapper__content > * {
  background-color: #111 !important;
  border-radius: 22px !important;
  border: 1px solid rgba(0, 0, 0, 0.25) !important;
  box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006,
    #9b9b9b 0 1px 0 !important;
  padding: 0.5rem !important;
}

.modal-wrapper__content > *:hover {
  box-shadow: inset 0 0 5px #0006, #5c16c5 0 1px 0, 10px 10px 5px #0a0a0a66,
    #5c16c5 0 1px 0 !important;
}

/* Purple Gradient 
_____________________________________________________________________________________________________*/

.bsRjSc,
.cJwYjn,
.dxyVIj,
.fMIGgJ,
.kJHXRx,
.tEmJK
{
  background: linear-gradient(to bottom, #5c16c5 0%, #000 100%) !important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.side-nav {
  background: linear-gradient(to right, #242424 0%, #000 100%) !important;
}

.fMxWnV {
  background: linear-gradient(to bottom, #2f2e2e 0%, #000 100%) !important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.chat-line__message:focus .chat-line__message-highlight,
.chat-line__message:focus-within .chat-line__message-highlight,
.chat-line__message:hover .chat-line__message-highlight,
.tw-root--theme-dark .side-nav-card__link:hover
{
  background: linear-gradient(to right, #5c16c5, #0000) !important;
  border-left-color: #434343 !important;
}

}

@-moz-document domain("twitch.tv") {
/* Image Settings */

.default-panel,
.dyQMib,
.kDmwsl,
.tw-transition,
.vertical-selector__wrapper {
  filter: brightness(75%) !important;
}

/*
_____________________________________________________________________________________________________*/

.default-panel:hover,
.dyQMib:hover,
.kDmwsl:hover,
.tw-transition:hover,
.vertical-selector__wrapper:hover {
  filter: brightness(100%) !important;
}

/*
_____________________________________________________________________________________________________*/

img {
  border-radius: 22px !important;
}

/*
_____________________________________________________________________________________________________*/

img {
  -webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("twitch.tv") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
  appearance: none !important;
}

/* Arrows _______________________________________________________________________*/

/* Up _________________*/

selector {
  background: url(https://i.ibb.co/bvZmSFj/red-up.png) no-repeat !important;
  box-sizing: border-box;
}

/* Down _________________*/

[aria-label="Subscribe"] svg {
  background: url(https://i.ibb.co/z7MRv74/direction-arrow-down.png) no-repeat !important;
  box-sizing: border-box;
}

/* Left _________________*/

selector {
  background: url(https://i.ibb.co/mRKFvH8/direction-arrow-left.png) no-repeat !important;
  box-sizing: border-box;
}

/* Right _________________*/

selector {
  background: url(https://i.ibb.co/LQDKzQT/direction-arrow-right.png) no-repeat !important;
  box-sizing: border-box;
}

[aria-label="Subscribe"] svg {
  background-size: 25px !important;
  height: 26px !important;
  padding-left: 26px !important;
  width: 1px !important;
}

/* Creative ___________________________________________________________________________*/

[alt="Creative Icon"] {
  background: url(https://i.ibb.co/QQswXjS/painters-pallett.png) no-repeat !important;
  box-sizing: border-box;
}

[alt="Creative Icon"] {
  background-size: 65px !important;
  height: 86px !important;
  padding-left: 66px !important;
  transform: rotate(35deg);
  width: 1px !important;
}

/* Esports ___________________________________________________________________________*/

[alt="Esports Icon"] {
  background: url(https://i.ibb.co/3k19VHt/trophy.png) no-repeat !important;
  box-sizing: border-box;
}

[alt="Esports Icon"] {
  background-size: 65px !important;
  height: 86px !important;
  padding-left: 66px !important;
  transform: rotate(35deg);
  width: 1px !important;
}

/* Games ___________________________________________________________________________*/

[alt="Games Icon"] {
  background: url(https://i.ibb.co/z89TfKr/Game-Controller.png) no-repeat !important;
  box-sizing: border-box;
}

[alt="Games Icon"] {
  background-size: 85px !important;
  height: 86px !important;
  padding-left: 86px !important;
  transform: rotate(35deg);
  width: 1px !important;
}

/* Heart ________________________________________________________________________________*/

/* On ___________________*/

[aria-label="Follow"]:hover svg,
[data-a-target="game-directory-follow-button"]:hover svg {
  background: url(https://i.ibb.co/FB9kX4W/healthy-heart.png) no-repeat !important;
  box-sizing: border-box;
}

/* Off ___________________*/

[aria-label="Follow"] svg,
[data-a-target="game-directory-follow-button"] svg {
  background: url(https://i.ibb.co/cD452WP/healthy-hear-darkt-32x32.png)
    no-repeat !important;
  box-sizing: border-box;
}

[aria-label="Follow"]:hover svg,
[aria-label="Follow"] svg,
[data-a-target="game-directory-follow-button"]:hover svg,
[data-a-target="game-directory-follow-button"] svg {
  background-size: 25px !important;
  height: 26px !important;
  padding-left: 26px !important;
  width: 10px !important;
}

/* IRL ___________________________________________________________________________*/

[alt="IRL Icon"] {
  background: url(https://i.ibb.co/vDMZnf4/microphone2.png) no-repeat !important;
  box-sizing: border-box;
}

[alt="IRL Icon"] {
  background-size: 65px !important;
  height: 86px !important;
  padding-left: 66px !important;
  transform: rotate(35deg);
  width: 1px !important;
}

/* Music ___________________________________________________________________________*/

[alt="Music Icon"] {
  background: url(https://i.ibb.co/FwwB9CG/headphones1.png) no-repeat !important;
  box-sizing: border-box;
}

[alt="Music Icon"] {
  background-size: 65px !important;
  height: 86px !important;
  padding-left: 66px !important;
  transform: rotate(35deg);
  width: 1px !important;
}

/* Search ___________________________________________________________________________*/

[aria-label="Search Button"] svg {
  background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat !important;
  box-sizing: border-box;
}

[aria-label="Search Button"] svg {
  background-size: 25px !important;
  height: 26px !important;
  padding-left: 26px !important;
  width: 1px !important;
}

/* Star ___________________________________________________________________________*/

/* On ___________________*/

selector {
  background: url(https://i.ibb.co/WH0qS2k/star.png) no-repeat !important;
  box-sizing: border-box;
}

/* Off __________________*/

[aria-label="Subscribe"] svg:nth-child(2) {
  background: url(https://i.ibb.co/VBcDjnC/star-empty-glow.png) no-repeat !important;
  box-sizing: border-box;
}

[aria-label="Subscribe"] svg:nth-child(2) {
  background-size: 25px !important;
  height: 26px !important;
  padding-left: 26px !important;
  width: 1px !important;
}

/* Warning ___________________________________________________________________________*/

.content-overlay-icon svg {
  background: url(https://i.ibb.co/DVxqrCP/Warning.png) no-repeat !important;
  box-sizing: border-box;
}

.content-overlay-icon svg {
  background-size: 85px !important;
  height: 86px !important;
  padding-left: 86px !important;
  width: 1px !important;
}

}

@-moz-document domain("twitch.tv") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
