style.css:
@-moz-document domain("codechef.com") {
body {
  background-color: #222;
  color: #aaa;
}

a {
  color: #5f7ec1; /* #3b5998; */
}

/*************** Main Page ***************/

main {
  background-color: #282828;
  box-shadow: 0 4px 5px 2px rgba(96,96,96,.3);
}

/**** Top Header ****/
.topbar {
  background-color: #282828;
  box-shadow: 0 2px 4px 0 rgba(96,96,96,.5);
}

/* CodeChef Logo */
header.site h1 a img {
  opacity: 0.8;
}

/* 'Username, Password' */
#user-bar input[type="password"], #user-bar input[type="text"] {
  background-color: #282828;
  border-color: #333;
}

input[type="color"], input[type="date"], input[type="datetime-local"], input[type="datetime"], input[type="email"], input[type="month"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], input[type="text"], input[type="time"], input[type="url"], input[type="week"], textarea {
  color: #aaa;
}

#user-bar input[type="submit"], .signup a {
  background-color: #113969 !important; /* #4a90e2; */
  color: #aaa;
}

#facebook-login {
  border-left-color: #666;
  border-bottom-color: #666;
  border-right-color: #666;
}

.topbar .social-login.inline-list #oauth-buttons li .icon-social#button_github {
  opacity: 0.8;
}

.topbar .social-login.inline-list #oauth-buttons li .icon-social#button_facebook {
  opacity: 0.8;
}

/* User profile */

.picture > img {
  opacity: 0.5;
}

.loggedin-user span.right {
  color: #aaa;
}

.topbar .social-login.inline-list > li.loggedin-user span.right > a {
  color: #aaa;
}

.topbar .social-login.inline-list > li.loggedin-user span.right .user-dropdown {
  background-color: #282828;
  border-color: #333;
}

.topbar .social-login.inline-list > li.loggedin-user span.right .user-dropdown li {
  border-bottom-color: #333;
}

.topbar .social-login.inline-list > li.loggedin-user span.right .user-dropdown li a {
  color: #aaa;
}

.topbar .social-login.inline-list > li.loggedin-user span.right .user-dropdown span::before {
  border-bottom-color: #444;
}

.topbar .social-login.inline-list > li.loggedin-user span.right .user-dropdown span::after {
  border-bottom-color: #282828;
}

.topbar .social-login.inline-list > li.loggedin-user span.right .user-dropdown span {
  background-color: #282828;
  border-color: #333;
}

/** Login Error Pop-up Pane **/

.generic-dialog-box {
  background-color: #282828;
  border-color: #333;
}

.generic-dialog-box .messages.error {
  background-color: #591d1d; /* #ffeded */
}

/**** Middle Header ****/

/* 'Practice, Compete, Discuss' dropdowns */
header.site nav ul.nice-menu > li ul {
  background-color: #282828;
}

header.site nav ul.nice-menu > li ul li:hover {
  background-color: #333;
}

header.site nav ul.nice-menu > li ul li {
  border-bottom-color: #444;
}

header.site nav ul.nice-menu > li ul li a {
  color: #aaa;
}

header.site nav ul.nice-menu > li.has-dropdown:hover, header.site nav ul.nice-menu > li.menuparent:hover {
  background-color: #282828;
}

header.site nav ul.nice-menu > li.has-dropdown:hover > a, header.site nav ul.nice-menu > li.menuparent:hover > a {
  color: #aaa;
}

/**** Body ****/

/* 'CodeChef DSA Certification, November Challenge, November Cook-Off, Top Performers' */
main .slider .owl-dots {
  background-color: #282828;
}

main .slider .owl-dots > div {
  color: #aaa;
}

main .slider .owl-nav .owl-prev {
  filter: invert(0.7);
}

main .slider .owl-nav .owl-next {
  filter: invert(0.7);
}

/* 'New to Programming...' */
main .getting-started {
  background-color: #282828;
  border-top-color: #444 !important;
  border-bottom-color: #444 !important;
}

main .getting-started p {
  color: #aaa;
}

/* 'Follow us' */
main .getting-started .social-media::before {
  color: #aaa;
}

main .getting-started .social-media {
  border-left-color: #666;
}

main .getting-started .social-media li a.fb,
main .getting-started .social-media li a.tw,
main .getting-started .social-media li a.gp,
main .getting-started .social-media li a.yt,
main .getting-started .social-media li a.qu {
  filter: invert(1.0);
}

/*** Widgets ***/

main .widget {
  background-color: #282828;
  border-color: #444;
}

main .widget h4 {
  color: #aaa;
}

/** 'Upcoming Contest' **/

main .widget.border-light.upcoming-contest .content p a {
  color: #5f7ec1; /* #3b5998; */
}

/** 'Recent Questions' **/

main .widget.border-dark ul.questions li h5 a {
  color: #aaa;
}

main .widget.border-dark ul.questions li aside {
  background-color: #282828;
  border-color: #444;
  color: #aaa;
}

main .widget.border-dark ul.questions li {
  border-bottom-color: #444;
}

main .widget.border-dark ul.questions li p {
  color: #aaa;
}

main .widget.border-dark ul.questions li p a {
  color: #5f7ec1; /* #3b5998; */
}

/** 'Announcements' **/

main .widget .content ul.side-nav li p {
  color: #aaa;
}

/** 'Directi Go for Gold' **/

main .widget.go-for-gold .content p.right img {
  filter: invert(0.84);
}

/** 'Recent Blog Posts' **/

main .widget .content ul.side-nav.posts li aside {
  background-color: #282828;
  border-color: #444;
  color: #aaa;
}

main .widget .content ul.side-nav.posts li {
  border-bottom-color: #444;
}

main .widget .content ul.side-nav.posts li a {
  color: #5f7ec1; /* #3b5998; */
}

/*** Footer ***/

footer.site {
  background-color: #333;
  border-top-color: #333;
}

footer.site a {
  color: #aaa;
}

footer.site ul.inline-list li a {
  color: #aaa;
}

footer.site article h6 {
  color: #aaa;
}

footer.site article h6 a {
  color: #aaa;
}

footer.site article p {
  color: #aaa;
}

footer.site .link-group h6 {
  color: #aaa;
}

footer.site .link-group ul li a {
  color: #aaa;
}


/*************** Practice > Beginner Page i.e. /problems/school ***************/


/**** Top Header ****/
.header-container {
  background-color: #282828;
  box-shadow: 0 2px 4px 0 rgba(96,96,96,.5);
}

/**** Middle Header ****/

.rounded-cr-body {
  background-color: #282828;
  border-color: #444;
  box-shadow: 0 4px 5px 2px rgba(96,96,96,.3);
}

/* 'Practice, Compete, Discuss' dropdowns */

#header-region ul.nice-menu ul, ul.nice-menu ul {
  background-color: #282828;
}

ul.nice-menu a {
  color: #aaa;
}

ul.nice-menu li.menuparent a.active {
  color: #3a6cbf; /* from /color-palette/3390 */
}

ul.nice-menu-down li ul li {
  border-bottom-color: #666;
  opacity: 0.9;
}

ul.nice-menu-down li ul li:hover {
  background-color: #333;
}

ul.nice-menu li.menuparent:hover {
  background-color: #282828;
}

ul.nice-menu li:hover a {
  color: #aaa !important;
}

/** Img **/
.content-wrapper-header {
  filter: invert(0.87);
}

.content-wrapper {
  border-color: #444;
  background-color: #282828;
}

/** 'Search Problems' **/
#center .content, .block .content {
  background-color: #282828;
}

div.r1, div.r2, div.r3, div.r4, div.r5 {
  background-color: #282828;
}

.rounded-block {
  border-color: #444;
}

/* 'Beginner, Easy, Medium, Hard, Challenge, Peer' */

.c4s_topmenu {
  background-color: #f00;
  background-image: linear-gradient(to bottom,#444,#282828);
}

.active_category {
  background-color: #333;
}

.c4s_topmenu li a {
  color: #aaa;
  text-shadow: none;
}

.c4s_topmenu li:hover {
  background-color: #333;
}

.c4s_topmenu li a:hover {
  color: #ccc;
}

.border-right {
  border-right-color: #666;
}

/** 'Name, Code, Successful Submissions, Accuracy' **/
#content-regions th, #content-regions thead, #frontpage-regions th, #frontpage-regions thead {
  background-color: #333;
  color: #aaa;
}

#content-regions th a {
  color: #aaa !important;
}

tbody {
  border-top-color: #333;
}

.dataTable > thead > tr {
  background-color: #282828;
  border-color: #444;
}

.dataTable > tbody > tr {
  background-color: #282828;
  border-right-color: #444;
  border-left-color: #444;
  border-bottom-color: #444;
}

.dataTable > tbody > tr:hover {
  background-color: #333;
}

.dataTable > tbody > tr > td {
  color: #aaa;
}

#content-regions td, #frontpage-regions td {
  border-bottom-color: #444;
}

tbody th, tr.even, tr.odd {
  border-color: #444;
}

/*** Footer ***/

.footer-container {
  background-color: #333;
  border-top-color: #333;
}

.cc-footer-seo {
  color: #aaa;
}

.cc-footer-seo a {
  color: #aaa;
}

.footer-container #footer .content .cols-2 #secondary-nav ul li a {
  color: #aaa !important;
}

/*************** Problem Page e.g. /problems/BSTOPS/ ***************/

.problem-container .sidebar .successful-submissions {
  border-color: #444;
}


.button.grey, .problem-container .sidebar .successful-submissions .problems a, .user-profile-container .sidebar .widget.recent-activity .problems tr a {
  background-color: #333;
  color: #aaa;
}

.button.grey:focus, .button.grey:hover, .problem-container .sidebar .successful-submissions .problems a:focus, .problem-container .sidebar .successful-submissions .problems a:hover, .user-profile-container .sidebar .widget.recent-activity .problems tr a:focus, .user-profile-container .sidebar .widget.recent-activity .problems tr a:hover {
  background-color: #666;
}


.contest-container .content-area {
  box-shadow: 0 6px 0 0 #282828,4px 2px 6px -4px #444;
}

.problem-container header h1 {
  color: #aaa;
  border-bottom-color: #666;
}

.problem-container .content-area .problem-statement {
  color: #aaa;
}

.problem-container .content-area .problem-statement h3 {
  color: #aaa;
  border-top-color: #666;
}

.problem-container .content-area .problem-statement ol, .problem-container .content-area .problem-statement p, .problem-container .content-area .problem-statement ul {
  color: #bbb;
}

/* 'Submit' button */
.button.blue, .problem-container .sidebar .successful-submissions .dataTable tbody tr td:last-child a, main .widget.border-dark ul.questions li p a.btn {
  background: #113969; /* #4a90e2; */
  color: #aaa;
}

/** 'All Submissions' **/
.button {
  box-shadow: 0 1px 2px 0 rgba(96,96,96,.5);
}

/*** 'Successful Submissions' ***/
.contest-container .sidebar .widget h5 {
  color: #aaa;
}

/* Table */

.problem-container .sidebar .successful-submissions .dataTable thead tr {
  background-color: #282828;
  border-color: #444;
}

.problem-container .sidebar .successful-submissions .dataTable tbody tr {
  background-color: #282828;
  border-bottom-color: #444;
}

.problem-container .sidebar .successful-submissions .dataTable thead tr th {
  border-bottom-color: #666;
}

main .widget table tr:hover td {
  background-color: #333;
}

/** 'Sample Input', 'Sample Output' **/

code {
  color: #bbb;
}

/** Block with Author, Tags, Date Added, Time Limit, Source Limit, Languages **/

.problem-container .content-area .problem-info {
  background-color: #303030;
  border-bottom-color: #333;
}

label {
  color: #aaa;
}

/* Star */
span[style*="background: #FFBF00;"] {
  background-color: #333 !important;
  color: #aaa !important;
}


/*************** Solution Page e.g. /viewsolution/26193914 ***************/

main.content .solution-container .solution-info ul {
  background-color: #282828;
  border-color: #333;
}

main.content .solution-container .solution-info ul li {
  color: #aaa;
}

.ace-tomorrow {
  background-color: #282828;
  color: #bbb;
}

.cc-editor-component .ace-tomorrow .ace_gutter {
  background-color: #303030;
  color: #666;
  border-right-color: #333;
}

.cc-editor-component .cc-text-editor {
  border-color: #333;
}

.ace-tomorrow .ace_indent-guide {
  filter: invert(0.8);
}

/** Selected Line **/

.ace-tomorrow .ace_gutter-active-line {
  background-color: #444;
}

.ace-tomorrow .ace_marker-layer .ace_active-line {
  background-color: #333;
}

/* Cursor */
.ace-tomorrow .ace_cursor {
  color: #999;
}

/*************** Submit Solution Page e.g. /submit/MINIMAX2 ***************/

.c4s_content-in-new {
  background-color: #282828 !important;
}

/* 'Switch to Non-IDE mode' */
.related-links a, .related-links input[type="submit"], .related-links input[type="submit"]#edit-submit {
  background-color: #333;
  color: #aaa !important;
}

/* 'Contest Code', 'Problem Code' */
.cc-editor-component .full-width-box {
  background-color: #282828;
  border-color: #333;
}

.ns-content {
  color: #aaa;
}

/*** Editor ***/

.cc-editor-component .header {
  background-color: #282828;
  border-bottom-color: #333;
}

.header {
  color: #aaa;
}

.cc-editor-component .chosen-container-single .chosen-single {
  background-color: #282828;
  border-color: #333;
  color: #aaa;
}

.cc-editor-component .small-editor-button {
  background-color: #282828;
  border-color: #333;
  color: #aaa;
}

.cc-text-editor .editor-status {
  background-color: #282828;
  border-top-color: #333;
}

/* 'Open' button */
.cc-editor-component .ns-button {
  color: #aaa;
}

/* 'Oops! we couldn't initialize IDE. Please reload' */

.cc-editor-component .small-editor-button.fa-refresh {
  background-color: #fafafa;
  border-color: #fafafa;
}

.cc-editor-component .overlay {
  filter: invert(0.85);
}

/**** Non-IDE Mode ****/

#editor {
  border-color: #333;
}

.table-heading-box {
  border-bottom-color: #333;
}

.area_toolbar {
  background-color: #282828;
}

.editAreaButtonNormal {
  border-color: #333 !important;
}

#selection_field.show_colors {
  background-color: #282828;
}

#line_number {
  border-right-color: #333;
}


/*************** Login Error Page e.g. /submit/MINIMAX2 ***************/

.c4s_content-in-new {
  background-color: #282828;
  border-color: #333;
}

/*************** Tags Page i.e. /tags/problems ***************/

/** Search Tags **/

.search-style {
  background-color: #282828;
  color: #aaa !important;
}

.search-box {
  border-color: #333;
}

.search-box-icon {
  filter: invert(0.8);
}


/** 'Authors', 'Tags' buttons **/

.sort-box-inner {
  border-color: #666;
  color: #aaa;
}

.sort-box-inner:hover {
  background-color: #444;
}

.active {
  background-color: #444;
}

/** 'Tag Name', 'Problem Count' buttons **/

.sort-icon {
  filter: invert(0.8);
}

/** Items **/

/* Tags */

.problem-tag {
  background-color: #414c4c; /* from /color-palette/15103 */
  border-color: #333;
}

.problem-tag:hover {
  background-color: #596767;
  color: #ccc;
}

/* Author */

.problem-tagbox {
  background-color: #282828;
  border-color: #333;
}

.author {
  background-color: #333;
  border-color: #333;
}

.author img {
  filter: invert(0.9);
}

.problem-tagbox-top a {
  color: #aaa;
}

.author:hover {
  background-color: #444;
  color: #ccc;
}

/*************** Search Results Page i.e. /tags/problems/tree ***************/


.problem-tagbox-authoricon img {
  filter: invert(0.8);
}

.problem-tagbox-statusbox img[title="Un-attempted"] {
  filter: invert(0.8);
}

.problem-tagbox-statusbox img[title="Editorial"] {
  filter: invert(0.8);
}

.problem-tagbox-inner {
  border-bottom-color: #666;
}

/** Loading spinner **/
img[src="/sites/all/modules/codechef_tags/images/loader.gif"] {
  filter: invert(0.84);
}

/* Tags */
.problem-tagbox-bottom a {
  color: #aaa; /* #644837; */
}

.searched {
  background-color: #846607 !important; /* #f7d361; */
  color: #bbb;
}
}

@-moz-document url-prefix("https://www.facebook.com/v2.12/plugins/like_box.php"), url-prefix("https://www.facebook.com/v2.12/plugins/like.php"), url-prefix("https://platform.twitter.com/widgets/") {
