style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com"){

@media screen and (min-width:820px){

ytd-browse[page-subtype="home"] ytd-rich-grid-row #contents {
    margin: 0 !important;
}

/* News nado no shou section */
ytd-browse[page-subtype="home"] ytd-rich-section-renderer #content {
    margin: 0 !important;
}
ytd-browse[page-subtype="home"] ytd-rich-section-renderer #content #contents{
    margin: 0 !important;
}

/* split (channel icon - title) */
ytd-browse[page-subtype="home"] #avatar-link {
    margin-right: 6px !important;
}
/*[[bug]]*/
/*[[channel_icon]]*/

/*[[font_size]]*/

/*[[title_line]]*/

/*[[split_th_ti]]*/

/*[[split_ti_ch]]*/

/*[[split_mt_th]]*/

/*[[round]]*/

/*[[thumb_late]]*/

/******************************/
/* short kakushu taiou (from) */
/******************************/

/* section jouge wo sukoshi tsumeru */
ytd-browse[page-subtype="home"] #rich-shelf-header.ytd-rich-shelf-renderer {
    margin: 12px 0 12px 8px !important;
}

/* tsugou ni yori short no marume ha 8 kotei */
ytd-browse[page-subtype="home"] ytd-rich-grid-slim-media[is-short] a.ytd-thumbnail {
    border-radius: 8px !important;
}

/* title ya menu wo thumbnail no ue ni muriyari idou */
/* shoujiki hanbun kurai wakatte nai */
ytd-browse[page-subtype="home"] ytd-rich-grid-slim-media[is-short] div#details {
    position: absolute !important;
    display: initial !important;
    bottom: 0 !important;
    width: 100% !important;
}
ytd-browse[page-subtype="home"] ytd-rich-grid-slim-media[is-short] div#details a.ytd-rich-grid-slim-media {
    position: sticky !important;
    display: flex !important;
    flex-direction: column !important;
}
ytd-browse[page-subtype="home"] ytd-rich-grid-slim-media[is-short] div#details div#menu {
    position: static !important;
}

/* title, metadata no iro yara ichi yara */
ytd-browse[page-subtype="home"] ytd-rich-grid-slim-media[is-short] div#details a.ytd-rich-grid-slim-media {
    z-index: 200 !important;
}
ytd-browse[page-subtype="home"] ytd-rich-grid-slim-media[is-short] div#details span {
    color: white !important;
    text-shadow: 0 0 2px black !important;
    word-wrap: anywhere !important;
}
ytd-browse[page-subtype="home"] ytd-rich-grid-slim-media[is-short] div#details h3.ytd-rich-grid-slim-media {
    padding: 12px 6px 4px 6px !important;
}
ytd-browse[page-subtype="home"] ytd-rich-grid-slim-media[is-short] div#details div#metadata {
    padding: 0px 6px 4px 6px !important;
    z-index: 200 !important;
}

/* menu icon no iro yara ichi yara */
ytd-browse[page-subtype="home"] ytd-rich-grid-slim-media[is-short] div#details div#menu ytd-menu-renderer {
    position: absolute !important;
    top: -24px !important;
}
ytd-browse[page-subtype="home"] ytd-rich-grid-slim-media[is-short]:hover div#details div#menu yt-icon {
    z-index: 200 !important;
    color: white !important;
}

/* kabu no kage */
ytd-browse[page-subtype="home"] ytd-rich-grid-slim-media[is-short] div#details:before {
    content:'';
    display:inline-block;
    position: absolute;
    z-index: 100;
    bottom:0;
    background: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.8));
    width: 100%;
    height: 100px;
    border-radius: 8px;
}

/****************************/
/* short kakushu taiou (to) */
/****************************/

/*[[inline]]*/

} /* @media no close */

/*[[thumbnail_size]]*/
}

options.value:
/* thumbnail size 180 split 4 */

@media screen and (min-width:820px){
ytd-browse[page-subtype="home"] ytd-rich-grid-renderer{
    left: calc((100% - var(--ytd-rich-grid-items-per-row) * 180px - var(--ytd-rich-grid-items-per-row) * 4px ) /2 ) !important;
}

ytd-browse[page-subtype="home"] ytd-rich-item-renderer:not([is-movie]):not([is-post]):not([is-slim-media]) {
    margin-right: 4px !important; /* split size */
    margin-left: 0px !important;
    width: calc(100% / var(--ytd-rich-grid-items-per-row) - 4px - 0.01px)  !important; /* 4px = split size */
}
ytd-browse[page-subtype="home"] ytd-rich-item-renderer[is-slim-media] {
    margin-right: 4px !important; /* split size */
    margin-left: 0px !important;
    width: calc(100% / var(--ytd-rich-grid-slim-items-per-row) - 4px - 0.01px)  !important; /* 4px = split size */
}
ytd-browse[page-subtype="home"] #contents.ytd-rich-grid-renderer, ytd-browse[page-subtype="home"] #header.ytd-rich-grid-renderer #chips-wrapper.ytd-feed-filter-chip-bar-renderer,  ytd-browse[page-subtype="home"] #masthead-ad.ytd-rich-grid-renderer{
     max-width: calc(var(--ytd-rich-grid-items-per-row) * 180px + var(--ytd-rich-grid-items-per-row) * 4px) !important;
}   /* 190px = max saize , 4px = split size */

ytd-browse[page-subtype="home"] #meta ytd-video-meta-block {
    min-width: 135px !important;
}
}
@media screen and (min-width:820px) and (max-width: 859px){
    ytd-app[mini-guide-visible] ytd-browse[page-subtype="home"] ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 3 !important;
        --ytd-rich-grid-slim-items-per-row: 5 !important;
        --ytd-rich-grid-posts-per-row: 2 !important;
        width: 1050px !important;
        align-items: flex-start !important;
    }
}
