}

/* inline-player no seekbar idou ryou */
@keyframes seekbar-animation {
    100%{
        transform: translate(0px, 61px);
        /* 1 para ha 0 kotei , 2 para ha thmbnnail yoko*9/16-40 */
        box-shadow: 0 0 15px 1px black;
    }
}

