 .theme-light .hljs-addition,.theme-light .hljs-attribute,.theme-light .hljs-meta,.theme-light .hljs-parameter,.theme-light .hljs-selector-attr,.theme-light .hljs-selector-pseudo,.theme-light .hljs-title,.theme-light .hljs-type,.theme-high-contrast .hljs-addition,.theme-high-contrast .hljs-attribute,.theme-high-contrast .hljs-meta,.theme-high-contrast .hljs-parameter,.theme-high-contrast .hljs-selector-attr,.theme-high-contrast .hljs-selector-pseudo,.theme-high-contrast .hljs-title,.theme-high-contrast .hljs-type{
