style.css:
@-moz-document domain("hh.ru") {
/*
*   Основная страница
*/
body {
    background: #222f;
}

.bloko-link-switch_tertiary, .bloko-link-switch_tertiary:visited {
    color: #adadad;
}

.l-ambient {
    color: #aaa;
}

.company-sidebar {
    background-color: #3336;
    color: #bbb;
}

.company-header {
    color: #fff;
}

.supernova-overlay_visible {
    background: #222f;
}

.bloko-link_list {
    color: #bbb;
}

.bloko-link_list:visited {
    color: #7f3cba;
}

.news-box-item__link {
    color: #bbb;
}

.work-in-city {
    color: #bbb;
}

.supernova-footer-services {
    background: #1115;
}

.supernova-navi-search {
    background: #1115;
}

.supernova-search-select__text {
    color: #bbb;
}

.search-submit-wrapper {
    background-color: rgba(34, 34, 34, .8);
}

.search-submit-wrapper:after {
    background: linear-gradient(to top,rgba(34, 34, 34, .9) 0,rgba(200, 200, 200, 0) 100%);
}

.employer-index-special-offers {
    background-color: #1c1c1c;
}

.bloko-input {
    display: inline-block;
    box-sizing: border-box;
    width: 100%;
    height: 38px;
    padding: 0 11px;
    margin: 0;
    border-radius: 0;
    border: 1px solid #cbd1d4;
    font-size: 14px;
    font-family: Arial,sans-serif;
    color: #c7c7c7;
    -webkit-appearance: none;
    -moz-appearance: none;
    -webkit-tap-highlight-color: transparent;
    background-color: #4a4a4a;
    line-height: 38px;
}

.bloko-tabs__item {
    color: #bbb;
}

.bloko-tabs-right-glare {
    background: linear-gradient(to right,rgba(255, 255, 255, 0),#222 30px);
}

.bloko-modal {
    background-color: #3a3a3a;
}

.price-services-item-wrapper {
    background: #333;
}

.announcement {
    background-color: #222222;
    border: 1px solid #848484;
}

.index-dashboard-applicant {
    background-color: #222222;
}

.my-events {
    color: #bbb;
}

.advices__link:link, .advices__link:visited {
    color: #bbb;
}

.recommended-vacancies {
    color: #bbb;
}

.clusters {
    background-color: #1c1c1c;
}

.clusters-value_selected .clusters-value__count, .clusters-value_selected .clusters-value__close, .clusters-value_selected .clusters-value__name {
    background-color: #444444;
    color: #bbb;
}

.serp-special {
    background-color: #222222;
}

.search_no-bg {
    color: #bbb;
}

.promo-page {
    color: #bbb;
}

.promo-page-advantage__recommendation {
    color: #000;
}

.promo-page-advantage__list li {
    color: #bbb;
}

.promo-page-services__background {
    background: #212122 no-repeat top / cover;
}

/*
*   Личная страница
*/


/*
*   Поле регистрации  
*/
.s-friendly .light-page-content, .xs-friendly .light-page-content {
    color: #bbb;
    background: #333;
}
}
