style.css:
@-moz-document url-prefix("https://www.jameds.org/SRB2/ping/") {
/*==================================*/
/* Colors / Misc. Positioning Fixes */
/*==================================*/

#serverinfo, #serverheader, #filecount {
    background: #232323;
    border: none;
    border-radius: 6px; }

#serverheader {
    padding: 0; }

/*===========================*/
/* Header/Footer Shenanigans */
/*===========================*/

input.field {
    background: #232323;
    transition: background 0.2s, color 0.2s;
    border: none;
    border-radius: 6px;
    color: #C8C8C8; }

input.field:hover {
    background: #303030;
    color: #FFFFFF; }

header {
    padding: 1rem 0; }

header a {
    padding: 0 0.5rem; }

header h1 {
    background: rgba(0,0,0,0); }

header a[href="https://www.jameds.org/SRB2/ping/"] {
    font-weight: 700;
    text-decoration: none;
    padding: 0 0.5rem 0 0; }

footer {
    background: #232323;
    margin: 1rem 0;
    color: #C8C8C8; 
    border-radius: 6px; }

/*=========================*/
/* Credit Because I'm Epic */
/*=========================*/

footer p::after {
    content: " Thank you to Eldog for making it prettier!"}

#contact::after {
    content: ""; }

/*=========================*/
/* Player Sort Positioning */
/*=========================*/

.sort {
    margin: 0px 5px 0 5px; }

/* How the actual fuck do I use css on something that's not technically an element */

/*===============*/
/* Adjust Tables */
/*===============*/

/* Normalize Files List Position */

#filecount {
    padding: 0.5rem; }

#fileinfo {
    padding: 0 0 0.5rem 0.5rem !important; }

#files {
    background: #191919 !important;
}

/* Awful hacky way to round table corners */

#files tr:first-child th:first-child, #playertable tr:first-child th:first-child { /* Top Left */
    border-color: rgba(0,0,0,0) !important;
    border-top-left-radius: 6px !important; }

#files tr:first-child th:last-child, #playertable tr:first-child th:last-child { /* Top Right */
    border-color: rgba(0,0,0,0) !important;
    border-top-right-radius: 6px !important; }

#files tr:last-child td:first-child, #playertable tr:last-child td:first-child { /* Bottom Left */
    border-color: rgba(0,0,0,0) !important;
    border-bottom-left-radius: 6px !important; }

#files tr:last-child td:last-child, #playertable tr:last-child td:last-child { /* Bottom Right */
    border-color: rgba(0,0,0,0) !important;
    border-bottom-right-radius: 6px !important; }

/* Change colors and padding */

#fileheader th {
    background: #232323;
    padding: 0.5rem;
    border: none; }

#playerheader th {
    background: #191919;
    padding: 0.5rem;
    border: none; }

#files, #files td, #playertable, #playertable td {
    border: none !important;
    padding: 0.5rem; }

/* Change Colors of Odd Rows */

#files tr.file:nth-child(odd) {
    background: #232323 !important; }

#playertable tr.player:nth-child(odd) {
    background: #191919 !important; }

/* Hover Actions - Commented out for being misleading despite being cool looking */

/* #files tr.file, #playertable tr.player {
    transition: color 0.2s; }

#files tr.file:hover, #playertable tr.player:hover {
    color: #FFFFFF !important; } */
}

@-moz-document url-prefix("https://www.jameds.org") {
/*==================================*/
/* Colors / Misc. Positioning Fixes */
/*==================================*/

html, body, header, #flexcontainer {
    background: #191919;
    color: #C8C8C8; }

h1, h2 {
    background: rgba(0,0,0,0);
    color: #FEFEFE; }

body, .document {
    background: #191919 !important;
    color: #C8C8C8 !important;
    margin: 0 auto;
    max-width: 900px; }

table, .note, .diduknow, #credits {
    border-radius: 6px;
    background: #232323 !important;
    padding: 0.5rem;
    color: #FEFEFE !important; }

.extra {
    background: rgba(0,0,0,0); }

form h1, form h2, #indextitle {
    text-align: center;
    margin: 2rem 0.25rem 0.5rem 0; }

#indexlist {
    margin: 0 auto; }

hr, #itsibuki {
    display: none; }

code {
    border-radius: 0.25em;
    background: #000000;
    padding: 0 0.25em;
    color: #FFFFFF;
}

/*===================*/
/* Restyle All Links */
/*===================*/

a {
    transition: color 0.2s;
    text-decoration: none;
    color: #6080d5 !important; }

a:hover {
    color: #8C9ECC !important; }
}

@-moz-document url-prefix("https://www.jameds.org/SRB2/") {
h2 {
    text-align: center; }

img[alt="Meme image"] {
    display: block;
    margin: 0 auto !important; }
}

@-moz-document url("https://www.jameds.org/") {
header {
   margin: 2rem 0 0.5rem 0;   }

.main {
    margin: 0;
    padding: 0;
    border: none; }

table {
    width: 100%; }
}
