/* Logo positioning */
.site_logo {
	width: 115px;
	transform: rotate(-15deg);
	transition: transform 0.35s cubic-bezier(0.2, 0.8, 0.3, 2);
}
.site_logo:hover {
	transform: none;
}
.site-description {
	padding-left: 0;
}


/* Full-width */
.wrap {
	max-width: none;
}
.post > .inner {
	display: flex; /* instead of floats */
}
.postbody {
	float: none;
	flex-grow: 1; /* take up all free space */
}
.postprofile {
	max-width: 20em;
	float: none;
	order: 1; /* flex: profile on right side */
}
.back2top {
	/* bottom right corner of post */
	position: absolute;
	bottom: 0;
	right: 0;
}
.back2top .top {
	float: none;
	margin: 0;
}


/* Dark theme */
html, body {
	background-color: #1f2225;
}
.forabg, .forumbg {
	background-color: #1f2225;
}
.bg3, .wrap, .headerbar {
	background-color: #2f3136;
}
.navbar {
	background-color: inherit;
}
.navbar.navbar-top {
	background-color: #1f2225;
}
.cp-mini,
.navigation .active-subsection a,
.tabs .tab > a {
	background-color: #36393f;
	color: #fffddf;
}
.bg2 .button, a.button1, a.button2, input.button1, input.button2, input.button3 {
	background-color: #36393f;
	border-color: #36393f;
}
.cp-main .panel {
	background-color: #31343a;
}
.bg1, .panel-container .panel,
.cp-main .pm {
	background-color: #3b3e45;
}
.bg2, .panel,
table.zebra-list tr:nth-child(odd) td, ul.zebra-list li:nth-child(odd),
table.zebra-list tr:nth-child(even) td, ul.zebra-list li:nth-child(even) {
	background-color: #40444b;
}
.navigation a,
.tabs .activetab > a, .tabs .activetab > a:hover,
.pagination li a,
.button {
	background-color: #464b53;
	border-color: #464b53;
	color: #fffddf;
}
.inputbox, select {
	background-color: #464b53;
	border-color: #2f3136;
	color: #baddd9;
}
.search-box .inputbox, .search-box .inputbox:hover, .search-box .inputbox:focus {
	background-color: #464b53;
	border-color: #464b53;
	color: #baddd9;
}
.inputbox::placeholder {
	color: dimgray;
}
.button-search {
	border-left-color: #2f3136;
	border-right-color: #2f3136;
}
a.button1, input.button1, input.button3, a.button2, input.button2a.button1, input.button1, input.button3, a.button2, input.button2 {
	padding: 6px 14px;
}
.post .button:hover, .post .button:focus {
	color: #d60;
	background-color: #fff;
	border-color: #d60;
}

html, body, .content, .postbody, label, dd label,
.panel,
.site-description h1,
.post:target .content, .post:target h3 a,
dl.faq dt,
ul.topiclist li
{
	/* Default text color */
	color: #baddd9;
}
dl.mini dt,
a, a:hover,
.message-box textarea,
.pagination li.active span,
.button .icon, a.button1, input.button1, input.button3, a.button2, input.button2,
h1, h2, h3,
.content h2, .panel h2,
.stat-block h3,
fieldset dl:hover dt label {
	/* Default link/highlighted text color */
	color: #fffddf;
}
a.topictitle {
	/* color: navajowhite; */
}

.postlink {
	color: #fffddf;
	border-bottom-color: #baddd9;
}
.postlink:visited {
	color: #fffddf;
	border-bottom-color: gray;
}
.postlink:hover {
	color: #fffddf;
	background-color: rgba(255, 255, 255, 10%);
}

table.table1 tbody td,
ul.topiclist dd,
.panel-container .panel li.row,
.jumpbox .dropdown li {
	border-color: #666;
}
ul.cplist,
li.row {
	border-top-color: #666;
}
.jumpbox-cat-link {
	background-color: #2f3136;
}
.jumpbox-sub-link {
	background-color: #4b5058;
}
.jumpbox-cat-link:hover {
	background-color: #40444b;
	border-color: #40444b;
}
.jumpbox-sub-link:hover {
	background-color: #40444b;
}

.codebox {
	background-color: #333;
}
blockquote {
	background-color: #333;
	border-color: #222;
}
blockquote blockquote {
	background-color: #3c3c3c;
	border-color: #333;
}
blockquote blockquote blockquote {
	background-color: #484848;
	border-color: #3c3c3c;
}

div[style*="background-color:beige"] {
	background-color: #4b5058;
}
}
