/* deze code doet de rest*/
body {
    background-attachment: fixed;
    background-size: cover;
}
#header-wrapper {
    background-color: var(--mainkleur);
}
#main-menu a.active,
#main-menu a.active:hover {
    text-decoration: none;
    cursor: pointer;
    color: #eaf5ff;
    background: var(--mainkleur);
}
#main-menu a {
    display: block;
    float: left;
    height: 30px;
    margin-right: 4px;
    font-size: 13.5px;
    border-radius: 5px 5px 0 0;
    padding: 12px 16px 4px;
    color: var(--textkleur);
    background: #00000024;
}
#main-menu-bottom {
    height: 5px;
    background-color: var(--mainkleur);
}
#user img {
    height: 50px;
    visibility: hidden;
}
#header .date,
#header .version,
#search-form,
.select.block > span {
    display: inline-block;
    visibility: hidden;
}
#main-menu a:hover {
    color: var(--textkleur);
    background: var(--mainkleur);
}
#content {
    display: table;
    background-color: #9c4e4e00;
    box-shadow: 1px 1px 4px 1px rgba(102, 102, 102, .5);
    border: 1px solid #d4e4e9;
    border-top: 0;
    box-shadow: 0 2px 5px #e5eef1;
}
#master-panel {
    display: table-cell;
    background-color: var(--mainkleur);
    position: relative;
    vertical-align: top;
    -moz-transition: width .3s;
    -webkit-transition: width .3s;
    -o-transition: width .3s;
    -ms-transition: width .3s;
    transition: width .3s;
}
#detail-panel-wrapper {
    display: table-cell;
    padding: 0 15px;
    background-color: var(--mainkleur);
    vertical-align: top;
    padding-top: 0 15px;
    min-width: 250px;
}
h1 {
    font-weight: 700;
    font-size: 16pt;
    color: var(--textkleur);
}
.m-element:hover {
    background-color: #000000c2;
}
.type {
    font-weight: 600;
    margin-left: 8px;
    border: 2px solid #00000000;
    height: 34px;
    margin-right: 10px;
    padding: 3px;
    color: var(--textkleur);
    font-size: 11pt;
    background-color: #00000059;
    border-radius: 3px;
}
.m-element,
div.m-wrapperTable table tr td {
    margin-top: -1px;
    border: 0px solid #1313139c;
    overflow: hidden;
}
#master-panel ~ #detail-panel-wrapper {
    border-left: 0px solid #000!important;
}
.section .block {
    padding: 12px;
    border-radius: 5px;
    background: var(--mainkleur);
    border: 0px solid #bee1f0;
    margin: 8px;
}
a {
    outline: 0;
    color: var(--textkleur);
    cursor: pointer;
}
#master-panel .date div,
.pauze,
.roster-table-header .day .huiswerk-items,
.uurNummer {
    background: var(--mainkleur)!important;
}
#master-panel .panel-header {
    padding-left: 15px;
    border-bottom: 0px solid #e2e9ee;
}
body {
    line-height: 1;
    color: var(--textkleur);
}
.icon-toets {
    background: var(--mainkleur);
}
#master-panel h2 {
    font-size: 15px;
    font-weight: 400;
    line-height: 20px;
    color: var(--textkleur);
    margin-bottom: 4px;
}
.IconFont.type {
    color: var(--textkleur);
    font-size: 22px;
    width: 29px;
    line-height: 29px;
}
.yellow.ribbon a.IconFont {
    padding: 4px;
    font-size: 16pt;
    background: var(--mainkleur);
}
.ribbon .chk.checked,
.ribbon .chk:hover,
.ribbon.chk.checked {
    color: var(--textkleur);
    border-bottom: 3px solid #000000;
}
.ribbon .chk,
.ribbon .chk.checked:hover {
    color: var(--textkleur);
    border-bottom: 3px solid #fff;
}
.yellow.ribbon span {
    font-style: italic;
    color: #eaeef1;
    position: relative;
    margin-right: 5px;
}
#master-panel .date div p.date-day,
#master-panel .date div p.date-month,
#master-panel .date span {
    color: #ffffff!important;
    font-weight: 600!important;
}
#master-panel .date div p.date-day {
    height: 11px;
    padding-top: 0;
    margin: 0 1px;
    font-size: 10px;
    background-color: c;
    text-transform: uppercase;
}
.m-element.no-click:hover {
    cursor: default;
    background: 0 0;
    border-bottom: 0px solid #000000;
}
.icon-geen-van-beide,
.icon-huiswerk,
.icon-leermiddelen,
.icon-studiewijzer,
.icon-toets {
    color: var(--textkleur);
}
.icon-geen-van-beide,
.icon-huiswerk,
.icon-leermiddelen,
.icon-maatregel,
.icon-studiewijzer,
.icon-toets {
    height: 22px;
    width: 22px;
    text-align: center;
    line-height: 22px;
    border-radius: 50%;
    background: var(--mainkleur);
    display: inline-block;
}
h2,
h3 {
    color: var(--textkleur);
    margin: 0 0 8px;
}
.goverview th.vaklabel {
    max-width: 40px!important;
    padding-top: 5px;
    padding-bottom: 5px;
    cursor: pointer;
}
.goverview tbody td {
    border: 1px solid #00000054;
    background-color: var(--mainkleur);
    text-align: center;
}
#master-panel .date div p.date-day {
    height: 11px;
    padding-top: 0;
    margin: 0 1px;
    font-size: 10px;
    background-color: var(--mainkleur);
    text-transform: uppercase;
}
.yellow.ribbon a {
    line-height: 22px;
    color: var(--textkleur);
}
.blue.ribbon p {
    background: 0 0;
    margin-right: -23px;
    color: var(--textkleur);
}
.sub {
    line-height: 18px;
    color: var(--textkleur);
    font-size: 12px;
}
.load-more:active,
.section {
    color: var(--textkleur);
}
.weekitems .weekitemLabel {
    color: #e8eaea;
    font-size: 15px;
    margin-left: 10px;
}
.roster-table-content .uurNummer {
    color: var(--textkleur)!important;
    border-radius: 5px!important;
    border: 0px solid #bee1f0!important;
}
.pauze {
    width: 960px;
    height: 18px!important;
    color: var(--textkleur);
    background-color: #dce5e6;
    line-height: 26px;
    vertical-align: middle;
}
.afspraak {
    position: absolute;
    left: 5px;
    right: 5px;
    background-color: var(--mainkleur);
    margin: 2px;
    text-align: left;
}
.afspraakVakNaam {
    height: 14px;
    color: var(--textkleur);
    font-size: 13px;
}
.afspraak .toekenning {
    background-color: var(--mainkleur);
    margin-left: 3px;
    height: 100%;
}
.toekenning a {
    color: var(--textkleur);
}
.afspraakLocatie {
    color: var(--textkleur);
    font-size: 12px;
    line-height: 20px;
    float: left;
}
.roster-table-content .hours .hour {
    border-top: 1px solid #e5eef100!important;
    border-bottom: 1px solid #e5eef100!important;
}
.roster-table-header .day {
    text-align: center;
    font-size: 15px;
    background: var(--mainkleur);
    height: 100%;
}
.dayTitle {
    border-bottom: solid 3px #dce5e600;
    background: var(--mainkleur);
}
.roster-table-content .day {
    border-top: solid 0px #dce5e6;
}
.day {
    display: table-cell;
    position: relative;
    border-left: dotted 0px #dce5e6;
    overflow: hidden;
}
#content {
    display: table;
    background-color: #9c4e4e00;
    box-shadow: 0px 0px 0px 0px rgba(102, 102, 102, .5);
    border: 0px solid #d4e4e9;
    border-top: 0;
    box-shadow: 0 0px 0px #e5eef1;
}
.stpanel section div.form--holder .stpanel--error--message,
.stpanel section div.form--holder form {
    background: var(--mainkleur);
    visibility: visible;
    -webkit-backface-visibility: hidden;
    -webkit-animation: slide-up 500ms ease;
    -moz-animation: slide-up 500ms ease;
    animation: slide-up 500ms ease;
    border-radius: 5px;
    box-shadow: 0 0 30px 0 rgba(13, 59, 94, .3);
    padding: 2rem;
}
.stpanel .form--input.state-disabled input {
    background-color: var(--mainkleur);
    border-color: #13131300;
    opacity: 1;
}
.stpanel input[type=email],
.stpanel input[type=number],
.stpanel input[type=password],
.stpanel input[type=search],
.stpanel input[type=tel],
.stpanel input[type=text],
.stpanel input[type=url],
.stpanel select,
.stpanel textarea {
    min-width: 6rem;
    margin: 1rem 0 0;
    padding: .5em;
    line-height: 1.5em;
    font-family: 'Open Sans Semibold', 'Roboto', Helvetica;
    font-size: 14px;
    color: var(--textkleur);
    border: 2px solid var(--textkleur);
    border-radius: 0;
    -webkit-appearance: none;
    background: #fff;
}
.stpanel .form--input i {
    position: absolute;
    top: .35rem;
    width: 2rem;
    height: 2rem;
    right: .35rem;
    line-height: 2rem;
    font-size: 1rem;
    text-align: center;
    color: var(--textkleur);
}
.stpanel input[type=email],
.stpanel input[type=number],
.stpanel input[type=password],
.stpanel input[type=search],
.stpanel input[type=tel],
.stpanel input[type=text],
.stpanel input[type=url],
.stpanel select,
.stpanel textarea {
    min-width: 6rem;
    margin: 1rem 0 0;
    padding: .5em;
    line-height: 1.5em;
    font-family: 'Open Sans Semibold', 'Roboto', Helvetica;
    font-size: 14px;
    color: var(--textkleur);
    border: 2px solid var(--mainkleur);
    border-radius: 0;
    -webkit-appearance: none;
    background: var(--mainkleur);
}
.stpanel .form--checkbox input + i:after {
    content: '\1E12';
    width: 1.3rem;
    height: 1.2rem;
    font-size: 1.2rem;
    bottom: 1px;
    left: 0;
    color: var(--textkleur);
    line-height: 1.2;
}
.stpanel .form--checkbox input:checked + i,
.stpanel .form--radio input:checked + i {
    border-color: var(--mainkleur);
    background-color: var(--mainkleur);
}
.stpanel section div.form--holder .stpanel--error--message a.button--stpanel,
.stpanel section div.form--holder form a.button--stpanel,
.stpanel--meldingen a.button--stpanel,
.stpanel--screensaver section div.form--holder .stpanel--error--message a.button--stpanel,
.stpanel--screensaver section div.form--holder form a.button--stpanel {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    cursor: pointer;
    font-size: 13px;
    font-weight: 700!important;
    font-family: 'Open Sans Semibold', 'Roboto', Helvetica;
    line-height: 1.5rem!important;
    padding: .56rem 1rem;
    vertical-align: baseline;
    text-decoration: none;
    color: var(--textkleur)!important;
    -webkit-font-smoothing: antialiased;
    display: inline-block;
    width: 175px;
    border: .15rem solid #ffffff;
    border-radius: 25px;
    background-color: transparent;
    text-align: center;
    margin-top: 1.5rem;
}
.m-wrapper.active {
    margin-right: -5px;
    padding-right: 5px;
    background: #00000066;
}
.roster-table-header .day {
    text-align: left;
    font-size: 15px;
    background: var(--mainkleur);
    height: 100%;
}
.roster-table-header .day,
.roster-table-header .today {
    color: var(--textkleur)!important;
}
}
