style.css:
@-moz-document domain(nitrotype.com) {

  .dash-copyContainer {
    background: #0d0d0d ; 
    border-radius: 2px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
    border: none;    
   }

  .dash-copy
  {
    color: #fff;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
	font-size: 25px;
    
  }

  ::selection
  {
    background-color: #fff;
    color: #000;
  }
  
  .dash-bg
  {
    content: url("https://i.ibb.co/bQ89pcz/Screen-Shot-2020-03-06-at-6-39-08-PM-2.png");
  }

}
