style.css:
@-moz-document url-prefix("https://www.youtube.com") {
html {
  font-size: 9px !important;
}
ytd-rich-item-renderer {
    margin-left: calc(16px / 2);
    margin-right: calc(16px / 2);
    margin-bottom: 40px;
    width: calc(70% / var(--ytd-rich-grid-items-per-row) - 16px - 0.01px);
}
}
