style.css:
@-moz-document domain("youtube.com") {
  ytd-browse[page-subtype="home"] ytd-rich-grid-row,
  ytd-browse[page-subtype="home"] #contents.ytd-rich-grid-row {
    display:contents !important;
  }
  
 ytd-browse[page-subtype="home"] ytd-rich-grid-renderer {
   --ytd-rich-grid-items-per-row: /*[[wide]]*/ !important;
   --ytd-rich-grid-posts-per-row: 3;
   --ytd-rich-grid-movies-per-row: /*[[wide]]*/ !important;
}

ytd-video-meta-block[rich-meta] #channel-name.ytd-video-meta-block,
ytd-video-meta-block[rich-meta] #metadata-line.ytd-video-meta-block {
    font-size: 1.2rem;
}

#meta.ytd-rich-grid-video-renderer {
    padding-right: 12px;
}

#title.ytd-rich-grid-renderer,
#title.ytd-rich-shelf-renderer {
    font-size: 1.4rem;
}


/* RWD - like a Bootstrap 4 - better for small window than nothing */

@media(max-width: 1200px) {
   body ytd-browse[page-subtype="home"] ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 5 /*[[over]]*/;
        --ytd-rich-grid-movies-per-row: 5 /*[[over]]*/;
    }
}


@media(max-width: 992px) {
   body ytd-browse[page-subtype="home"] ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 4 /*[[over]]*/;
        --ytd-rich-grid-movies-per-row: 4 /*[[over]]*/;
    }
}

@media(max-width: 768px) {
   body ytd-browse[page-subtype="home"] ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 3 /*[[over]]*/;
        --ytd-rich-grid-movies-per-row: 3 /*[[over]]*/;
    }
}

@media(max-width: 576px) {
   body ytd-browse[page-subtype="home"] ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 2 /*[[over]]*/;
        --ytd-rich-grid-movies-per-row: 2 /*[[over]]*/;
    }
}


@media(max-width: 360px) {
   body ytd-browse[page-subtype="home"] ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 2 /*[[over]]*/;
        --ytd-rich-grid-movies-per-row: 2 /*[[over]]*/;
    }
}
}


/* end of style / modification */

@-moz-document url-prefix("https://userstyles.org/styles/177216/5") {
/* Self-promo */
div[class*="recommended-styles_recommendedStylesWrapper_"]:not(#krystian3w),
div.recommendations:not(#krystian3w) {
  display: none !important;
  visibility: hidden !important;
  opacity: 0 !important;
  filter: opacity(0) !important;
  user-select: none !important;
  pointer-events: none !important;
  width: 0 !important;
  height: 0 !important;
}
div[class*="recommended-styles_recommendedStylesWrapper_"]:not(#krystian3w) * {
  width: 0 !important;
  height: 0 !important;
}
/* userstyles.org customisation panel on Firefox only: */

/* an additional bottom margin for the option: */
#style-settings > #advancedsettings_area > .setting_div {
    margin-bottom: 15px !important;
}
/* equally wide adjustment fields 1/2: */
#style-settings > #advancedsettings_area > .setting_div > div > div > div,
.setting_div > div > input {
    min-width: 260px !important;
}
/* equally wide adjustment fields 2/2: */
#style-settings > #advancedsettings_area > .setting_div > div > select {
    min-width: 265px !important;
}
/* Additional description (notes) visible without stretching the field: */
#notes_textarea {
    min-height: 310px !important;
    max-height: 100% !important;
}

#applies_link::after {
    content: ".com";
}
}
