style.css:
@-moz-document domain("forums.redflagdeals.com") {
/* hide sidebar */
.sidebar_content {
    display: none !important;
}

.primary_content {
    padding-right: 0 !important;
}

/* hide stuff */
.forums_page_header_container,
#site_headerbar,
.forums_nav,
.announcement.message,
#header_billboard_bottom,
#site_footer {
    display: none !important;
}
#site_container {
    margin-bottom: 0 !important;
}

.forum_content:not(:hover) {
    filter: grayscale(1);
    opacity: 0.5;
}

/*customize*/
.thread_info_title {
    display: flex;
    position: relative;
    font-family: 'Open Sans';
}
.thread_meta_large_primary {
    display: none;
}
.thread-meta-large {
    order: -1;
}
.thread-meta-large .pagination_pages {
    position: absolute;
    right: 0;
    display: none;
}
.lastpost {
    filter: grayscale(1);
    opacity: 0.5;
}
}
