style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("madfanboy.com") {

    @media (min-width: 1200px) {

        .body-layout-Fluid .wrap,
        .body-layout-Fluid #inner-wrap {
            width: 100%;
            min-width: 1200px;
        }
    }

    body {
        background-color: rgb(0, 0, 0);
        background-image: none;
        color: #bbb;
    }

    a {
        color: #d8d8d8;
    }

    hr {
        border-color: #000;
        border-top-color: #000;
    }

    h2 {
        color: #d8d8d8;
    }

    select {
        color: #000;
    }

    #inner-wrap {
        background: rgb(42, 42, 42);
        border: solid 2px #000;
    }

    ul#nav-breadcrumbs {
        background-color: rgb(53, 53, 53);
        border: 1px solid #000;
    }

    .content_block_header_block ul.topiclist li.header {
        background-color: rgb(27, 27, 27);
        background-image: none;
        border-left: 1px solid #000;
        border-right: 1px solid #000;
    }

    .wrap {
        background-color: rgb(83, 83, 83);
    }

    li.row {
        border-bottom-color: rgb(0, 0, 0);
        background-color: rgb(58, 58, 58);
        border-top: solid 1px rgb(0, 0, 0);
        border-right: solid 1px rgb(0, 0, 0);
        border-left: solid 1px rgb(0, 0, 0);
    }

    li.header {
        border-left: 1px solid #000;
        border-right: 1px solid #000;
    }

    li.row strong {
        color: #f00;
    }

    dl.details dt {
        color: #dadadadd;
    }

    dl.details dd {
        color: #f3f3f3;
    }

    .navbar_footer {
        background-color: rgb(6, 6, 6);
        background-image: none;
    }

    .nav-b-a {
        background-color: rgb(17, 17, 17);
        background-image: none;
    }

    .navigation a {
        color: #fff;
        background: rgb(120, 120, 120);
    }

    .dropdown .dropdown-contents {
        background: rgb(87, 87, 87);
    }

    .particles_container {
        background: rgb(0, 0, 0);
        background: linear-gradient(90deg, rgb(27, 27, 27) 0%, rgb(46, 46, 46) 100%);
    }

    .inputbox {
        color: rgb(204, 204, 206);
        background-color: rgb(70, 70, 70);
        border-color: #000;
    }

    .panel {
        background-color: rgb(80, 80, 80);
        border: 1px solid #000;
        color: #fff;
    }

    .panel .inputbox {
        background-color: rgb(79, 79, 79);
        border-color: #000;
    }

    .bg1,
    .bg2 {
        background-color: rgb(37, 37, 37);
    }

    .forabg,
    .forumbg,
    .viewtopic_wrapper {
        background-color: rgb(10, 10, 10);
        background-image: none;
    }

    .content {
        color: #ccccce;
    }

    .postbody {
        color: #ccccce;
    }

    .post {
        border: 1px solid #000;
    }

    .post:target .content {
        color: #ccc;
    }

    .postprofile {
        color: #c5c5c5;
    }

    .postprofile strong {
        color: #fff;
    }

    .specialbutton,
    .specialbutton .icon,
    .specialbutton:hover,
    .specialbutton:hover .icon {
        background-color: rgb(5, 5, 5);
    }

    .postlink {
        color: #fff !important;
    }

    .button {
        background-color: #3A3A3A;
        color: #f00;
        color: #eee;
    }

    a.button1,
    input.button1,
    input.button3,
    a.button2,
    input.button2 {
        color: #efefef;
        background-color: #396dac;
        border-color: #d9d9d9;
    }

    .panel .button {
        background-color: #101010;
        color: #fff;
    }

    .action-bar .button {
        background-color: #5a5a5a;
    }

    .pagination-s li a,
    .pagination-s li span {
        border-color: #000;
        background-color: #333333;
    }

    .dropdown-extended .header {
        background-color: rgb(80, 80, 80);
    }

    .dropdown-extended .header {
        text-shadow: 0px 0px 0px #f000;
    }

    .notification_list p.notification-time {
        color: #FFFF;
    }

    .cp-mini {
        background-color: rgb(255, 255, 255);
    }

    .tabs .tab > a {
        background: #101010;
    }

    .tabs .activetab > a,
    .tabs .activetab > a:hover {
        background-color: #bbb;
    }

    .pagination li a {
        color: #fff;
    }

    .pagination li.active span {
        color: #fff;
        border-color: #f00;
        background-color: #545454;
    }

    .message-box textarea {
        color: #fff;
    }

    .quotecontent div {
        background-color: rgb(70, 70, 70) !important;
    }

    blockquote {
        background-color: rgb(70, 70, 70);
        border: 1px solid #000;
    }

    blockquote blockquote {
        background-color: rgb(70, 70, 70);
        border: 1px solid #000;
    }

    blockquote blockquote blockquote {
        background-color: rgb(70, 70, 70);
        border: 1px solid #000;
    }

    blockquote blockquote blockquote blockquote {
        background-color: rgb(70, 70, 70);
        border: 1px solid #000;
    }

    blockquote blockquote blockquote blockquote blockquote {
        background-color: rgb(70, 70, 70);
        border: 1px solid #000;
    }

    blockquote blockquote blockquote blockquote blockquote blockquote {
        background-color: rgb(70, 70, 70);
        border: 1px solid #000;
    }

    blockquote blockquote blockquote blockquote blockquote blockquote blockquote {
        background-color: rgb(70, 70, 70);
        border: 1px solid #000;
    }

    blockquote blockquote blockquote blockquote blockquote blockquote blockquote blockquote {
        background-color: rgb(70, 70, 70);
        border: 1px solid #000;
    }

}
