style.css:
@-moz-document domain("uesp.net") {
body
, table
, td
, tr
, div.thumb div
, table.wikitable > * > tr > th
, #catlinks
, #wpTextbox1
, .editOptions
, #toc
, code
, li.gallerybox div.thumb
{
    background-image: none;
    background-color: #141414;
}

[style*="background-color: #f9f9f9"]
, .mw-search-profile-tabs
{
    background-color: #141414 !important
}

[style*="background:#efefef"]
, [style*="background:#DDDDDD;"]
{
    background-color: #424242 !important;
}

.suggestions-result-current
{
    color: black;
    background-color: white;
}

div#content
, .generated-sidebar .pBody
, #p-tb .pBody
, #p-cactions ul li.selected a
, div#footer
, .navbox-even
, #wpSummary
, .portlet .pBody
{
    background-color: #262626;
}

#column-one h3
, .mw-body
, table.wikitable
, div#footer
, #wpTextbox1
{
    color: #D9D9D9;
}

.mw-search-profile-tabs div.search-types ul li.current a
{
    color: #D9D9D9 !important;
}

#searchBody
, .suggestions a div:not(.suggestions-result-current)
, #p-cactions li
, #p-cactions li a
, #mf-main-page table td
{
    background-color: #262626 !important;
}

#searchBody input
, .suggestions a div:not(.suggestions-result-current)
, #wpSummary
, code
{
    color: #E8E8E8 !important;
}

h1
, h2
, h3
{
    color: #E8E8E8;
}

#p-logo
{
    filter: grayscale(60%) brightness(70%);
}

a
, #p-cactions li a
{
    color: #B89C64;
}

#p-personal li a
{
    color: #B89C64 !important;
}

[style*="background:#b5cbea"]
, [style*="background:#B5CBEA"]
{
    background-color: #373C4A !important;
}

.mw-body a.external
{
    color: white;
}

[style*="background:#FFCCCC"]
, [style*="background:#EEEEFF"]
{
    color: black;
}
[style*="background:#EEEEFF"]
{
    background-color: #98A9AB !important;
}
[style*="background:#FFCCCC"]
, [class*="racecolor"][class*="red"]
{
    background-color: #AB9898 !important;
}
[style*="background:#f0b74b"]
, [style*="background:#F0B74B"]
{
    background-color: #453F34 !important;
}
[style*="background:#FFCCCC"] a
, [style*="background:#EEEEFF"] a
, .mw-body [style*="background:#FFCCCC"] a.external
, .mw-body [style*="background:#EEEEFF"] a.external
{
    color: darkblue;
}

#contentSub
{
    color: #A8A8A8;
}

[style*="background-color"] *
{
    color: white;
}

table.MWMagicAlt th
, tr.MWMagicAlt th
{
    background-color: #3F3445 !important;
}

[style*="background:#D0F5D0"]
, table.MWMagicAlt th[style*="background:#D0F5D0"]
{
    background-color: #3B4534 !important;
}

table.MWMagicDes th
, tr.MWMagicDes th
{
    background-color: #453434 !important;
}

#p-cactions .new a
{
    color: #C26161;
}

.mw-search-result-data
{
    color: #81B386;
}

[style*="border: 2px solid #CCCC66"]
, [style*="border: 2px solid #CCCC66"] *
{
    border-color: transparent !important;
    border-bottom: none !important;
    border-top: none !important;
    border-left: none !important;
    border-right: none !important;
    border-width: 0 !important;
    border: none !important;
}

table[style*="border: 2px solid #CCCC66"]
{
    background-color: transparent !important;
}

[class*="racecolor"][class*="kha"]
{
    background-color: #837b58 !important;
}

[class*="racecolor"][class*="dun"]
{
    background-color: #4c4c4c !important;
}
}
