style.css:
@-moz-document domain("school.mosreg.ru") {
body, .page-header, .page-wrapper, .page-footer, .page-footer__content, .page-footer__statistics, .user-profile-box__info{
    color: #dbdbdb;
    background-color: #111;
}
.error{
    background-color: #222;
}
.link{
    color: #dbdbdb;
}
.link:visited{
    color: #dbdbdb;
}
.dropdown-container .link.link_dropdown-container-link:active, .dropdown-container .link.link_dropdown-container-link:focus, .dropdown-container .link.link_dropdown-container-link:focus:hover, .dropdown-container .link.link_dropdown-container-link:hover, .dropdown-container .link.link_dropdown-container-link:visited, .dropdown-container .link.link_dropdown-container-link:visited:hover{
    color: #cacaca;
}
form .panel .rounded, .user-profile-box__info:hover, .user-profile-box__info_active{
    background-color: #222225;
}
.rounded, #content .gray .rounded{
    background-color: #222225;
    border: 1px solid #050510;
}
.tabs li a{
    background-image: linear-gradient(to bottom, #222225, #111);
    border-top-left-radius: 7px;
    border-top-right-radius: 7px;
    border: solid 1px #222225;
}
.tabs li a:hover{
    background-image: linear-gradient(to bottom, #111, #222225);
    border: solid 1px #222225;
}
.tabs li.active a{
    background-image: linear-gradient(to top, #111 50%, #333336);
}
.tabs li.active{
    border-bottom: solid 1px #111;
}
.tabs .tc1, .tabs .tc2{
    display: none;
}
div.notificationBlock.commercial, .page-notification_commercial{
    background-color: #333f22;
    border: 1px solid #222e11;
}
.page-notification__content-button{
    box-shadow: 0 0 2px #121212;
    background: #222225;
    color: #8f8f8f;
}
.page-notification{
    color: #a0a0a0;
}
.header-menu, .header-menu__link{
    background-color: #223d68;
}
.header-menu__item_active .header-menu__link{
    background-color: #222d56;
}
.header-menu__link:hover{
    color: #445f8a;
}
.header-submenu{
    background-color: #111;
    box-shadow: 0 1px 2px rgba(0,0,0,.44);
}
.header-submenu__item .header-submenu__link, .header-links__link{
    color: #dbdbdb;
}
.header-links__link{
    border-bottom-color: #111;
}
h3, #content .panel h3, .user-profile-box__initials, .text{
    color: #dbdbdb;
}
.user-profile-box__category{
    color: rgba(230,230,230,.7);
}
label, .light, #content .panel h4{
    color: #999;
}
.disabled, a.disabled, button.disabled{
    color: #555;
}
textarea, input.t, select, input[type='text'], input[type='password'], select.t, div.textarea{
    color: #dbdbdb;
    border: 1px solid #333;
    background-color: #111;
}
input[type='text']:disabled{
    color: #555;
    background-color: rgb(30, 30, 30);
}
.tabs ul{
    border-bottom: solid 1px #222225;
}
.help a{
    color: #999;
}
.analytics-popup-hint{
    background-color: #222225;
}
#content .blue2 .rounded, .blue2 .rounded2{
    background-color: #222225;
}
.grid td{
    border-top: solid 1px #333;
    border-bottom: solid 1px #333;
}
.gray .cc .grid td, .blue2 .cc .grid td, form .cc .grid td{
    background: #222225;
}
.gray .cc .grid tr:hover td, .blue2 .cc .grid tr:hover td, form .cc .grid tr:hover td{
    background: #333336;
}
.header-menu__item_current .header-menu__link:after{
    border-bottom: 8px solid #111;
}
.dropdown-container :last-child{
    background-color: #222;
    border-color: #333;
    color: #dbdbdb;
}
.dropdown-container{
    background-color: #111;
}
.link.link_dropdown-container-link:hover{
    background-color: #333336;
}
/*.dropdown-container:before{
